float PrRoundBoxDf (vec3 p, vec3 b, float r);
float PrSphDf (vec3 p, float r);
float PrCapsDf (vec3 p, float r, float h);
float PrCylDf (vec3 p, float r, float h);
float PrFlatCylDf (vec3 p, float rhi, float rlo, float h);
vec2 Rot2D (vec2 q, float a);
vec3 HsvToRgb (vec3 c);
float SmoothMin (float a, float b, float r);
float SmoothMax (float a, float b, float r);
float SmoothBump (float lo, float hi, float w, float x);
float Hashfv2 (vec2 p);
vec2 Hashv2v2 (vec2 p);
vec3 Noisev3v2 (vec2 p);
float Fbm2 (vec2 p);
vec3 VaryNf (vec3 p, vec3 n, float f);

vec3 flPos, sunDir, qHit, qHitTransObj;
vec2 gVec[7], hVec[7], gId;
float tCur, dstFar, fusLen, wSpan, vbOff, gRand;
int idObj;
bool transObj;
const int idFus = 11, idPipe = 12, idWing = 13, idStrut = 14, idHstab = 15,
   idFin = 16, idLeg = 17, idAxl = 18, idWhl = 19, idNose = 20, idCkpit = 21,
   idPlt = 22;
const float pi = 3.14159;

#define SQRT3 1.73205

vec2 PixToHex (vec2 p)
{
  vec3 c, r, dr;
  c.xz = vec2 ((1./SQRT3) * p.x - (1./3.) * p.y, (2./3.) * p.y);
  c.y = - c.x - c.z;
  r = floor (c + 0.5);
  dr = abs (r - c);
  r -= step (dr.yzx, dr) * step (dr.zxy, dr) * dot (r, vec3 (1.));
  return r.xz;
}

vec2 HexToPix (vec2 h)
{
  return vec2 (SQRT3 * (h.x + 0.5 * h.y), (3./2.) * h.y);
}

void HexVorInit ()
{
  vec3 e = vec3 (1., 0., -1.);
  gVec[0] = e.yy;
  gVec[1] = e.xy;
  gVec[2] = e.yx;
  gVec[3] = e.xz;
  gVec[4] = e.zy;
  gVec[5] = e.yz;
  gVec[6] = e.zx;
  for (int k = 0; k < 7; k ++) hVec[k] = HexToPix (gVec[k]);
}

vec4 HexVor (vec2 p)
{
  vec4 sd, udm;
  vec2 ip, fp, d, u;
  float amp, a;
  amp = 0.7;
  ip = PixToHex (p);
  fp = p - HexToPix (ip);
  sd = vec4 (4.);
  udm = vec4 (4.);
  for (int k = 0; k < 7; k ++) {
    u = Hashv2v2 (ip + gVec[k]);
    a = 2. * pi * (u.y - 0.5);
    d = hVec[k] + amp * (0.4 + 0.6 * u.x) * vec2 (cos (a), sin (a)) - fp;
    sd.w = dot (d, d);
    if (sd.w < sd.x) {
      sd = sd.wxyw;
      udm = vec4 (d, u);
    } else sd = (sd.w < sd.y) ? sd.xwyw : ((sd.w < sd.z) ? sd.xyww : sd);
  }
  sd.xyz = sqrt (sd.xyz);
  return vec4 (SmoothMin (sd.y, sd.z, 0.1) - sd.x, udm.xy, Hashfv2 (udm.zw));
}

float GrndHt (vec2 p)
{
  vec4 sv;
  float h1, h2, r, s;
  r = 0.2 * length (Noisev3v2 (0.3 * p));
  sv = HexVor (0.06 * p + r);
  s = (0.8 + 0.5 * sv.w);
  h1 = s * smoothstep (0.1, 0.3 + 0.1 * sv.w, sv.x);
  h2 = 1.5 * s * smoothstep (0.2, 0.3 + 0.1 * sv.w, max (0.,
     (0.45 - dot (sv.yz, sv.yz))));
  return 2.5 * SmoothMax (h1, h2, 0.2) - 0.5;
}

float GrndRay (vec3 ro, vec3 rd)
{
  vec3 p;
  float dHit, h, s, sLo, sHi;
  dHit = dstFar;
  if (rd.y < 0.) {
    s = - (ro.y - 6.) / rd.y;
    sLo = s;
    for (int j = 0; j < 150; j ++) {
      p = ro + s * rd;
      h = p.y - GrndHt (p.xz);
      if (h < 0.) break;
      sLo = s;
      s += max (0.2, 0.4 * h);
      if (s > dstFar) break;
    }
    if (h < 0.) {
      sHi = s;
      for (int j = 0; j < 5; j ++) {
        s = 0.5 * (sLo + sHi);
        p = ro + s * rd;
        if (p.y > GrndHt (p.xz)) sLo = s;
        else sHi = s;
      }
      dHit = 0.5 * (sLo + sHi);
    }
  }
  return dHit;
}

vec3 GrndNf (vec3 p)
{
  vec2 e = vec2 (0.01, 0.);
  float h;
  h = GrndHt (p.xz);
  return normalize (vec3 (h - GrndHt (p.xz + e.xy), e.x, h - GrndHt (p.xz + e.yx)));
}

float ObjDf (vec3 p)
{
  vec3 q, qq;
  float dMin, d, wr;
  dMin = dstFar;
  q = p;
  wr = -0.2 + q.z / fusLen;
  d = PrRoundBoxDf (q, vec3 (0.07 * (1. - 0.8 * wr * wr),
     0.11 * (1. - 0.6 * wr * wr), 1.) * fusLen, 0.05 * fusLen);
  q -= vec3 (0., 0.1, 0.3) * fusLen;
  d = max (d, - PrRoundBoxDf (q, vec3 (0.05, 0.1, 0.15) * fusLen,
     0.03 * fusLen)); 
  if (d < dMin) { dMin = d;  idObj = idFus;  qHit = q; }
  q = p;  q -= vec3 (0., 0.08, 0.3) * fusLen;
  d = PrRoundBoxDf (q, vec3 (0.05, 0.02, 0.15) * fusLen, 0.03 * fusLen); 
  if (d < dMin) { dMin = d;  idObj = idCkpit;  qHit = q; }
  q = p;  q.z = abs (q.z - 0.33 * fusLen) - 0.08 * fusLen;
  q -= vec3 (0., 0.17, 0.) * fusLen;
  d = PrSphDf (q, 0.04 * fusLen); 
  if (d < dMin) { dMin = d;  idObj = idPlt;  qHit = q; }
  q = p - fusLen * vec3 (0., 0.03, 0.8);
  q.x = abs (q.x) - 0.1 * fusLen;
  d = PrCapsDf (q, 0.02 * fusLen, 0.15 * fusLen);
  if (d < dMin) { dMin = d;  idObj = idPipe;  qHit = q; }
  q = p - fusLen * vec3 (0., 0.03, 1.05);
  d = PrCapsDf (q, 0.05 * fusLen, 0.02 * fusLen);
  if (d < dMin) { dMin = d;  idObj = idNose;  qHit = q; }
  q = p - fusLen * vec3 (0., 0.09, 0.2);
  qq = q;  qq.y = abs (qq.y) - 0.21 * fusLen;
  wr = q.x / wSpan;
  d = PrFlatCylDf (qq.zyx, 0.24 * (1. - 0.2 * wr * wr) * fusLen,
     0.01 * (1. - 0.8 * wr * wr) * fusLen, wSpan);
  if (d < dMin) { dMin = d;  idObj = idWing;  qHit = q; }
  q = p - fusLen * vec3 (0., 0.09, 0.25);
  q.xz = abs (q.xz) - fusLen * vec2 (0.5, 0.1);
  d = PrCylDf (q.xzy, 0.01 * fusLen, 0.21 * fusLen);
  if (d < dMin) { dMin = d;  idObj = idStrut;  qHit = q; }
  q = p - fusLen * vec3 (0., 0.15, 0.25);
  q.x = abs (q.x) - 0.1 * fusLen;
  d = PrCylDf (q.xzy, 0.01 * fusLen, 0.15 * fusLen);
  if (d < dMin) { dMin = d;  idObj = idStrut;  qHit = q; }
  float tSpan = 0.35 * wSpan;
  q = p - fusLen * vec3 (0., 0., - 0.9);
  wr = q.x / tSpan;
  d = PrFlatCylDf (q.zyx, 0.15 * (1. - 0.25 * wr * wr) * fusLen,
     0.007 * (1. - 0.2 * wr * wr) * fusLen, tSpan);
  q.x = abs (q.x);
  d = max (d, 0.02 * fusLen - 1.5 * q.x - q.z);
  if (d < dMin) { dMin = d;  idObj = idHstab;  qHit = q; }
  float fSpan = 0.32 * wSpan;
  q = p - fusLen * vec3 (0., 0., - 0.87);
  q.yz = Rot2D (q.yz, 0.15);
  wr = q.y / fSpan;
  d = PrFlatCylDf (q.zxy, 0.15 * (1. - 0.3 * wr * wr) * fusLen,
     0.007 * (1. - 0.3 * wr * wr) * fusLen, fSpan);
  d = max (d, - q.y);
  if (d < dMin) { dMin = d;  idObj = idFin;  qHit = q; }
  q = p - fusLen * vec3 (0., -0.25, 0.5);
  q.x = abs (q.x) - 0.14 * fusLen;
  q.xy = Rot2D (q.xy, -0.55);  q.yz = Rot2D (q.yz, 0.15);
  d = PrCylDf (q.xzy, 0.013 * fusLen, 0.12 * fusLen);
  if (d < dMin) { dMin = d;  idObj = idLeg;  qHit = q; }
  q = p - fusLen * vec3 (0., -0.34, 0.515);
  q.x = abs (q.x) - 0.22 * fusLen;
  d = PrCylDf (q.yzx, 0.01 * fusLen, 0.035 * fusLen);
  if (d < dMin) { dMin = d;  idObj = idAxl;  qHit = q; }
  q.x -= 0.01 * fusLen;
  d = PrCylDf (q.yzx, 0.1 * fusLen, 0.015 * fusLen);
  if (d < dMin) { dMin = d;  idObj = idWhl;  qHit = q; }
  return dMin;
}

float TransObjDf (vec3 p)
{
  vec3 q;
  q = p - fusLen * vec3 (0., 0.02, 1.07);
  qHitTransObj = q;
  return PrCylDf (q, 0.3 * fusLen, 0.007 * fusLen);
}

float ObjRay (vec3 ro, vec3 rd)
{
  vec3 vri, vf, hv, p;
  vec2 edN[3], pM, gIdP, rs;
  float dHit, d, s, f;
  edN[0] = vec2 (1., 0.);
  edN[1] = 0.5 * vec2 (1., SQRT3);
  edN[2] = 0.5 * vec2 (1., - SQRT3);
  for (int k = 0; k < 3; k ++) edN[k] *= sign (dot (edN[k], rd.xz));
  vri = 1. / vec3 (dot (rd.xz, edN[0]), dot (rd.xz, edN[1]), dot (rd.xz, edN[2]));
  rs = ro.xz - flPos.xz;
  vf = (SQRT3/2.) - vec3 (dot (rs, edN[0]), dot (rs, edN[1]), dot (rs, edN[2]));
  pM = HexToPix (PixToHex (rs));
  gIdP = vec2 (-10.);
  dHit = 0.;
  for (int j = 0; j < 120; j ++) {
    hv = (vf + vec3 (dot (pM, edN[0]), dot (pM, edN[1]), dot (pM, edN[2]))) * vri;
    s = min (hv.x, min (hv.y, hv.z));
    p = ro - flPos + dHit * rd;
    gId = PixToHex (p.xz);
    if (max (max (abs (gId.x), abs (gId.y)), abs (gId.x + gId.y)) <= 4.) {
      if (gId.x != gIdP.x || gId.y != gIdP.y) {
        gRand = Hashfv2 (gId);
        gIdP = gId;
      }
      p.y += vbOff * gRand - 0.5;
      p.xz -= HexToPix (gId);
      d = transObj ? TransObjDf (p) : ObjDf (p);
    } else d = dstFar;
    if (dHit + d < s) {
      dHit += d;
    } else {
      dHit = s + 0.01;
      pM += SQRT3 * ((s == hv.x) ? edN[0] : ((s == hv.y) ? edN[1] : edN[2]));
    }
    if (d < 0.001 || dHit > dstFar) break;
  }
  if (d >= 0.001) dHit = dstFar;
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec3 e = vec3 (0.001, -0.001, 0.);
  p -= flPos;
  p.y += vbOff * gRand - 0.5;
  p.xz -= HexToPix (gId);
  v = vec4 (ObjDf (p + e.xxx), ObjDf (p + e.xyy),
     ObjDf (p + e.yxy), ObjDf (p + e.yyx));
  return normalize (vec3 (v.x - v.y - v.z - v.w) + 2. * v.yzw);
}

float ObjSShadow (vec3 ro, vec3 rd)
{
  vec3 p;
  float sh, d, h;
  sh = 1.;
  d = 0.01;
  p = ro - flPos;
  p.y += vbOff * gRand - 0.5;
  p.xz -= HexToPix (gId);
  for (int j = 0; j < 30; j ++) {
    h = ObjDf (p + rd * d);
    sh = min (sh, smoothstep (0., 0.05 * d, h));
    d += 0.015;
    if (sh < 0.05) break;
  }
  return 0.5 + 0.5 * sh;
}

vec3 SkyBg (vec3 rd)
{
  return vec3 (0.1, 0.2, 0.5) + 0.1 * pow (1. - max (rd.y, 0.), 4.);
}

vec3 SkyCol (vec3 ro, vec3 rd)
{
  vec3 col;
  float sd, f;
  ro.x += 0.5 * tCur;
  f = Fbm2 (0.05 * (rd.xz * (100. - ro.y) / rd.y + ro.xz));
  sd = pow (max (dot (rd, sunDir), 0.), 64.);
  col = SkyBg (rd) + (0.35 * sd + 0.65 * min (pow (sd, 4.), 0.3)) *
     vec3 (1., 1., 0.3);
  return mix (col, vec3 (0.85), clamp (f * rd.y + 0.1, 0., 1.));
}

vec4 FlyerCol (vec3 n)
{
  vec3 col, qqHit;
  float spec, b;
  spec = 0.2;
  qqHit = qHit / fusLen;
  col = HsvToRgb (vec3 (mod (Hashfv2 (gId * vec2 (17., 27.) + 0.5), 1.), 0.8, 1.));
  if (idObj == idFus) {
    qqHit.yz -= vec2 (-0.1, -0.7);
    col = mix (vec3 (0.5, 0.5, 1.), col,
       (1. - SmoothBump (0.06, 0.09, 0.01, length (qqHit.yz))) *
       (1. - SmoothBump (-0.01, 0.03, 0.01, length (qqHit.yz))));
    if (n.z > 0.9 && qqHit.y < -0.03) col *= 0.3;
  } else if (idObj == idWing) {
    b = wSpan / (8. * fusLen);
    b = mod (qqHit.x + 0.5 * b, b) - 0.5 * b;
    col *= 1. + 0.1 * SmoothBump (-0.01, 0.01, 0.002, b);
    if (abs (qqHit.x) > 0.7)
       col *= 1. - 0.6 * SmoothBump (-0.128, -0.117, 0.002, qqHit.z);
    if (qqHit.z < -0.125)
       col *= 1. - 0.6 * SmoothBump (0.695, 0.705, 0.002, abs (qqHit.x));
    if (qqHit.y * n.y > 0.) {
      qqHit.x = abs (qqHit.x) - 0.8;
      qqHit.z -= 0.03;
      col *= mix (vec3 (0.5, 0.5, 1.), col,
         (1. - SmoothBump (0.08, 0.12, 0.01, length (qqHit.xz))) *
         (1. - SmoothBump (-0.01, 0.03, 0.01, length (qqHit.xz))));
    }
  } else if (idObj == idFin || idObj == idHstab) {
    col *= 1. - 0.6 * SmoothBump (-0.062, -0.052, 0.002, qqHit.z);
  } else if (idObj == idStrut || idObj == idLeg) {
    col = 0.6 * col + vec3 (0.4);
  } else if (idObj == idPipe || idObj == idNose) {
    col = HsvToRgb (vec3 (mod (Hashfv2 (gId * vec2 (17., 27.) + 0.5) + 0.5, 1.), 1., 1.));
    spec = 0.7;
  } else if (idObj == idAxl) {
    col = vec3 (0.3, 0.2, 0.);
  } else if (idObj == idCkpit) {
    col = vec3 (0.2, 0.15, 0.05);
  } else if (idObj == idPlt) {
    col = vec3 (0.1, 0.07, 0.);
    if (n.z > 0.7) {
      col *= 2.;
      qqHit.x = abs (qqHit.x) - 0.015 * fusLen;
      col *= (1. - 0.9 * SmoothBump (0.003, 0.01, 0.001, length (qqHit.xy)));
    }
  } else if (idObj == idWhl) {
    if (length (qqHit.yz) < 0.07) col = vec3 (0.4);
    else {
      col = vec3 (0.02);
      spec = 0.;
    }
  }
  return vec4 (col, spec);
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec4 col4;
  vec3 vn, col;
  float dstObj, dstPropel, dstGrnd, sh;
  HexVorInit ();
  dstGrnd = GrndRay (ro, rd);
  transObj = true;
  dstPropel = ObjRay (ro, rd);
  transObj = false;
  dstObj = ObjRay (ro, rd);
  if (min (dstObj, dstGrnd) < dstPropel) dstPropel = dstFar;
  if (dstGrnd < min (dstObj, dstFar)) {
    ro += dstGrnd * rd;
    if (ro.y > 0.1) {
      vn = VaryNf (1.3 * ro, GrndNf (ro), 2.);
      vn = VaryNf (15. * ro.zyx, vn, 0.5);
      col = vec3 (1.);
      col = col * (0.1 + 0.2 * max (dot (vn.xz, - normalize (sunDir.xz)), 0.) +
         0.1 * max (vn.y, 0.) + 0.8 * max (dot (vn, sunDir), 0.)) +
         0.5 * pow (max (dot (normalize (sunDir - rd), vn), 0.), 64.);
    } else {
      vn = VaryNf (5.1 * ro, vec3 (0., 1., 0.), 0.2);
      rd = reflect (rd, vn);
      col = mix (vec3 (0.15, 0.2, 0.15), vec3 (0.1, 0.1, 0.2), Fbm2 (ro.xz));
      col = mix (col, 0.8 * SkyCol (ro, rd), smoothstep (0.8, 0.95,
          1. - pow (dot (rd, vn), 3.)));
    }
    col = mix (col, SkyBg (rd), max (pow (dstGrnd / dstFar, 4.) - 0.2, 0.));
  } else if (dstObj < dstFar) {
    ro += dstObj * rd;
    vn = ObjNf (ro);
    col4 = FlyerCol (vn);
    sh = ObjSShadow (ro, sunDir);
    col = col4.rgb * (0.2 + 0.8 * sh * max (dot (sunDir, vn), 0.)) +
       col4.a * sh * pow (max (dot (normalize (sunDir - rd), vn), 0.), 128.);
  } else col = SkyCol (ro, rd);
  if (dstPropel < dstFar) col = vec3 (0.1) * (1. - 0.3 * SmoothBump (0.25, 0.27,
     0.006, length (qHitTransObj.xy) / fusLen)) + 0.7 * col;
  return pow (clamp (col, 0., 1.), vec3 (0.8));
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr;
  vec3 ro, rd, vd, u;
  vec2 canvas, uv, ori, ca, sa;
  float az, el, f, flyVel, t;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  rd = normalize (vec3 (uv, 1.7));
  fusLen = 0.45;
  wSpan = 1.2 * fusLen;
  flyVel = 5.;
  flPos = vec3 (0., 16., flyVel * tCur);
  vbOff = 2. * cos (0.1 * tCur);
  ro = vec3 (0., 2., -8.);
  if (mPtr.z > 0.) {
    el = 0.6 * pi * mPtr.y;
    az = 2.1 * pi * mPtr.x;
  } else {
    t = mod (0.03 * tCur, 2.);
    el = 0.2 * pi * sign (t - 1.) * SmoothBump (0.25, 0.75, 0.2, mod (t, 1.));
    az = - pi * sin (0.005 * 2. * pi * tCur);
  }
  ro.yz = Rot2D (ro.yz, el);
  ro.xz = Rot2D (ro.xz, az);
  ro += flPos;
  vd = normalize (flPos + vec3 (0., -8. * sin (el), 0.) - ro);
  u = - vd.y * vd;
  f = 1. / sqrt (1. - vd.y * vd.y);
  vuMat = mat3 (f * vec3 (vd.z, 0., - vd.x), f * vec3 (u.x, 1. + u.y, u.z), vd);
  rd = vuMat * rd;
  sunDir = normalize (vec3 (cos (0.01 * tCur), 1., - sin (0.01 * tCur)));
  dstFar = 200.;
  fragColor = vec4 (ShowScene (ro, rd), 1.);
}

float PrRoundBoxDf (vec3 p, vec3 b, float r)
{
  return length (max (abs (p) - b, 0.)) - r;
}

float PrSphDf (vec3 p, float r)
{
  return length (p) - r;
}

float PrCapsDf (vec3 p, float r, float h)
{
  return length (p - vec3 (0., 0., clamp (p.z, - h, h))) - r;
}

float PrCylDf (vec3 p, float r, float h)
{
  return max (length (p.xy) - r, abs (p.z) - h);
}

float PrFlatCylDf (vec3 p, float rhi, float rlo, float h)
{
  return max (length (p.xy - vec2 (rhi *
     clamp (p.x / rhi, -1., 1.), 0.)) - rlo, abs (p.z) - h);
}

vec2 Rot2D (vec2 q, float a)
{
  return q * cos (a) + q.yx * sin (a) * vec2 (-1., 1.);
}

float SmoothMin (float a, float b, float r)
{
  float h;
  h = clamp (0.5 + 0.5 * (b - a) / r, 0., 1.);
  return mix (b, a, h) - r * h * (1. - h);
}

float SmoothMax (float a, float b, float r)
{
  return - SmoothMin (- a, - b, r);
}

float SmoothBump (float lo, float hi, float w, float x)
{
  return (1. - smoothstep (hi - w, hi + w, x)) * smoothstep (lo - w, lo + w, x);
}

vec3 HsvToRgb (vec3 c)
{
  vec3 p;
  p = abs (fract (c.xxx + vec3 (1., 2./3., 1./3.)) * 6. - 3.);
  return c.z * mix (vec3 (1.), clamp (p - 1., 0., 1.), c.y);
}

const vec4 cHashA4 = vec4 (0., 1., 57., 58.);
const vec3 cHashA3 = vec3 (1., 57., 113.);
const float cHashM = 43758.54;

float Hashfv2 (vec2 p)
{
  return fract (sin (dot (p, cHashA3.xy)) * cHashM);
}

vec4 Hashv4f (float p)
{
  return fract (sin (p + cHashA4) * cHashM);
}

vec2 Hashv2v2 (vec2 p)
{
  const vec2 cHashVA2 = vec2 (37.1, 61.7);
  const vec2 e = vec2 (1., 0.);
  return fract (sin (vec2 (dot (p + e.yy, cHashVA2),
     dot (p + e.xy, cHashVA2))) * cHashM);
}

float Noisefv2 (vec2 p)
{
  vec4 t;
  vec2 ip, fp;
  ip = floor (p);
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = Hashv4f (dot (ip, cHashA3.xy));
  return mix (mix (t.x, t.y, fp.x), mix (t.z, t.w, fp.x), fp.y);
}

vec3 Noisev3v2 (vec2 p)
{
  vec4 h;
  vec3 g;
  vec2 ip, fp, ffp;
  ip = floor (p);
  fp = fract (p);
  ffp = fp * fp * (3. - 2. * fp);
  h = Hashv4f (dot (ip, cHashA3.xy));
  g = vec3 (h.y - h.x, h.z - h.x, h.x - h.y - h.z + h.w);
  return vec3 (h.x + dot (g.xy, ffp) + g.z * ffp.x * ffp.y,
     30. * fp * fp * (fp * fp - 2. * fp + 1.) * (g.xy + g.z * ffp.yx));
}

float Fbm2 (vec2 p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int i = 0; i < 5; i ++) {
    f += a * Noisefv2 (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

float Fbmn (vec3 p, vec3 n)
{
  vec3 s;
  float a;
  s = vec3 (0.);
  a = 1.;
  for (int i = 0; i < 5; i ++) {
    s += a * vec3 (Noisefv2 (p.yz), Noisefv2 (p.zx), Noisefv2 (p.xy));
    a *= 0.5;
    p *= 2.;
  }
  return dot (s, abs (n)) * (1. / 1.9375);
}

vec3 VaryNf (vec3 p, vec3 n, float f)
{
  vec3 g;
  float s;
  const vec3 e = vec3 (0.1, 0., 0.);
  s = Fbmn (p, n);
  g = vec3 (Fbmn (p + e.xyy, n) - s, Fbmn (p + e.yxy, n) - s,
     Fbmn (p + e.yyx, n) - s);
  return normalize (n + f * (g - n * dot (n, g)));
}
