/*
	My technique is mostly based on chapter 17 of 
	https://scholarsarchive.byu.edu/cgi/viewcontent.cgi?article=1000&context=facpub

	But i've also used stuff from
	http://140.129.20.249/~jmchen/ana/docs/resultant/implicitization-sederbeg.pdf
	and http://research.microsoft.com/en-us/um/people/hoppe/ravg.pdf
	(see https://www.shadertoy.com/view/XsX3zf)
*/

//#define TAYLOR
//#define RATIONAL

const float dot_size=.005;

const vec3 point_col=vec3(1,1,0);

const float weight_p1=1.;
const float weight_p2=1.;

const float zoom=1.;

const float pi=3.1415926535;

float det(mat2 m){
	return m[0][0] * m[1][1] - m[1][0] * m[0][1];
}

// Modified from http://tog.acm.org/resources/GraphicsGems/gems/Roots3And4.c
// Credits to Doublefresh for hinting there
int solve_quadric(vec3 coeffs, out vec2 roots){
    float p, q, D;

    // normal form: x^2 + px + q = 0

    p = coeffs[1] / (2. * coeffs[0]);
    q = coeffs[2] / coeffs[0];

    D = p * p - q;

    if (D < 0.){
		return 0;
    }
    else if (D > 0.){
		float sqrt_D = sqrt(D);

		roots[0] =   sqrt_D - p;
		roots[1] = - sqrt_D - p;
		return 2;
    }
}

//From Trisomie21
//But instead of his cancellation fix i'm using a newton iteration
int solve_cubic(float a, float b, float c, out vec3 r){
	float p = b - a*a / 3.0;
	float q = a * (2.0*a*a - 9.0*b) / 27.0 + c;
	float p3 = p*p*p;
	float d = q*q + 4.0*p3 / 27.0;
	float offset = -a / 3.0;
	if(d >= 0.0) { // Single solution
		float z = sqrt(d);
		float u = (-q + z) / 2.0;
		float v = (-q - z) / 2.0;
		u = sign(u)*pow(abs(u),1.0/3.0);
		v = sign(v)*pow(abs(v),1.0/3.0);
		r[0] = offset + u + v;	

		//Single newton iteration to account for cancellation
		float f = ((r[0] + a) * r[0] + b) * r[0] + c;
		float f1 = (3. * r[0] + 2. * a) * r[0] + b;

		r[0] -= f / f1;

		return 1;
	}
	float u = sqrt(-p / 3.0);
	float v = acos(-sqrt( -27.0 / p3) * q / 2.0) / 3.0;
	float m = cos(v), n = sin(v)*1.732050808;

	//Single newton iteration to account for cancellation
	//(once for every root)
	r[0] = offset + u * (m + m);
    r[1] = offset - u * (n + m);
    r[2] = offset + u * (n - m);

    vec3 f = ((r + a) * r + b) * r + c;
	vec3 f1 = (3. * r + 2. * a) * r + b;

    r -= f / f1;

	return 3;
}

//Sign computation is pretty straightforward:
//I'm solving a cubic equation to get the intersection count
//of a ray from the current point to infinity and parallel to the x axis
//Also i'm computing the intersection count with the tangent in the end points of the curve
float cubic_bezier_sign(vec2 uv, vec3 p0, vec3 p1, vec3 p2, vec3 p3){

	#ifndef RATIONAL
	p0/=p0.z;
	p1/=p1.z;
	p2/=p2.z;
	p3/=p3.z;
	#endif

	#ifdef RATIONAL
	float cu=(-p0.y+3.*p1.y-3.*p2.y+p3.y)-uv.y*(-p0.z+3.*p1.z-3.*p2.z+p3.z);
	float qu=(3.*p0.y-6.*p1.y+3.*p2.y)-uv.y*(3.*p0.z-6.*p1.z+3.*p2.z);
	float li=(-3.*p0.y+3.*p1.y)-uv.y*(-3.*p0.z+3.*p1.z);
	float co=p0.y-uv.y*p0.z;
	#else
	float cu=(-p0.y+3.*p1.y-3.*p2.y+p3.y);
	float qu=(3.*p0.y-6.*p1.y+3.*p2.y);
	float li=(-3.*p0.y+3.*p1.y);
	float co=p0.y-uv.y;
	#endif

	vec3 roots;
	int n_roots=solve_cubic(qu/cu,li/cu,co/cu,roots);

	int n_ints=0;

	for(int i=0;i<3;i++){
		if(i<n_roots){
			if(roots[i]>=0. && roots[i]<=1.){
				float x_pos=((((-p0.x+3.*p1.x-3.*p2.x+p3.x)*roots[i]+(3.*p0.x-6.*p1.x+3.*p2.x))*roots[i])+(-3.*p0.x+3.*p1.x))*roots[i]+p0.x;
				#ifdef RATIONAL
				float x_pos_w=((((-p0.z+3.*p1.z-3.*p2.z+p3.z)*roots[i]+(3.*p0.z-6.*p1.z+3.*p2.z))*roots[i])+(-3.*p0.z+3.*p1.z))*roots[i]+p0.z;
				x_pos/=x_pos_w;
				#endif 

				if(x_pos<uv.x){
					n_ints++;
				}
			}
		}
	}

	#ifdef RATIONAL
	p0/=p0.z;
	p1/=p1.z;
	p2/=p2.z;
	p3/=p3.z;
	#endif

	vec2 tang1=p0.xy-p1.xy;
	vec2 tang2=p2.xy-p3.xy;

	vec2 nor1=vec2(tang1.y,-tang1.x);
	vec2 nor2=vec2(tang2.y,-tang2.x);

	if(p0.y<p1.y){
		if((uv.y<=p0.y) && (dot(uv-p0.xy,nor1)<0.)){
			n_ints++;
		}
	}
	else{
		if(!(uv.y<=p0.y) && !(dot(uv-p0.xy,nor1)<0.)){
			n_ints++;
		}
	}

	if(p2.y<p3.y){
		if(!(uv.y<=p3.y) && dot(uv-p3.xy,nor2)<0.){
			n_ints++;
		}
	}
	else{
		if((uv.y<=p3.y) && !(dot(uv-p3.xy,nor2)<0.)){
			n_ints++;
		}
	}

	if(n_ints==0 || n_ints==2 || n_ints==4){
		return 1.;
	}
	else{
		return -1.;
	}
}

float cubic_bezier_dis_approx(vec2 uv, vec3 p0, vec3 p1, vec3 p2, vec3 p3){

	#ifndef RATIONAL
	p0/=p0.z;
	p1/=p1.z;
	p2/=p2.z;
	p3/=p3.z;
	#endif

	float bezier_dis=1e38;

	//Control lines of quadratic bezier lines
	//The intersection of two of the three moving lines
	//at the same parameter value define the cubic bezier curve
	vec3 line00=3.*cross(p0,p1);
	vec3 line01=3.*cross(p0,p2);
	vec3 line02=cross(p0,p3);

	vec3 line10=line01;
	vec3 line11=cross(p0,p3)+9.*cross(p1,p2);
	vec3 line12=3.*cross(p1,p3);

	vec3 line20=line02;
	vec3 line21=line12;
	vec3 line22=3.*cross(p2,p3);

	//Compute a linear moving line (line pencil) through elimination
	//The intersection of this and one of the quadratic moving lines
	//define the cubic bezier curve
	float pre_factor1=line20.z/line00.z;
	float pre_factor2=line20.z/line10.z;

	float sub_factor=(line20.x-pre_factor2*line10.x)/(pre_factor1*line00.x-pre_factor2*line10.x);

	float factor1=pre_factor1*sub_factor;
	float factor2=pre_factor2*(1.-sub_factor);

	vec3 line31=line21-factor1*line01-factor2*line11;
	vec3 line32=line22-factor1*line02-factor2*line12;

	//Compute signed distance of pixel coordinate to the control lines
	float l00=dot(vec3(uv,1),line00);
	float l01=dot(vec3(uv,1),line01);
	float l02=dot(vec3(uv,1),line02);

	float l11=dot(vec3(uv,1),line31);
	float l12=dot(vec3(uv,1),line32);

	//Compute the value of the resultant
	//of both line equations at the pixel coordinate
	//this is an implicit equation for the cubic bezier curve
	//(resultant is zero iff point lies on curve)
	vec2 a=vec2(l00,0.);
	vec2 b=vec2(l01,l11);
	vec2 c=vec2(l02,l12);

	mat2 bezout=mat2(det(mat2(a,b)),det(mat2(a,c)),det(mat2(a,c)),det(mat2(b,c)));

	//Also compute the gradient of the resultant
	vec2 bezout_grad1=(line00.xy*b.y+a.x*line31.xy);
	vec2 bezout_grad2=(line00.xy*c.y+a.x*line32.xy);
	vec2 bezout_grad3=bezout_grad2;
	vec2 bezout_grad4=(line01.xy*c.y+b.x*line32.xy)-(line02.xy*b.y+c.x*line31.xy);

	float resultant=det(bezout);
	vec2 resultant_grad=(bezout_grad1*bezout[1][1]+bezout[0][0]*bezout_grad4)-(bezout_grad2*bezout[0][1]+bezout[1][0]*bezout_grad3);

	//Interestingly, i'm getting a kind of parasitic line in my resultant
	//The resultant is zero also if point lies on line00
	//This is not mentioned in Sederberg's script, so i am not entirely sure
	//if i'm doing something wrong
	//Anyway, i'm just dividing it out ;)
	vec2 gradient=(resultant_grad*l00-resultant*line00.xy)/(l00*l00);
	resultant/=l00;

	//Use the gradient to compute a point that's nearer to the curve
	vec2 nearest_point_global=uv-(resultant*gradient)/dot(gradient,gradient);

	float npgl31=dot(vec3(nearest_point_global,1),line31);
	float npgl32=dot(vec3(nearest_point_global,1),line32);

	//Get parameter value of this point by solving the linear line equation
	float parameter_global=npgl31/(npgl31-npgl32);
	parameter_global=clamp(parameter_global,0.,1.);

	#ifdef RATIONAL
	vec3 clamped_point_global=((((-p0+3.*p1-3.*p2+p3)*parameter_global+(3.*p0-6.*p1+3.*p2))*parameter_global)+(-3.*p0+3.*p1))*parameter_global+p0;
	vec2 uv_to_cpg=uv-clamped_point_global.xy/clamped_point_global.z;
	#else
	vec2 clamped_point_global=((((-p0.xy+3.*p1.xy-3.*p2.xy+p3.xy)*parameter_global+(3.*p0.xy-6.*p1.xy+3.*p2.xy))*parameter_global)+(-3.*p0.xy+3.*p1.xy))*parameter_global+p0.xy;
	vec2 uv_to_cpg=uv-clamped_point_global.xy;
	#endif

	bezier_dis=min(bezier_dis,dot(uv_to_cpg,uv_to_cpg));

	//The above method doesn't work well when we have a double point
	//So we are doing a Taylor (or Padé) approximation to both branches

	//The double point is the intersection of the control lines
	//of the linear moving line
	vec3 double_point=cross(line31,line32);

	//Compute the parameter values at the double point
	//by solving the quadratic moving line equation
	float dpl00=dot(double_point,line00);
	float dpl01=dot(double_point,line01);
	float dpl02=dot(double_point,line02);

	vec3 coeffs=vec3(dpl00-dpl01+dpl02,dpl01-2.*dpl00,dpl00);
	vec2 roots;

	int n=solve_quadric(coeffs,roots);

	if(n!=0){ //Acnode or crunode?
		for(int i=0;i<2;i++){
			//First compute the derivatives of the parametric cubic bezier curve at
			//both parameter values of the double point
			#ifndef RATIONAL
			vec2 f=double_point.xy/double_point.z;
			#else
			vec2 f=((((-p0.xy+3.*p1.xy-3.*p2.xy+p3.xy)*roots[i]+(3.*p0.xy-6.*p1.xy+3.*p2.xy))*roots[i])+(-3.*p0.xy+3.*p1.xy))*roots[i]+p0.xy;
			#endif
			vec2 f1=(3.*(-p0.xy+3.*p1.xy-3.*p2.xy+p3.xy)*roots[i]+2.*(3.*p0.xy-6.*p1.xy+3.*p2.xy))*roots[i]+(-3.*p0.xy+3.*p1.xy);
			vec2 f2=2.*(3.*(-p0.xy+3.*p1.xy-3.*p2.xy+p3.xy)*roots[i]+(3.*p0.xy-6.*p1.xy+3.*p2.xy));
			#ifndef TAYLOR
			vec2 f3=6.*(-p0.xy+3.*p1.xy-3.*p2.xy+p3.xy);
			#endif

			#ifdef RATIONAL
			float q=((((-p0.z+3.*p1.z-3.*p2.z+p3.z)*roots[i]+(3.*p0.z-6.*p1.z+3.*p2.z))*roots[i])+(-3.*p0.z+3.*p1.z))*roots[i]+p0.z;
			float q1=(3.*(-p0.z+3.*p1.z-3.*p2.z+p3.z)*roots[i]+2.*(3.*p0.z-6.*p1.z+3.*p2.z))*roots[i]+(-3.*p0.z+3.*p1.z);
			float q2=2.*(3.*(-p0.z+3.*p1.z-3.*p2.z+p3.z)*roots[i]+(3.*p0.z-6.*p1.z+3.*p2.z));
			#ifndef TAYLOR
			float q3=6.*(-p0.z+3.*p1.z-3.*p2.z+p3.z);
			#endif

			//Compute Taylor coefficients (at point 0)
			vec2 a0=f/q;
			vec2 a1=(f1-f*q1/q)/q;
			vec2 a2=(1./2.)*(((2.*f*q1*q1)/q-(2.*f1*q1+f*q2))/q+f2)/q;
			#ifndef TAYLOR
			vec2 a3=(1./6.)*((((-6.*f*q1*q1*q1)/q+(6.*f1*q1*q1+6.*f*q1*q2))/q+(-3.*f2*q1-f*q3-3.*f1*q2))/q+f3)/q;
			#endif

			#else //RATIONAL

			vec2 a0=f;
			vec2 a1=f1;
			vec2 a2=(1./2.)*f2;
			#ifndef TAYLOR
			vec2 a3=(1./6.)*f3;
			#endif

			#endif //RATIONAL

			//Padé approximation: Use Taylor coefficients to compute Padé coefficients
			//Padé is better for most curve's, but sometimes, especially for rational cubic curves
			//The Padé approximation itself (which is a conic section)
			//degenerates to two lines, then Taylor approximation is the better variant
			#ifndef TAYLOR
			float d0_tmp=1.;
			float d1_tmp=-dot(a2,a3)/(dot(a2,a2)-dot(a1,a3));
			float d2_tmp=dot(a3,a3)/(dot(a2,a2)-dot(a1,a3));

			vec2 c0=a0;
			vec2 c1=a1+a0*d1_tmp;
			vec2 c2=a2+a1*d1_tmp+a0*d2_tmp;

			//Resubstitution: The previous calculation assumed the Taylor base point was 0 (which it isn't)
			float d2=d2_tmp;
			float d1=d1_tmp-2.*roots[i]*d2_tmp;
			float d0=d0_tmp+roots[i]*(roots[i]*d2_tmp-d1_tmp);

			vec2 qu=c2-d2*uv;
			vec2 li=c1-2.*roots[i]*c2-d1*uv;
			vec2 co=c0+roots[i]*(roots[i]*c2-c1)-d0*uv;
			#else
			vec2 qu=a2;
			vec2 li=a1-2.*roots[i]*a2;
			vec2 co=a0+roots[i]*(roots[i]*a2-a1)-uv;
			#endif

			//Implicitize the parametric approximation with the Bézout matrix
			//And compute it's gradient
			mat2 taylor_bezout=mat2(det(mat2(qu,li)),det(mat2(qu,co)),det(mat2(qu,co)),det(mat2(li,co)));
			float taylor_resultant=det(taylor_bezout);

			#ifndef TAYLOR
			vec2 taylor_grad1=(vec2(-d2,0)*li.y+qu.x*vec2(0,-d1))-(vec2(-d1,0)*qu.y+li.x*vec2(0,-d2));
			vec2 taylor_grad2=(vec2(-d2,0)*co.y+qu.x*vec2(0,-d0))-(vec2(-d0,0)*qu.y+co.x*vec2(0,-d2));
			vec2 taylor_grad3=taylor_grad2;
			vec2 taylor_grad4=(vec2(-d1,0)*co.y+li.x*vec2(0,-d0))-(vec2(-d0,0)*li.y+co.x*vec2(0,-d1));

			vec2 taylor_grad=(taylor_grad1*taylor_bezout[1][1]+taylor_bezout[0][0]*taylor_grad4)-(taylor_grad2*taylor_bezout[0][1]+taylor_bezout[1][0]*taylor_grad3);
			#else
			vec2 taylor_grad=taylor_bezout[0][0]*(vec2(li.y,-li.x))-(vec2(qu.y,-qu.x)*taylor_bezout[0][1]+taylor_bezout[1][0]*vec2(qu.y,-qu.x));
			#endif

			//Now use this gradient to get a point that's more close to the curve (but on the approximation curve now)
			vec2 nearest_point_taylor=uv-(taylor_resultant*taylor_grad)/dot(taylor_grad,taylor_grad);

			#ifndef TAYLOR
			vec2 npt_qu=c2-d2*nearest_point_taylor;
			vec2 npt_li=c1-2.*roots[i]*c2-d1*nearest_point_taylor;
			vec2 npt_co=c0+roots[i]*(roots[i]*c2-c1)-d0*nearest_point_taylor;
			#else
			vec2 npt_qu=a2;
			vec2 npt_li=a1-2.*roots[i]*a2;
			vec2 npt_co=a0+roots[i]*(roots[i]*a2-a1)-nearest_point_taylor;
			#endif

			//Get the parameter value of the point computed above
			//I'm using curve inversion of the approximation curve
			//using some coefficients of the bezout matrix
			float parameter_taylor=-det(mat2(npt_qu,npt_co))/det(mat2(npt_qu,npt_li));
			parameter_taylor=clamp(parameter_taylor,0.,1.);

			//Compute point on original curve using the parameter at the point on the approximation curve
			#ifdef RATIONAL
			vec3 clamped_point_taylor=((((-p0+3.*p1-3.*p2+p3)*parameter_taylor+(3.*p0-6.*p1+3.*p2))*parameter_taylor)+(-3.*p0+3.*p1))*parameter_taylor+p0;
			vec2 uv_to_cpt=uv-clamped_point_taylor.xy/clamped_point_taylor.z;
			#else
			vec2 clamped_point_taylor=((((-p0.xy+3.*p1.xy-3.*p2.xy+p3.xy)*parameter_taylor+(3.*p0.xy-6.*p1.xy+3.*p2.xy))*parameter_taylor)+(-3.*p0.xy+3.*p1.xy))*parameter_taylor+p0.xy;
			vec2 uv_to_cpt=uv-clamped_point_taylor.xy;
			#endif
			bezier_dis=min(bezier_dis,dot(uv_to_cpt,uv_to_cpt));
		}
	}

	return sqrt(bezier_dis);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord){
	float border=2./iResolution.x;

	vec2 uv=fragCoord.xy/iResolution.xy;
	uv-=.5;
    uv.y*=iResolution.y/iResolution.x;

	vec2 mouse=iMouse.xy/iResolution.xy;
	mouse-=.5;
    mouse.y*=iResolution.y/iResolution.x;

	border*=zoom;
	uv*=zoom;
    mouse*=zoom;

	vec3 p0=vec3(-.3,-.1,1);
	vec3 p1=weight_p1*vec3(mouse,1);
	//vec3 p1=weight_p1*vec3(-.1,.2,1);
	//vec3 p2=weight_p2*vec3(mouse,1);
	vec3 p2=weight_p2*vec3(.1,-.2,1);
	vec3 p3=vec3(.2,.15,1);

	float dis=cubic_bezier_dis_approx(uv,p0,p1,p2,p3);

	float sgn=cubic_bezier_sign(uv,p0,p1,p2,p3);

	//iq's sd color scheme
	vec3 color = vec3(1.0) - sgn*vec3(0.1,0.4,0.7);
	color *= 1.0 - exp(-8.0*dis);
	color *= 0.8 + 0.2*cos(480.0*dis*sgn);
	color = mix( color, vec3(1.0), 1.0-smoothstep(0.0,0.005,dis) );

	dis=1e38;

	dis=min(dis,distance(p0.xy/p0.z,uv)-dot_size);
	dis=min(dis,distance(p1.xy/p1.z,uv)-dot_size);
	dis=min(dis,distance(p2.xy/p2.z,uv)-dot_size);
	dis=min(dis,distance(p3.xy/p3.z,uv)-dot_size);

	color=mix(point_col,color,smoothstep(0.,border,dis));

	fragColor=vec4(color,1);
}
