// "Colliding Blocks" by dr2 - 2016
// License: Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License

#define txBuf iChannel0
#define txSize iChannelResolution[0].xy

mat3 QtToRMat (vec4 q) 
{
  mat3 m;
  float a1, a2, s;
  s = q.w * q.w - 0.5;
  m[0][0] = q.x * q.x + s;  m[1][1] = q.y * q.y + s;  m[2][2] = q.z * q.z + s;
  a1 = q.x * q.y;  a2 = q.z * q.w;  m[0][1] = a1 + a2;  m[1][0] = a1 - a2;
  a1 = q.x * q.z;  a2 = q.y * q.w;  m[2][0] = a1 + a2;  m[0][2] = a1 - a2;
  a1 = q.y * q.z;  a2 = q.x * q.w;  m[1][2] = a1 + a2;  m[2][1] = a1 - a2;
  return 2. * m;
}

vec3 HsvToRgb (vec3 c)
{
  vec3 p;
  p = abs (fract (c.xxx + vec3 (1., 2./3., 1./3.)) * 6. - 3.);
  return c.z * mix (vec3 (1.), clamp (p - 1., 0., 1.), c.y);
}

const float txRow = 128.;

vec4 Loadv4 (int idVar)
{
  float fi;
  fi = float (idVar);
  return texture (txBuf, (vec2 (mod (fi, txRow), floor (fi / txRow)) + 0.5) /
     txSize);
}

const float pi = 3.14159;
const vec4 cHashA4 = vec4 (0., 1., 57., 58.);
const vec3 cHashA3 = vec3 (1., 57., 113.);
const float cHashM = 43758.54;

vec4 Hashv4f (float p)
{
  return fract (sin (p + cHashA4) * cHashM);
}

float Noisefv2 (vec2 p)
{
  vec4 t;
  vec2 ip, fp;
  ip = floor (p);
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = Hashv4f (dot (ip, cHashA3.xy));
  return mix (mix (t.x, t.y, fp.x), mix (t.z, t.w, fp.x), fp.y);
}

float Fbm2 (vec2 p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int i = 0; i < 5; i ++) {
    f += a * Noisefv2 (p);
    a *= 0.5;
    p *= 2.;
  }
  return f;
}

const int nBlock = 64;
const int nSiteBk = 27;
const vec3 blkSph = vec3 (3.);
const vec3 blkGap = vec3 (0.4);

vec3 vnBlk, vnSph, ltDir;
vec2 qBlk;
float tCur, dstFar, spRad;
int idBlk;

float SphHit (vec3 ro, vec3 rd)
{
  float b, d, w;
  b = dot (rd, ro);
  w = b * b + spRad * spRad - dot (ro, ro);
  d = dstFar;
  if (w >= 0.) {
    d = - b - sqrt (w);
    vnSph = (ro + d * rd) / spRad;
  }
  return d;
}

float SphHitSh (vec3 ro, vec3 rd, float rng)
{
  float b, d, w;
  b = dot (rd, ro);
  w = b * b + spRad * spRad - dot (ro, ro);
  d = dstFar;
  if (w >= 0.) d = - b - sqrt (w);
  return smoothstep (0., rng, d);
}

float BlkHit (vec3 ro, vec3 rd)
{
  mat3 m;
  vec3 rm, rdm, v, tm, tp, bSize, u;
  float dMin, dn, df;
  bSize = 0.5 * blkGap * (blkSph - 1.) + 0.4;
  dMin = dstFar;
  for (int n = 0; n < nBlock; n ++) {
    rm = Loadv4 (4 + 4 * n).xyz;
    m = QtToRMat (Loadv4 (4 + 4 * n + 2));
    rdm = rd * m;
    v = ((ro - rm) * m) / rdm;
    tp = bSize / abs (rdm) - v;
    tm = - tp - 2. * v;
    dn = max (max (tm.x, tm.y), tm.z);
    df = min (min (tp.x, tp.y), tp.z);
    if (df > 0. && dn < min (df, dMin)) {
      dMin = dn;
      vnBlk = - sign (rdm) * step (tm.zxy, tm) * step (tm.yzx, tm);
      idBlk = n;
      u = (v + dn) * rdm;
    }
  }
  if (dMin < dstFar) {
    qBlk = vec2 (dot (u.zxy, vnBlk), dot (u.yzx, vnBlk));
    vnBlk = QtToRMat (Loadv4 (4 + 4 * idBlk + 2)) * vnBlk;
  }
  return dMin;
}

float BlkHitSh (vec3 ro, vec3 rd, float rng)
{
  mat3 m;
  vec3 rm, rdm, v, tm, tp, bSize;
  float dMin, dn, df;
  bSize = 0.5 * blkGap * (blkSph - 1.) + 0.4;
  dMin = dstFar;
  for (int n = 0; n < nBlock; n ++) {
    rm = Loadv4 (4 + 4 * n).xyz;
    m = QtToRMat (Loadv4 (4 + 4 * n + 2));
    rdm = rd * m;
    v = ((ro - rm) * m) / rdm;
    tp = bSize / abs (rdm) - v;
    tm = - tp - 2. * v;
    dn = max (max (tm.x, tm.y), tm.z);
    df = min (min (tp.x, tp.y), tp.z);
    if (df > 0. && dn < min (df, dMin)) dMin = dn;
  }
  return smoothstep (0., rng, dMin);
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec4 objCol;
  vec3 col, vn, ltDirUp;
  vec2 w, iw;
  float dstBlk, dstSph, c, sh, dstFloor, dstBack;
  bool isBg, isBlk;
  isBg = false;
  isBlk = false;
  ltDirUp = vec3 (0., -1., 0.);
  dstSph = SphHit (ro, rd);
  dstBlk = BlkHit (ro, rd);
  dstFloor = (rd.y < 0.) ? - (ro.y + 1.4 * spRad) / rd.y : dstFar;
  dstBack = (rd.z > 0.) ? (- ro.z + 1.4 * spRad) / rd.z : dstFar;
  if (dstBlk < dstFar) {
    ro += rd * dstBlk;
    vn = vnBlk;
    c = float (idBlk) / float (nBlock);
    objCol = (max (abs (qBlk.x), abs (qBlk.y)) > 0.7) ? vec4 (1.) :
       vec4 (HsvToRgb (vec3 (c, 1. - 0.3 * mod (4. * c, 1.),
       1. - 0.4 * mod (7. * c, 1.))), 0.5);
    isBlk = true;
  } else if (min (dstBack, dstFloor) < dstFar) {
    if (dstBack < dstFloor) {
      ro += dstBack * rd;
      w = 0.25 * ro.xy * vec2 (1., 2.);
      iw = floor (w);
      if (2. * floor (iw.y / 2.) != iw.y) w.x += 0.5;
      w = smoothstep (0.03, 0.05, abs (fract (w + 0.5) - 0.5));
      objCol = vec4 ((1. - 0.4 * w.x * w.y) * vec3 (0.6, 0.65, 0.6), 0.2);
      vn = vec3 (0., 0., -1.);
    } else if (dstFloor < dstFar) {
      ro += dstFloor * rd;
      objCol = vec4 (mix (vec3 (0.8, 0.4, 0.2), vec3 (0.45, 0.25, 0.1),
         Fbm2 (ro.xz * vec2 (1., 0.1))), 0.1) *
         (0.5 + 0.5 * smoothstep (0.05, 0.1, mod (ro.x, 4.)));
      vn = vec3 (0., 1., 0.);
    } else isBg = true;
  } else isBg = true;
  if (isBg) col = vec3 (0., 0., 0.1);
  else {
    sh = min (BlkHitSh (ro + 0.01 * ltDir, ltDir, 100.),
       0.7 + 0.3 * SphHitSh (ro + 0.01 * ltDir, ltDir, 100.));
    col = objCol.rgb * (0.1 + 0.9 * sh * max (dot (vn, ltDir), 0.)) +
       0.5 * objCol.a * sh * pow (max (0., dot (ltDir, reflect (rd, vn))), 64.);
    if (isBlk) {
      sh = BlkHitSh (vec3 (0., - 1.1 * spRad, 0.), ltDirUp, 100.);
      col += objCol.rgb * (0.3 * sh * max (dot (vn, ltDirUp), 0.)) +
         0.3 * objCol.a * sh * pow (max (0., dot (ltDirUp, reflect (rd, vn))), 64.);
    }
  }
  if (dstSph < dstFar) {
    col = mix (col, vec3 (0.15), pow (1. - abs (dot (rd, vnSph)), 4.));
    col += 0.01 + 0.07 * max (dot (vnSph, ltDir), 0.) +
       0.1 * pow (max (0., dot (ltDir, reflect (rd, vnSph))), 32.) +
       0.06 * max (dot (vnSph, ltDirUp), 0.) +
       0.08 * pow (max (0., dot (ltDirUp, reflect (rd, vnSph))), 32.);
  }

  return clamp (col, 0., 1.);
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec3 col, rd, ro;
  vec2 canvas, uv, ori, ca, sa;
  float az, el, ltAng;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  spRad = Loadv4 (0).y;
  dstFar = 150.;
  az = 0.15 * pi * cos (0.05 * pi * tCur);
  el = 0.1 * pi + 0.08 * pi * sin (0.027 * pi * tCur);
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  vuMat = mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x) *
     mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y);
  ro = vec3 (0., 0., - spRad * 3.) * vuMat;
  rd = normalize (vec3 (uv, 2.3)) * vuMat;
  ltAng = 0.35 * pi * sin (0.023 * pi * tCur);
  ltDir = normalize (vec3 (sin (ltAng), 1., - cos (ltAng)));
  col = ShowScene (ro, rd);
  fragColor = vec4 (col, 1.);
}

// "Colliding Blocks" by dr2 - 2016
// License: Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License

#define txBuf iChannel0
#define txSize iChannelResolution[0].xy

vec4 QtMul (vec4 q1, vec4 q2)
{
  return vec4 (
       q1.w * q2.x - q1.z * q2.y + q1.y * q2.z + q1.x * q2.w,
       q1.z * q2.x + q1.w * q2.y - q1.x * q2.z + q1.y * q2.w,
     - q1.y * q2.x + q1.x * q2.y + q1.w * q2.z + q1.z * q2.w,
     - q1.x * q2.x - q1.y * q2.y - q1.z * q2.z + q1.w * q2.w);
}

mat3 QtToRMat (vec4 q) 
{
  mat3 m;
  float a1, a2, s;
  q = normalize (q);
  s = q.w * q.w - 0.5;
  m[0][0] = q.x * q.x + s;  m[1][1] = q.y * q.y + s;  m[2][2] = q.z * q.z + s;
  a1 = q.x * q.y;  a2 = q.z * q.w;  m[0][1] = a1 + a2;  m[1][0] = a1 - a2;
  a1 = q.x * q.z;  a2 = q.y * q.w;  m[2][0] = a1 + a2;  m[0][2] = a1 - a2;
  a1 = q.y * q.z;  a2 = q.x * q.w;  m[1][2] = a1 + a2;  m[2][1] = a1 - a2;
  return 2. * m;
}

vec4 EulToQt (vec3 e)
{
  float a1, a2, a3, c1, s1;
  a1 = 0.5 * e.y;  a2 = 0.5 * (e.x - e.z);  a3 = 0.5 * (e.x + e.z);
  s1 = sin (a1);  c1 = cos (a1);
  return normalize (vec4 (s1 * cos (a2), s1 * sin (a2), c1 * sin (a3),
     c1 * cos (a3)));
}

vec4 RMatToQt (mat3 m)
{
  vec4 q;
  const float tol = 1e-6;
  q.w = 0.5 * sqrt (max (1. + m[0][0] + m[1][1] + m[2][2], 0.));
  if (abs (q.w) > tol) q.xyz =
     vec3 (m[1][2] - m[2][1], m[2][0] - m[0][2], m[0][1] - m[1][0]) / (4. * q.w);
  else {
    q.x = sqrt (max (0.5 * (1. + m[0][0]), 0.));
    if (abs (q.x) > tol) q.yz = vec2 (m[0][1], m[0][2]) / q.x;
    else {
      q.y = sqrt (max (0.5 * (1. + m[1][1]), 0.));
      if (abs (q.y) > tol) q.z = m[1][2] / q.y;
      else q.z = 1.;
    }
  }
  return normalize (q);
}

mat3 LpStepMat (vec3 a)
{
  mat3 m1, m2;
  vec3 t, c, s;
  float b1, b2;
  t = 0.25 * a * a;
  c = (1. - t) / (1. + t);
  s = a / (1. + t);
  m1[0][0] = c.y * c.z;  m2[0][0] = c.y * c.z;
  b1 = s.x * s.y * c.z;  b2 = c.x * s.z;
  m1[0][1] = b1 + b2;  m2[1][0] = b1 - b2;
  b1 = c.x * s.y * c.z;  b2 = s.x * s.z;
  m1[0][2] = - b1 + b2;  m2[2][0] = b1 + b2;
  b1 = c.y * s.z;
  m1[1][0] = - b1;  m2[0][1] = b1;  
  b1 = s.x * s.y * s.z;  b2 = c.x * c.z;
  m1[1][1] = - b1 + b2;  m2[1][1] = b1 + b2; 
  b1 = c.x * s.y * s.z;  b2 = s.x * c.z;
  m1[1][2] = b1 + b2;  m2[2][1] = b1 - b2;
  m1[2][0] = s.y;  m2[0][2] = - s.y;
  b1 = s.x * c.y;
  m1[2][1] = - b1;  m2[1][2] = b1;
  b1 = c.x * c.y;
  m1[2][2] = b1;  m2[2][2] = b1;
  return m1 * m2;
}

float Hashff (float p)
{
  const float cHashM = 43758.54;
  return fract (sin (p) * cHashM);
}

const float txRow = 128.;

vec4 Loadv4 (int idVar)
{
  float fi;
  fi = float (idVar);
  return texture (txBuf, (vec2 (mod (fi, txRow), floor (fi / txRow)) + 0.5) /
     txSize);
}

void Savev4 (int idVar, vec4 val, inout vec4 fCol, vec2 fCoord)
{
  vec2 d;
  float fi;
  fi = float (idVar);
  d = abs (fCoord - vec2 (mod (fi, txRow), floor (fi / txRow)) - 0.5);
  if (max (d.x, d.y) < 0.5) fCol = val;
}

const float pi = 3.14159;

const int nBlock = 64;
const int nSiteBk = 27;
const vec3 blkSph = vec3 (3.);
const vec3 blkGap = vec3 (0.4);

float nStep, spRad, gravSgn;

vec3 RSite (int sId)
{
  float sIdf;
  sIdf = float (sId);
  return blkGap * (floor (vec3 (mod (sIdf, blkSph.x),
      mod (sIdf, blkSph.x * blkSph.y) / blkSph.x,
      sIdf / (blkSph.x * blkSph.y))) - 0.5 * (blkSph - 1.));
}

vec3 FcFun (vec3 dr, float rSep, vec3 dv)
{
  vec3 f;
  float vRel, fo, drv;
  const float fOvlap = 1000., fricN = 5., fricT = 0.5, fricS = 5.;
  fo = fOvlap * (1. / rSep - 1.);
  drv = dot (dr, dv) / (rSep * rSep);
  dv -= drv * dr;
  vRel = length (dv);
  fo = max (fo - fricN * drv, 0.);
  f = fo * dr;
  if (vRel > 0.001) f -= min (fricT, fricS * abs (fo) * rSep / vRel) * dv;
  return f;
}

void Step (int mId, out vec3 rm, out vec3 vm, out vec4 qm, out vec3 wm)
{
  mat3 mRot, mRotN;
  vec4 drw4;
  vec3 rmN, vmN, wmN, dr, dv, rts, rtsN, rms, vms, fc, am, wam, dSp, drw;
  float farSep, rSep, grav, dt;
  grav = 2. * gravSgn;
  dt = 0.01;
  rm = Loadv4 (4 + 4 * mId).xyz;
  vm = Loadv4 (4 + 4 * mId + 1).xyz;
  qm = Loadv4 (4 + 4 * mId + 2);
  wm = Loadv4 (4 + 4 * mId + 3).xyz;
  mRot = QtToRMat (qm);
  farSep = length (blkGap * (blkSph - 1.)) + 1.;
  am = vec3 (0.);
  wam = vec3 (0.);
  for (int n = 0; n < nBlock; n ++) {
    rmN = Loadv4 (4 + 4 * n).xyz;
    if (n != mId && length (rm - rmN) < farSep) {
      vmN = Loadv4 (4 + 4 * n + 1).xyz;
      mRotN = QtToRMat (Loadv4 (4 + 4 * n + 2));
      wmN = Loadv4 (4 + 4 * n + 3).xyz;
      for (int j = 0; j < nSiteBk; j ++) {
        rts = mRot * RSite (j);
        rms = rm + rts;
        vms = vm + cross (wm, rts);
        dv = vms - vmN;
        fc = vec3 (0.);
        for (int jN = 0; jN < nSiteBk; jN ++) {
          rtsN = mRotN * RSite (jN);
          dr = rms - (rmN + rtsN);
          rSep = length (dr);
          if (rSep < 1.) fc += FcFun (dr, rSep, dv - cross (wmN, rtsN));
        }
        am += fc;
        wam += cross (rts, fc);
      }
    }
  }
  if (length (rm) > spRad - 0.5 * (farSep + 1.)) {
    for (int j = 0; j < nSiteBk; j ++) {
      rts = mRot * RSite (j);
      dr = rm + rts - spRad * normalize (rm + rts);
      rSep = length (dr);
      if (rSep < 1.) {
        fc = FcFun (dr, rSep, vm + cross (wm, rts));
        am += fc;
        wam += cross (rts, fc);
      }
    }
  }
  am.y -= grav;
  dSp = blkGap * blkSph;
  wam = mRot * (wam * mRot / (0.5 * (vec3 (dot (dSp, dSp)) - dSp * dSp) + 1.));
  vm += dt * am;
  rm += dt * vm;
  wm += dt * wam;
  qm = normalize (QtMul (RMatToQt (LpStepMat (0.5 * dt * wm)), qm));
}

void Init (int mId, out vec3 rm, out vec3 vm, out vec4 qm, out vec3 wm)
{
  vec3 e;
  float mIdf, nmEdge;
  mIdf = float (mId);
  nmEdge = floor (pow (float (nBlock), 1./3.) + 0.1);
  rm = 3. * (floor (vec3 (mod (mIdf, nmEdge),
      mod (mIdf, nmEdge * nmEdge) / nmEdge,
      mIdf / (nmEdge * nmEdge))) - 0.5 * (nmEdge - 1.));
  e = normalize (vec3 (Hashff (mIdf), Hashff (mIdf + 0.3),
     Hashff (mIdf + 0.6)));
  vm = e - 0.5;
  qm = EulToQt (e);
  wm = 1.5 * e;
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  vec4 stDat, p, qm;
  vec3 rm, vm, wm;
  vec2 iFrag;
  int mId, pxId;
  bool doInit;
  iFrag = floor (fragCoord);
  pxId = int (iFrag.x + txRow * iFrag.y);
  if (iFrag.x >= txRow || pxId >= 4 * nBlock + 4) discard;
  if (pxId >= 4) mId = (pxId - 4) / 4;
  else mId = -1;
  doInit = false;
  if (iFrame <= 5) {
    spRad = 2.5 * floor (pow (float (nBlock), 1./3.) + 0.1);
    doInit = true;
  } else {
    stDat = Loadv4 (0);
    nStep = stDat.x;
    spRad = stDat.y;
    ++ nStep;
    gravSgn = (mod (nStep, 1000.) < 700.) ? 1. : -1.;
    if (mId >= 0) Step (mId, rm, vm, qm, wm);
  }
  if (doInit) {
    nStep = 100.;
    if (mId >= 0) Init (mId, rm, vm, qm, wm);
  }
  if (pxId == 0) stDat = vec4 (nStep, spRad, 0., 0.);
  else if (pxId == 4 + 4 * mId) p = vec4 (rm, 0.);
  else if (pxId == 4 + 4 * mId + 1) p = vec4 (vm, 0.);
  else if (pxId == 4 + 4 * mId + 2) p = qm;
  else if (pxId == 4 + 4 * mId + 3) p = vec4 (wm, 0.);
  Savev4 (pxId, ((pxId >= 4) ? p : stDat), fragColor, fragCoord);
}

// "Colliding Blocks" by dr2 - 2016
// License: Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License

#define txBuf iChannel0
#define txSize iChannelResolution[0].xy

vec4 QtMul (vec4 q1, vec4 q2)
{
  return vec4 (
       q1.w * q2.x - q1.z * q2.y + q1.y * q2.z + q1.x * q2.w,
       q1.z * q2.x + q1.w * q2.y - q1.x * q2.z + q1.y * q2.w,
     - q1.y * q2.x + q1.x * q2.y + q1.w * q2.z + q1.z * q2.w,
     - q1.x * q2.x - q1.y * q2.y - q1.z * q2.z + q1.w * q2.w);
}

mat3 QtToRMat (vec4 q) 
{
  mat3 m;
  float a1, a2, s;
  q = normalize (q);
  s = q.w * q.w - 0.5;
  m[0][0] = q.x * q.x + s;  m[1][1] = q.y * q.y + s;  m[2][2] = q.z * q.z + s;
  a1 = q.x * q.y;  a2 = q.z * q.w;  m[0][1] = a1 + a2;  m[1][0] = a1 - a2;
  a1 = q.x * q.z;  a2 = q.y * q.w;  m[2][0] = a1 + a2;  m[0][2] = a1 - a2;
  a1 = q.y * q.z;  a2 = q.x * q.w;  m[1][2] = a1 + a2;  m[2][1] = a1 - a2;
  return 2. * m;
}

vec4 EulToQt (vec3 e)
{
  float a1, a2, a3, c1, s1;
  a1 = 0.5 * e.y;  a2 = 0.5 * (e.x - e.z);  a3 = 0.5 * (e.x + e.z);
  s1 = sin (a1);  c1 = cos (a1);
  return normalize (vec4 (s1 * cos (a2), s1 * sin (a2), c1 * sin (a3),
     c1 * cos (a3)));
}

vec4 RMatToQt (mat3 m)
{
  vec4 q;
  const float tol = 1e-6;
  q.w = 0.5 * sqrt (max (1. + m[0][0] + m[1][1] + m[2][2], 0.));
  if (abs (q.w) > tol) q.xyz =
     vec3 (m[1][2] - m[2][1], m[2][0] - m[0][2], m[0][1] - m[1][0]) / (4. * q.w);
  else {
    q.x = sqrt (max (0.5 * (1. + m[0][0]), 0.));
    if (abs (q.x) > tol) q.yz = vec2 (m[0][1], m[0][2]) / q.x;
    else {
      q.y = sqrt (max (0.5 * (1. + m[1][1]), 0.));
      if (abs (q.y) > tol) q.z = m[1][2] / q.y;
      else q.z = 1.;
    }
  }
  return normalize (q);
}

mat3 LpStepMat (vec3 a)
{
  mat3 m1, m2;
  vec3 t, c, s;
  float b1, b2;
  t = 0.25 * a * a;
  c = (1. - t) / (1. + t);
  s = a / (1. + t);
  m1[0][0] = c.y * c.z;  m2[0][0] = c.y * c.z;
  b1 = s.x * s.y * c.z;  b2 = c.x * s.z;
  m1[0][1] = b1 + b2;  m2[1][0] = b1 - b2;
  b1 = c.x * s.y * c.z;  b2 = s.x * s.z;
  m1[0][2] = - b1 + b2;  m2[2][0] = b1 + b2;
  b1 = c.y * s.z;
  m1[1][0] = - b1;  m2[0][1] = b1;  
  b1 = s.x * s.y * s.z;  b2 = c.x * c.z;
  m1[1][1] = - b1 + b2;  m2[1][1] = b1 + b2; 
  b1 = c.x * s.y * s.z;  b2 = s.x * c.z;
  m1[1][2] = b1 + b2;  m2[2][1] = b1 - b2;
  m1[2][0] = s.y;  m2[0][2] = - s.y;
  b1 = s.x * c.y;
  m1[2][1] = - b1;  m2[1][2] = b1;
  b1 = c.x * c.y;
  m1[2][2] = b1;  m2[2][2] = b1;
  return m1 * m2;
}

float Hashff (float p)
{
  const float cHashM = 43758.54;
  return fract (sin (p) * cHashM);
}

const float txRow = 128.;

vec4 Loadv4 (int idVar)
{
  float fi;
  fi = float (idVar);
  return texture (txBuf, (vec2 (mod (fi, txRow), floor (fi / txRow)) + 0.5) /
     txSize);
}

void Savev4 (int idVar, vec4 val, inout vec4 fCol, vec2 fCoord)
{
  vec2 d;
  float fi;
  fi = float (idVar);
  d = abs (fCoord - vec2 (mod (fi, txRow), floor (fi / txRow)) - 0.5);
  if (max (d.x, d.y) < 0.5) fCol = val;
}

const float pi = 3.14159;

const int nBlock = 64;
const int nSiteBk = 27;
const vec3 blkSph = vec3 (3.);
const vec3 blkGap = vec3 (0.4);

float nStep, spRad, gravSgn;

vec3 RSite (int sId)
{
  float sIdf;
  sIdf = float (sId);
  return blkGap * (floor (vec3 (mod (sIdf, blkSph.x),
      mod (sIdf, blkSph.x * blkSph.y) / blkSph.x,
      sIdf / (blkSph.x * blkSph.y))) - 0.5 * (blkSph - 1.));
}

vec3 FcFun (vec3 dr, float rSep, vec3 dv)
{
  vec3 f;
  float vRel, fo, drv;
  const float fOvlap = 1000., fricN = 5., fricT = 0.5, fricS = 5.;
  fo = fOvlap * (1. / rSep - 1.);
  drv = dot (dr, dv) / (rSep * rSep);
  dv -= drv * dr;
  vRel = length (dv);
  fo = max (fo - fricN * drv, 0.);
  f = fo * dr;
  if (vRel > 0.001) f -= min (fricT, fricS * abs (fo) * rSep / vRel) * dv;
  return f;
}

void Step (int mId, out vec3 rm, out vec3 vm, out vec4 qm, out vec3 wm)
{
  mat3 mRot, mRotN;
  vec4 drw4;
  vec3 rmN, vmN, wmN, dr, dv, rts, rtsN, rms, vms, fc, am, wam, dSp, drw;
  float farSep, rSep, grav, dt;
  grav = 2. * gravSgn;
  dt = 0.01;
  rm = Loadv4 (4 + 4 * mId).xyz;
  vm = Loadv4 (4 + 4 * mId + 1).xyz;
  qm = Loadv4 (4 + 4 * mId + 2);
  wm = Loadv4 (4 + 4 * mId + 3).xyz;
  mRot = QtToRMat (qm);
  farSep = length (blkGap * (blkSph - 1.)) + 1.;
  am = vec3 (0.);
  wam = vec3 (0.);
  for (int n = 0; n < nBlock; n ++) {
    rmN = Loadv4 (4 + 4 * n).xyz;
    if (n != mId && length (rm - rmN) < farSep) {
      vmN = Loadv4 (4 + 4 * n + 1).xyz;
      mRotN = QtToRMat (Loadv4 (4 + 4 * n + 2));
      wmN = Loadv4 (4 + 4 * n + 3).xyz;
      for (int j = 0; j < nSiteBk; j ++) {
        rts = mRot * RSite (j);
        rms = rm + rts;
        vms = vm + cross (wm, rts);
        dv = vms - vmN;
        fc = vec3 (0.);
        for (int jN = 0; jN < nSiteBk; jN ++) {
          rtsN = mRotN * RSite (jN);
          dr = rms - (rmN + rtsN);
          rSep = length (dr);
          if (rSep < 1.) fc += FcFun (dr, rSep, dv - cross (wmN, rtsN));
        }
        am += fc;
        wam += cross (rts, fc);
      }
    }
  }
  if (length (rm) > spRad - 0.5 * (farSep + 1.)) {
    for (int j = 0; j < nSiteBk; j ++) {
      rts = mRot * RSite (j);
      dr = rm + rts - spRad * normalize (rm + rts);
      rSep = length (dr);
      if (rSep < 1.) {
        fc = FcFun (dr, rSep, vm + cross (wm, rts));
        am += fc;
        wam += cross (rts, fc);
      }
    }
  }
  am.y -= grav;
  dSp = blkGap * blkSph;
  wam = mRot * (wam * mRot / (0.5 * (vec3 (dot (dSp, dSp)) - dSp * dSp) + 1.));
  vm += dt * am;
  rm += dt * vm;
  wm += dt * wam;
  qm = normalize (QtMul (RMatToQt (LpStepMat (0.5 * dt * wm)), qm));
}

void Init (int mId, out vec3 rm, out vec3 vm, out vec4 qm, out vec3 wm)
{
  vec3 e;
  float mIdf, nmEdge;
  mIdf = float (mId);
  nmEdge = floor (pow (float (nBlock), 1./3.) + 0.1);
  rm = 3. * (floor (vec3 (mod (mIdf, nmEdge),
      mod (mIdf, nmEdge * nmEdge) / nmEdge,
      mIdf / (nmEdge * nmEdge))) - 0.5 * (nmEdge - 1.));
  e = normalize (vec3 (Hashff (mIdf), Hashff (mIdf + 0.3),
     Hashff (mIdf + 0.6)));
  vm = e - 0.5;
  qm = EulToQt (e);
  wm = 1.5 * e;
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  vec4 stDat, p, qm;
  vec3 rm, vm, wm;
  vec2 iFrag;
  int mId, pxId;
  bool doInit;
  iFrag = floor (fragCoord);
  pxId = int (iFrag.x + txRow * iFrag.y);
  if (iFrag.x >= txRow || pxId >= 4 * nBlock + 4) discard;
  if (pxId >= 4) mId = (pxId - 4) / 4;
  else mId = -1;
  doInit = false;
  if (iFrame <= 5) {
    spRad = 2.5 * floor (pow (float (nBlock), 1./3.) + 0.1);
    doInit = true;
  } else {
    stDat = Loadv4 (0);
    nStep = stDat.x;
    spRad = stDat.y;
    ++ nStep;
    gravSgn = (mod (nStep, 1000.) < 700.) ? 1. : -1.;
    if (mId >= 0) Step (mId, rm, vm, qm, wm);
  }
  if (doInit) {
    nStep = 100.;
    if (mId >= 0) Init (mId, rm, vm, qm, wm);
  }
  if (pxId == 0) stDat = vec4 (nStep, spRad, 0., 0.);
  else if (pxId == 4 + 4 * mId) p = vec4 (rm, 0.);
  else if (pxId == 4 + 4 * mId + 1) p = vec4 (vm, 0.);
  else if (pxId == 4 + 4 * mId + 2) p = qm;
  else if (pxId == 4 + 4 * mId + 3) p = vec4 (wm, 0.);
  Savev4 (pxId, ((pxId >= 4) ? p : stDat), fragColor, fragCoord);
}
