void mainImage( out vec4 fragColor, in vec2 fragCoord )
{   
    vec2 r =  2.0 * vec2(fragCoord.xy - 0.5*iResolution.xy)/iResolution.y;

    vec3 colorTop = vec3(1.0, 0.0, 0.0); // red
    vec3 colorBottom = vec3(0.0, 0.0, 1.0); // blue
    vec3 colorDeep = vec3(0.0, 0.0, 0.6); // blue darker
    vec3 colorSurface = vec3(0.4, 0.8, 0.8); // lighter
    vec3 colorClouds = vec3(0.6, 0.0, 0.0); 

    float cloudPoint = 0.7 + cos((r.x * 1.5) - (iTime * 0.25) ) * 0.05;
    cloudPoint += sin( (14.0 * r.x) - (iTime * 1.5)  ) * 0.02;
    cloudPoint += cos( (4.0 * r.x) - (iTime * 0.5)  ) * 0.05;

    float midPoint = sin(r.x + (iTime * 5.0) ) * 0.3;
    midPoint += sin( (10.0 * r.x) + (iTime * 20.0) ) * 0.05;

    float darkWavePoint = -0.2 + sin(r.x + (iTime * 3.0) ) * 0.2;
    darkWavePoint += sin( (20.0 * r.x) + (iTime * 20.0) ) * 0.015;

    float surfaceOffset = 0.2;

    vec3 pixel;
    float alpha = 1.0;


    if (r.y >= cloudPoint)
    {
        pixel = colorClouds;
        //pixel = colorTop; 
    }
    else if (r.y >= midPoint)
    {
        if (r.y > darkWavePoint + surfaceOffset)
        {
          pixel = colorTop;  
        }
        else
        {
            pixel = colorSurface;
        }
    }
    else if (r.y >= darkWavePoint)
    {
        pixel = colorDeep;
    }
    else
    {
        pixel = colorBottom;

    }

    fragColor = vec4(pixel, alpha);


}
