mat3 QtToRMat (vec4 q);
float SmoothBump (float lo, float hi, float w, float x);
float Maxv3 (vec3 p);
vec3 HsvToRgb (vec3 c);
float Noisefv2 (vec2 p);
float Fbm2 (vec2 p);
vec3 VaryNf (vec3 p, vec3 n, float f);
vec4 Loadv4 (int idVar);

vec3 sunDir, qHit, qnHit;
float tCur, dstFar, cylRad, cylLen;
int idHit;
const float pi = 3.14159;
const int nObj = 16, nSphObj = 48;

float GrndHt (vec2 p)
{
  mat2 fqRot;
  vec2 q;
  float h, a;
  fqRot = 2. * mat2 (0.6, -0.8, 0.8, 0.6);
  q = 0.025 * p;
  h = 0.;
  a = 10.;
  for (int j = 0; j < 4; j ++) {
    h += a * Noisefv2 (q);
    a *= 0.5;
    q *= fqRot;
  }
  return h;
}

float GrndRay (vec3 ro, vec3 rd)
{
  vec3 p;
  float dHit, h, s, sLo, sHi;
  s = 0.;
  sLo = 0.;
  dHit = dstFar;
  for (int j = 0; j < 200; j ++) {
    p = ro + s * rd;
    h = p.y - GrndHt (p.xz);
    if (h < 0.) break;
    sLo = s;
    s += max (0.5, 0.5 * h);
    if (s > dstFar) break;
  }
  if (h < 0.) {
    sHi = s;
    for (int j = 0; j < 5; j ++) {
      s = 0.5 * (sLo + sHi);
      p = ro + s * rd;
      if (p.y > GrndHt (p.xz)) sLo = s;
      else sHi = s;
    }
    dHit = 0.5 * (sLo + sHi);
  }
  return dHit;
}

vec3 GrndNf (vec3 p)
{
  const vec2 e = vec2 (0.01, 0.);
  return normalize (vec3 (GrndHt (p.xz) - vec2 (GrndHt (p.xz + e.xy), GrndHt (p.xz + e.yx)), e.x).xzy);
}

float GrndSShadow (vec3 ro, vec3 rd)
{
  vec3 p;
  float sh, d, h;
  sh = 1.;
  d = 0.1;
  for (int j = 0; j < 16; j ++) {
    p = ro + rd * d;
    h = p.y - GrndHt (p.xz);
    sh = min (sh, smoothstep (0., 0.05 * d, h));
    d += max (0.2, 0.1 * d);
    if (sh < 0.05) break;
  }
  return sh;
}

vec3 RSph (float s)
{
  vec3 r;
  float a;
  if (s < 30.) {
    a = 2. * pi * mod (s, 15.) / 15.;
    r = vec3 ((cylRad - 0.5) * cos (a), (cylRad - 0.5) * sin (a),
       cylLen * (1. - 2. * step (s, 15. - 0.5)));
  } else if (s < 42.) {
    s -= 30.;
    a = 2. * pi * mod (s, 6.) / 6.;
    r = vec3 ((cylRad - 1.5) * cos (a), (cylRad - 1.5) * sin (a),
       cylLen * (1. - 2. * step (s, 6. - 0.5)));
  } else {
    s -= 42.;
    r = vec3 (0., 0., cylLen * (2. * s / 5. - 1.));
  }
  return r;
}

vec4 CylHit (vec3 ro, vec3 rd, float r, float h)
{
  vec3 s;
  float d, a, b, w;
  d = dstFar;
  a = dot (rd.xy, rd.xy);
  b = dot (rd.xy, ro.xy);
  w = b * b - a * (dot (ro.xy, ro.xy) - r * r);
  if (w > 0.) {
    if (a > 0.) {
      d = (- b - sqrt (w)) / a;
      s = ro + d * rd;
    } else s = vec3 (0., 0., h);
    if (d < 0. || abs (s.z) > h) {
      d = - (sign (rd.z) * ro.z + h) / abs (rd.z);
      if (d < 0. || length (ro.xy + d * rd.xy) > r) d = dstFar;
    }
  }
  return vec4 (s, d);
}

float ObjHit (vec3 ro, vec3 rd)
{
  mat3 mRot;
  vec4 ds;
  vec3 rm, rom, rdm, rs;
  float dMin, len, rad, sRad, b, w;
  dMin = dstFar;
  sRad = length (vec2 (cylRad, cylLen)) + 1.;
  for (int n = 0; n < nObj; n ++) {
    rm = Loadv4 (4 * n).xyz;
    rom = ro - rm;
    b = dot (rd, rom);
    w = b * b - dot (rom, rom) + sRad * sRad;
    if (w >= 0.) {
      if (b + sqrt (w) < 0.) {
        mRot = QtToRMat (Loadv4 (4 * n + 2));
        rom = rom * mRot;
        rdm = rd * mRot;
        for (int k = 0; k < 3; k ++) {
          len = (k < 2) ? 0.5 : cylLen;
          rad = (k < 2) ? cylRad : 0.5;
          rs = rom;
          if (k < 2) rs.z -= (k == 0) ? cylLen : - cylLen;
          ds = CylHit (rs, rdm, rad, len);
          if (ds.w < dMin) {
            dMin = ds.w;
            qHit = rom + dMin * rdm;
            qnHit = mRot * ((abs (ds.z) > len) ? vec3 (0., 0., - sign (rdm.z)) :
               vec3 (normalize (ds.xy), 0.));
            idHit = n;
          }
        }
      }
    }
  }
  return dMin;
}

float ObjHitSh (vec3 ro, vec3 rd, float rng)
{
  mat3 mRot;
  vec3 rm, rom, rdm, rs;
  float dMin, len, rad, sRad, b;
  dMin = dstFar;
  sRad = length (vec2 (cylRad, cylLen)) + 1.;
  for (int n = 0; n < nObj; n ++) {
    rm = Loadv4 (4 * n).xyz;
    rom = ro - rm;
    b = dot (rd, rom);
    if (b * b > dot (rom, rom) - sRad * sRad) {
      mRot = QtToRMat (Loadv4 (4 * n + 2));
      rom = rom * mRot;
      rdm = rd * mRot;
      for (int k = 0; k < 3; k ++) {
        len = (k < 2) ? 0.5 : cylLen;
        rad = (k < 2) ? cylRad : 0.5;
        rs = rom;
        if (k < 2) rs.z -= (k == 0) ? cylLen : - cylLen;
        dMin = min (dMin, CylHit (rs, rdm, rad, len).w);
      }
    }
  }
  return smoothstep (0.5 * rng, rng, dMin);
}

vec3 BgCol (vec3 ro, vec3 rd)
{
  vec3 col;
  rd.y = abs (rd.y);
  ro.xz += 2. * tCur;
  col = vec3 (0.1, 0.2, 0.4) + 0.1 * (1. - max (rd.y, 0.)) +
     0.1 * pow (max (dot (rd, sunDir), 0.), 16.);
  col = mix (col, vec3 (0.8), clamp (0.2 + Fbm2 (0.05 *
     (ro.xz + rd.xz * (100. - ro.y) / max (rd.y, 0.001))) * rd.y, 0., 1.));
  return col;
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec4 objCol;
  vec3 col, vn, bgCol;
  float dstObj, dstGrnd, sh, f, r, a;
  cylRad = 2.5;
  cylLen = 2.;
  bgCol = BgCol (ro, rd);
  dstObj = ObjHit (ro, rd);
  dstGrnd = GrndRay (ro, rd);
  if (min (dstObj, dstGrnd) < dstFar) {
    if (dstObj < dstGrnd) {
      ro += rd * dstObj;
      vn = qnHit;
      objCol = vec4 (HsvToRgb (vec3 (mod (float (idHit) / float (nObj), 1.), 1., 0.9)), 0.2);
      r = length (qHit.xy);
      if (r < cylRad - 0.001 && abs (qHit.z) > cylLen - 0.55)
         objCol.rgb *= 1. - 0.3 * SmoothBump (0.4, 0.6, 0.05, mod (1.6 * r, 1.));
      else {
        a = atan (qHit.y, qHit.x) / pi;
        objCol.rgb *= 1. - 0.3 * SmoothBump (0.4, 0.6, 0.05, mod (((r > 0.51) ? 8. : 2.) * a, 1.));
      }
      sh = 1.;
    } else {
      ro += rd * dstGrnd;
      vn = GrndNf (ro);
      f = 1. - clamp (0.5 * pow (vn.y, 4.) + Fbm2 (0.5 * ro.xz) - 0.5, 0., 1.);
      vn = VaryNf (4. * ro, vn, 6. * f * f);
      objCol = vec4 (mix (vec3 (0.6, 0.5, 0.4), vec3 (0.3, 0.25, 0.2), f) *
         (1. - 0.1 * Noisefv2 (8. * ro.xz)), 0.05);
      sh = GrndSShadow (ro, sunDir);
    }
    sh = 0.6 + 0.4 * min (sh, ObjHitSh (ro + 0.01 * sunDir, sunDir, 20.));
    col = objCol.rgb * (0.2 + 0.8 * sh * max (dot (vn, sunDir), 0.) +
       0.1 * max (- dot (vn.xz, normalize (sunDir.xz)), 0.)) +
       objCol.a * sh * pow (max (dot (normalize (sunDir - rd), vn), 0.), 32.);
    col *= mix (1., smoothstep (0., 1., Maxv3 (col)), 0.2);
    col = mix (col, bgCol, clamp (4. * min (dstObj, dstGrnd) / dstFar - 3., 0., 1.));
  } else col = bgCol;
  return clamp (col, 0., 1.);
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr;
  vec3 col, rd, ro, vd, rMid, rLead;
  vec2 canvas, uv;
  float az, el;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  az = 0.015 * pi * tCur;
  el = pi * (0.08 + 0.05 * sin (0.021 * pi * tCur));
  if (mPtr.z > 0.) {
    az += 2. * pi * mPtr.x;
    el += 0.2 * pi * mPtr.y;
  }
  el = clamp (el, 0.02 * pi, 0.3 * pi);
  rMid = Loadv4 (4 * nObj).xyz;
  rLead = Loadv4 (4 * nObj + 1).xyz;
  ro = 0.5 * (rLead + rMid) + 60. * vec3 (cos (el) * cos (az), sin (el), cos (el) * sin (az));
  ro.y = max (ro.y, GrndHt (ro.xz) + 6.);
  vd = normalize (rMid - ro);
  vuMat = mat3 (vec3 (vd.z, 0., - vd.x) / sqrt (1. - vd.y * vd.y),
     vec3 (- vd.y * vd.x, 1. - vd.y * vd.y, - vd.y * vd.z) / sqrt (1. - vd.y * vd.y), vd);
  rd = vuMat * normalize (vec3 (uv, 2.2));
  dstFar = 150.;
  sunDir = normalize (vec3 (1., 2., -1.));
  col = pow (clamp (ShowScene (ro, rd), 0., 1.), vec3 (0.8));
  fragColor = vec4 (col, 1.);
}

mat3 QtToRMat (vec4 q) 
{
  mat3 m;
  float a1, a2, s;
  q = normalize (q);
  s = q.w * q.w - 0.5;
  m[0][0] = q.x * q.x + s;  m[1][1] = q.y * q.y + s;  m[2][2] = q.z * q.z + s;
  a1 = q.x * q.y;  a2 = q.z * q.w;  m[0][1] = a1 + a2;  m[1][0] = a1 - a2;
  a1 = q.x * q.z;  a2 = q.y * q.w;  m[2][0] = a1 + a2;  m[0][2] = a1 - a2;
  a1 = q.y * q.z;  a2 = q.x * q.w;  m[1][2] = a1 + a2;  m[2][1] = a1 - a2;
  return 2. * m;
}

float SmoothBump (float lo, float hi, float w, float x)
{
  return (1. - smoothstep (hi - w, hi + w, x)) * smoothstep (lo - w, lo + w, x);
}

float Maxv3 (vec3 p)
{
  return max (p.x, max (p.y, p.z));
}

vec3 HsvToRgb (vec3 c)
{
  vec3 p;
  p = abs (fract (c.xxx + vec3 (1., 2./3., 1./3.)) * 6. - 3.);
  return c.z * mix (vec3 (1.), clamp (p - 1., 0., 1.), c.y);
}

const float cHashM = 43758.54;

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (vec2 (dot (p, cHashVA2), dot (p + vec2 (1., 0.), cHashVA2))) * cHashM);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);  
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}

float Fbm2 (vec2 p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int i = 0; i < 5; i ++) {
    f += a * Noisefv2 (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

float Fbmn (vec3 p, vec3 n)
{
  vec3 s;
  float a;
  s = vec3 (0.);
  a = 1.;
  for (int i = 0; i < 3; i ++) {
    s += a * vec3 (Noisefv2 (p.yz), Noisefv2 (p.zx), Noisefv2 (p.xy));
    a *= 0.5;
    p *= 2.;
  }
  return dot (s, abs (n));
}

vec3 VaryNf (vec3 p, vec3 n, float f)
{
  vec3 g;
  vec3 e = vec3 (0.1, 0., 0.);
  g = vec3 (Fbmn (p + e.xyy, n), Fbmn (p + e.yxy, n), Fbmn (p + e.yyx, n)) - Fbmn (p, n);
  return normalize (n + f * (g - n * dot (n, g)));
}

#define txBuf iChannel0
#define txSize iChannelResolution[0].xy

const float txRow = 128.;

vec4 Loadv4 (int idVar)
{
  float fi;
  fi = float (idVar);
  return texture (txBuf, (vec2 (mod (fi, txRow), floor (fi / txRow)) + 0.5) /
     txSize);
}

