vec4 QtMul (vec4 q1, vec4 q2);
mat3 QtToRMat (vec4 q);
vec4 RMatToQt (mat3 m);
vec4 EulToQt (vec3 e);
mat3 LpStepMat (vec3 a);
float Hashff (float p);
float Noisefv2 (vec2 p);
vec4 Loadv4 (int idVar);
void Savev4 (int idVar, vec4 val, inout vec4 fCol, vec2 fCoord);

vec3 rLead;
float todCur, cylRad, cylLen;
const float pi = 3.14159;
const float txRow = 128.;
const int nObj = 16, nSphObj = 48;

float GrndHt (vec2 p)
{
  mat2 fqRot;
  vec2 q;
  float h, a;
  fqRot = 2. * mat2 (0.6, -0.8, 0.8, 0.6);
  q = 0.025 * p;
  h = 0.;
  a = 10.;
  for (int j = 0; j < 4; j ++) {
    h += a * Noisefv2 (q);
    a *= 0.5;
    q *= fqRot;
  }
  return h;
}

vec3 GrndNf (vec3 p)
{
  const vec2 e = vec2 (0.01, 0.);
  return normalize (vec3 (GrndHt (p.xz) - vec2 (GrndHt (p.xz + e.xy), GrndHt (p.xz + e.yx)), e.x).xzy);
}

vec3 RSph (float s)
{
  vec3 r;
  float a;
  if (s < 30.) {
    a = 2. * pi * mod (s, 15.) / 15.;
    r = vec3 ((cylRad - 0.5) * cos (a), (cylRad - 0.5) * sin (a),
       cylLen * (1. - 2. * step (s, 15. - 0.5)));
  } else if (s < 42.) {
    s -= 30.;
    a = 2. * pi * mod (s, 6.) / 6.;
    r = vec3 ((cylRad - 1.5) * cos (a), (cylRad - 1.5) * sin (a),
       cylLen * (1. - 2. * step (s, 6. - 0.5)));
  } else {
    s -= 42.;
    r = vec3 (0., 0., cylLen * (2. * s / 5. - 1.));
  }
  return r;
}

vec3 FcFun (vec3 dr, float rSep, vec3 dv)
{
  vec3 f;
  float vRel, fo, drv;
  const float fOvlap = 1000., fricN = 2., fricT = 2., fricS = 10.;
  fo = fOvlap * (1. / rSep - 1.);
  drv = dot (dr, dv) / (rSep * rSep);
  dv -= drv * dr;
  vRel = length (dv);
  fo = max (fo - fricN * drv, 0.);
  f = fo * dr;
  if (vRel > 0.001) f -= min (fricT, fricS * abs (fo) * rSep / vRel) * dv;
  return f;
}

void Step (int mId, out vec3 rm, out vec3 vm, out vec4 qm, out vec3 wm)
{
  mat3 mRot, mRotN;
  vec3 rmN, vmN, wmN, dr, dv, rts, rtsN, rms, vms, fc, am, wam;
  vec3 rMom;
  float farSep, rSep, grav, fDamp, fAttr, dt;
  const vec2 e = vec2 (0.1, 0.);
  grav = 10.;
  fDamp = 0.1;
  fAttr = 0.1;
  dt = 0.01;
  rm = Loadv4 (4 * mId).xyz;
  vm = Loadv4 (4 * mId + 1).xyz;
  qm = Loadv4 (4 * mId + 2);
  wm = Loadv4 (4 * mId + 3).xyz;
  mRot = QtToRMat (qm);
  farSep = 2. * length (vec2 (cylRad, cylLen)) + 2.;
  am = vec3 (0.);
  wam = vec3 (0.);
  for (int n = 0; n < nObj; n ++) {
    rmN = Loadv4 (4 * n).xyz;
    if (n != mId && length (rm - rmN) < farSep) {
      vmN = Loadv4 (4 * n + 1).xyz;
      mRotN = QtToRMat (Loadv4 (4 * n + 2));
      wmN = Loadv4 (4 * n + 3).xyz;
      for (float j = 0.; j < float (nSphObj); j ++) {
        rts = mRot * RSph (j);
        rms = rm + rts;
        vms = vm + cross (wm, rts);
        dv = vms - vmN;
        fc = vec3 (0.);
        for (float jN = 0.; jN < float (nSphObj); jN ++) {
          rtsN = mRotN * RSph (jN);
          dr = rms - (rmN + rtsN);
          rSep = length (dr);
          if (rSep < 1.) fc += FcFun (dr, rSep, dv - cross (wmN, rtsN));
        }
        am += fc;
        wam += cross (rts, fc);
      }
    }
  }
  rMom = vec3 (0.);
  for (float j = 0.; j < float (nSphObj); j ++) {
    rts = RSph (float (j));
    rMom += dot (rts, rts) - rts * rts + 1./6.;
    rts = mRot * rts;
    dr = rm + rts;
    dr.xz = -0.55 * GrndNf (dr).xz;
    dr.y += 0.55 - GrndHt (rm.xz - dr.xz);
    rSep = length (dr);
    if (rSep < 1.) {
      fc = FcFun (dr, rSep, vm + cross (wm, rts));
      am += fc;
      wam += cross (rts, fc);
    }
  }
  rMom /= float (nSphObj);
  am -= fDamp * vm;
  am.y -= grav;
  am += fAttr * (rLead - rm);
  wam = mRot * (wam * mRot / rMom);
  vm += dt * am;
  rm += dt * vm;
  wm += dt * wam;
  qm = normalize (QtMul (RMatToQt (LpStepMat (0.5 * dt * wm)), qm));
}

void Init (int mId, out vec3 rm, out vec3 vm, out vec4 qm, out vec3 wm)
{
  float mIdf;
  mIdf = float (mId);
  rm.xz = 9. * vec2 (mod (mIdf, 4.), floor (mIdf / 4.));
  rm.y = GrndHt (rm.xz) + 6.;
  mIdf += todCur;
  vm = vec3 (Hashff (mIdf), Hashff (mIdf + 0.3), Hashff (mIdf + 0.6));
  qm = EulToQt (normalize (vm));
  vm = 2. * normalize (vm - 0.5);
  wm = vec3 (0.2 * Hashff (mIdf));
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  vec4 stDat, p, qm;
  vec3 rm, vm, wm, rMid;
  vec2 iFrag;
  float nStep;
  int mId, pxId, kp;
  bool doInit;
  iFrag = floor (fragCoord);
  pxId = int (iFrag.x + txRow * iFrag.y);
  if (iFrag.x >= txRow || pxId >= 4 * nObj + 2) discard;
  todCur = iDate.w;
  mId = (pxId < 4 * nObj) ? pxId / 4 : -1;
  cylRad = 2.5;
  cylLen = 2.;
  if (iFrame <= 5) {
    doInit = true;
  } else {
    doInit = false;
    stDat = Loadv4 (4 * nObj);
    nStep = stDat.w;
    ++ nStep;
    rLead = Loadv4 (4 * nObj + 1).xyz;
    rLead += vec3 (0., 0., 0.05);
    rLead.y = GrndHt (rLead.xz) + 0.1;
    if (mId >= 0) Step (mId, rm, vm, qm, wm);
  }
  if (doInit) {
    nStep = 0.;
    if (mId >= 0) Init (mId, rm, vm, qm, wm);
  }
  if (pxId == 4 * nObj) {
    rMid = vec3 (0.);
    for (int n = 0; n < nObj; n ++) rMid += Loadv4 (4 * n).xyz;
    rMid /= float (nObj);
  }
  if (pxId < 4 * nObj) {
    kp = 4 * mId;
    if      (pxId == kp + 0) stDat = vec4 (rm, 0.);
    else if (pxId == kp + 1) stDat = vec4 (vm, 0.);
    else if (pxId == kp + 2) stDat = qm;
    else if (pxId == kp + 3) stDat = vec4 (wm, 0.);
  } else {
    kp = 4 * nObj;
    if      (pxId == kp + 0) stDat = vec4 (rMid, nStep);
    else if (pxId == kp + 1) stDat = vec4 (rLead, 0.);
  }
  Savev4 (pxId, stDat, fragColor, fragCoord);
}

vec4 QtMul (vec4 q1, vec4 q2)
{
  return vec4 (
       q1.w * q2.x - q1.z * q2.y + q1.y * q2.z + q1.x * q2.w,
       q1.z * q2.x + q1.w * q2.y - q1.x * q2.z + q1.y * q2.w,
     - q1.y * q2.x + q1.x * q2.y + q1.w * q2.z + q1.z * q2.w,
     - q1.x * q2.x - q1.y * q2.y - q1.z * q2.z + q1.w * q2.w);
}

mat3 QtToRMat (vec4 q) 
{
  mat3 m;
  float a1, a2, s;
  q = normalize (q);
  s = q.w * q.w - 0.5;
  m[0][0] = q.x * q.x + s;  m[1][1] = q.y * q.y + s;  m[2][2] = q.z * q.z + s;
  a1 = q.x * q.y;  a2 = q.z * q.w;  m[0][1] = a1 + a2;  m[1][0] = a1 - a2;
  a1 = q.x * q.z;  a2 = q.y * q.w;  m[2][0] = a1 + a2;  m[0][2] = a1 - a2;
  a1 = q.y * q.z;  a2 = q.x * q.w;  m[1][2] = a1 + a2;  m[2][1] = a1 - a2;
  return 2. * m;
}

vec4 RMatToQt (mat3 m)
{
  vec4 q;
  const float tol = 1e-6;
  q.w = 0.5 * sqrt (max (1. + m[0][0] + m[1][1] + m[2][2], 0.));
  if (abs (q.w) > tol) q.xyz =
     vec3 (m[1][2] - m[2][1], m[2][0] - m[0][2], m[0][1] - m[1][0]) / (4. * q.w);
  else {
    q.x = sqrt (max (0.5 * (1. + m[0][0]), 0.));
    if (abs (q.x) > tol) q.yz = vec2 (m[0][1], m[0][2]) / q.x;
    else {
      q.y = sqrt (max (0.5 * (1. + m[1][1]), 0.));
      if (abs (q.y) > tol) q.z = m[1][2] / q.y;
      else q.z = 1.;
    }
  }
  return normalize (q);
}

vec4 EulToQt (vec3 e)
{
  float a1, a2, a3, c1, s1;
  a1 = 0.5 * e.y;  a2 = 0.5 * (e.x - e.z);  a3 = 0.5 * (e.x + e.z);
  s1 = sin (a1);  c1 = cos (a1);
  return normalize (vec4 (s1 * cos (a2), s1 * sin (a2), c1 * sin (a3),
     c1 * cos (a3)));
}

mat3 LpStepMat (vec3 a)
{
  mat3 m1, m2;
  vec3 t, c, s;
  float b1, b2;
  t = 0.25 * a * a;
  c = (1. - t) / (1. + t);
  s = a / (1. + t);
  m1[0][0] = c.y * c.z;  m2[0][0] = c.y * c.z;
  b1 = s.x * s.y * c.z;  b2 = c.x * s.z;
  m1[0][1] = b1 + b2;  m2[1][0] = b1 - b2;
  b1 = c.x * s.y * c.z;  b2 = s.x * s.z;
  m1[0][2] = - b1 + b2;  m2[2][0] = b1 + b2;
  b1 = c.y * s.z;
  m1[1][0] = - b1;  m2[0][1] = b1;  
  b1 = s.x * s.y * s.z;  b2 = c.x * c.z;
  m1[1][1] = - b1 + b2;  m2[1][1] = b1 + b2; 
  b1 = c.x * s.y * s.z;  b2 = s.x * c.z;
  m1[1][2] = b1 + b2;  m2[2][1] = b1 - b2;
  m1[2][0] = s.y;  m2[0][2] = - s.y;
  b1 = s.x * c.y;
  m1[2][1] = - b1;  m2[1][2] = b1;
  b1 = c.x * c.y;
  m1[2][2] = b1;  m2[2][2] = b1;
  return m1 * m2;
}

const float cHashM = 43758.54;

float Hashff (float p)
{
  return fract (sin (p) * cHashM);
}

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (vec2 (dot (p, cHashVA2), dot (p + vec2 (1., 0.), cHashVA2))) * cHashM);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);  
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}

