mat2
rotate2d(float a)
{
    return mat2(cos(a),-sin(a),
                sin(a), cos(a));
}

#define ss smoothstep

float
box(vec2 st, vec2 size)
{
    vec2 hs = size / 2.,
         aa = vec2(2./iResolution.y);
    vec2 bb = ss(-hs - aa, -hs, st) - ss(hs, hs + aa, st);
    return bb.x * bb.y;
}

float
crs(vec2 st)
{
    return clamp(
        box(st, vec2(1., .33)) + box(st, vec2(.33, 1.)),
        0., 1.);
}

#define P(x) step(1., mod(x, 2.))
#define sgn(x) (step(0., x) * 2. - 1.)

void
mainImage(out vec4 O, in vec2 U)
{
	vec2 st = U / (O.xy=iResolution.xy);
    vec4 bg = vec4(st.xy, .5, 1.);
    st.x *= O.x / O.y;
    st *= 5.;

    vec2 v = vec2(-1. + 2. * P(st.yx))
           * vec2(P(iTime), P(1. + iTime));
    float t = ss(.2, .8, fract(iTime));

    st += v * t;
    float a = (t + P(st.y) + P(st.x))
        * .785 // 2 * PI / 8
        * sgn(v.x)
        * sgn(v.y);
    st = rotate2d(a) * (fract(st) * 2. - 1.);

	O = mix(bg, vec4(1), crs(st));
}
