void mainImage( out vec4 z, vec2 b )
{
    z -= z;
    b /= 1e3; // b /= iResolution.y because > 280 chars otherwise :-( 


    vec2 A,B,C,D,E,G,H;
    A=B=C-=C;
    B.x = 1.732; // sqrt(3)
    C.y = 1.;

    // Upper triangle
    if( 1. - b.y < b.x/B.x )
        b = vec2(B.x,1.)-b;


    for(int i = 0 ; i < 8 ; i++)
    {
        D = (2. * A + B)/3.;

        G = b - D;
        H = B - D;

        // z.a = cos( angle )
        z.a = dot(G,H)/(length(G)*length(H));


        // COS(2PI/3) = -1/2
        // COS( PI/3) =  1/2

        // ACD Triangle
        if( z.a < -.5 )
            B = C; 
        	// A stays the same
        else 
        {
            A = (B + C)/2.;
        	// B stays the same

            // ECD Triangle
            if( z.a < .5 ) 
            {
                B = C;
                z = 1. - z;
            }
        	// else EBD Triangle
        }

        C = D;

    }


}

