//change for a quick test, changes the GREEN text:
const float demo=-987654.1234567;
//rounding errors are still pretty bad, and gets worse for larger abs(value)s
//such is the bane of 16 bit floating points.
//this is nt so much an error of this code.
//but due to low precision of float.
//does not do a cascading rounding-up of values like 9.9999 when only displaying 3 decmals of that.

//only shows [glyphs] decimals left of the decimal point.
//only shows min(4,[glyphs]) decimals right of the decimal point. (5th rounds poorly)
const float glyphs =11.;//max glyphs
//Sets loop iterations.
//is used in 2 loops, cycling that often,
//..once for whats left of the decimal point, once for whats right of the decimal point

//transparent border around glyphs that
//replaces thin border of glyph with transparency, does not add whitespace.
const float marginT=1.04;//if (marginG==1. && small text scale) neigbors can bleed trough.

//#define div0
//some compilers may just cry and may refuse to compile shit or just return a black screen.
#ifdef div0
//for openGl division by 0 case handling of type float.
const float NaN= 0./0.;
const float Inf= 1./0.;
const float NIn=-1./0.;
const float Po0= pow(0.,0.);
//pow(0.,0.) should be "ndef" or "NaN", but is "0." (or "1.") depending on hardware.
#endif

//bounding-box-calculation.Start:
//return if a vec2() is within a bounding box==within a characters space.
#define lt(a) lessThan(vec2(-1.),a)
bool whiteRectangle(vec2 u){
 bvec2 a=lessThan(vec2(-1.),-abs(u));//bvec2 a=greaterThan(vec2(.1), uv); 
 return (a.x&&a.y);}//inspired by https://www.shadertoy.com/view/ldScRm
//bool whiteRectangle2(vec2 i){bvec2 a=lt(-i),b=lt(i);//symmetry--:
// return (a.x&&a.y)&&(b.x&&b.y);}//inspired by https://www.shadertoy.com/view/ldScRm
bool isInside3(vec2 p){return !whiteRectangle(p*2.-1.);}
/*
//below is same, using type float, for no good reasoon other than being worse alternatives.
//return distance to square, center at [0:0], corners are at [+-.5:+-.5]
float isOutside(vec2 p){float o=.5;//diagonally offset the squares center away from [0:0].
 vec2 d=abs(p-o)-.5;return max(d.x,d.y);}
//making "out of bounds" check branchless:
float isInside2(vec2 p){
 return step(isOutside(p),0.);//fastest, less sub()s than ifb(isInside(p),1.,0.)
 //return ifb(isOutside(p),0.,1.);//faster
 //if (isOutside(p)>0.)return 0.;return 1.;//fast
 //if (p.x<0.||p.x>1.||p.y<0.||p.y>1.)return 0.;return 1.;//slower
}
*/
//bounding-box-calculation.End:

// --- access to the image of ascii code c
vec4 char(vec2 p, int C) {
  if(isInside3(p*marginT))return vec4(0,0,0,1e5);   //type bool  -> 50.5 fps
  //if(isInside2(p*marginT)==0.)return vec4(0,0,0,1e5); //type float -> 50.0 fps
  //strangely, for both above, totalAverageFps lowers over time, approaching their limits.
  //strangely, the limits heavily depend on what moment you start on,
  //... if you scale textsinusoidial over time, without approaching the same limits over time.

  return texture(iChannel0,p/16.+fract(vec2(C,15-C/16)/16.));
  //return textureLod( iChannel0, p/16. + fract( vec2(C, 15-C/16) / 16. ) , 
  //                   log2(length(fwidth(p/16.*iResolution.xy))) );
    //return textureGrad( iChannel0, p/16. + fract( vec2(C, 15-C/16) / 16. ) , 
    //                   dFdx(p/16.),dFdy(p/16.) );
    //textureGrad() results in the decimal, and everything after the decimal, being blurrier than the rest.
    //more apparent with post processing 
}
// possible variants: (but better separated in an upper function) 
//     - inout pos and include pos.x -= .5 + linefeed mechanism
//     - flag for bold and italic 
#define ch(n) char(p,n).x;p.x-=.5;


//p=fragment, n=float value
vec4 print_number(vec2 p,float n){
   return char(p,int(floor(mod(n*10.,10.)))+48);}
   //return char(p,int(floor(fract(n)*10.))+48);}
//vec4 print_number(vec2 p,float n){return char(p,int(mod(n*10.,10.))+48);}
//alternatively use mod():  int(floor(mod(n,10.)));

//#define logb(b,x) log(x)/log(b)
const float l10=(1./log(10.));//precalc for speed
#define log10(x) log(x)*l10
//#define log10(x) logb(10.,x)//would be slower, log() is slow.
//http://stackoverflow.com/questions/14081024/opengl-using-a-logarithmic-coordinates-axis

//return number of digits left of decimal point
float getWholeDigits(float f){if(abs(f)==0.)return 0.;return floor(log10(max(abs(f),0.0000001)))+1.;}
//see http://stackoverflow.com/questions/1068849/how-do-i-determine-the-number-of-digits-of-an-integer-in-c
//to remember that dumb|raw solutions can often outperform smart solutions.



//left==digits left to the decimal point, relevant for other text being left-endian.
//commonly left=getWholeDigits(abs(number));
//and you can add one leading zero by 
// left=getWholeDigits(abs(number))+1.;
//right==digits right of the decimal point (todo, fix pathethic rounding errors)
//w returns the total width of the float in glyphs.
vec4 floatShow(vec2 p,float f,float left,float right,out float w){vec4 o=vec4(0); 
 w=p.x;
#ifdef div0
 #define ro w-=p.x;return o;}
 if(f==NaN){     o+=ch( 78)o+=ch( 97)o+=ch( 78)ro//Nan //  0./0.
 else if(f==Inf){o+=ch( 73)o+=ch(110)o+=ch(102)ro//Inf // +1./0.;
 else if(f==NIn){o+=ch(126)o+=ch( 73)o+=ch(110)ro//~In // -1./0.
#endif
 if(f<.0){f=abs(f);o+=ch(45)}//negative number handling:
 //f=abs(f);//double safe, because x>0 for log(x);
 //must also catch all cases of log(0) !!!
 float n=left;
 //loop displays "left" digits left to the decimal point:
 for(float i=glyphs;i>-1.;i--){ 
  if(left<1.){o+=print_number(p,0.).x;p.x-=.5;break;}
  float ex=n+i-glyphs;
  ex=max(0.,ex);
  float n=pow(10.,ex);if(abs(n)>0.){
   o+=print_number(p,f/n).x;p.x-=.5;}
  left--;if(left<=0.)break;}        
 //decimal point shifting,making "." non-fixedwidth:
 f=fract(f);//=mod(f,1.);//=1.-floor(f);//adds rounding errors.
 p.x+=.1;
 o+=char(p,46).x;p.x-=2.5-.1; //44->"," :: 46->"."
 p.x+=2.;
 //digits right of the decimal point:
 float decimals=right;
 //f=mod(f,1.);//=1.-floor(f);//adds rounding errors.
 //less errors in onstants?
 for(float i=decimals;i>0.;i--){
  float n=pow(10.,i-decimals);
   if(abs(n)>0.){o+=print_number(p,f/n).x;p.x-=.5;
 }}w-=p.x;return o;}




//mainImage2() to have this as completely optional layer.
void mainImage2(out vec4 o,vec2 p){
 p/=iResolution.y;
 p *= 16.2+sin(iTime);//scale text smaller
 //disregarding above scaling:
 //p.x-=.5 moves the "cursor" 1 glyph to the right
 //p.x+=.5 moves the "cursor" 1 glyph to the left
 //p.y-=1. moves the "cursor" 1 glyph to the bottom 
 //p.y+=1. moves the "cursor" 1 glyph to the top 
 //as we "move the paper", and we do not not move the typewriter.

 //iTime, time in white
 p.x -=4.;//p.x-= shifts cursor sideways.
 float left = getWholeDigits(abs(iTime));
 //left+=2.;    //adding 2 leadig zeroes
 //left=8.-left;//fixedwidth (character count), replacing spacing with leadig zeroes
 float w=0.;//will get width (as glyphs) of a float stored in it.
 o+=floatShow(p,iTime,left,4.,w).x;
 p.x-=w;
 o+=ch(115)//s

 //average total fps in red:
 p.x-=.5;
 float fpsTotal=float(iFrame);//totalAverage fps=totalFrames/totalSeconds
 if(iTime>0.)fpsTotal/=iTime;
 //if(pause time and rewind)
 //...shadertoy has an issue with the values for the very first frame?
 //...cant even tll if i is ==0./0. or ==1./0.;
 //if (iTime<.1)fpsTotal==1.;//overwriting doesnt even fix that
 left = getWholeDigits(abs(fpsTotal));
 o.xw+=floatShow(p,fpsTotal,left,2.,w).x; 
 p.x -=w;
 o.xw+=ch(102)o.xw+=ch(112)o.xw+=ch(115)//fps

 //single frame fps in blue
 p.x -=.5;

 float fpsFrame=iTimeDelta;//single frame fps, highly varies over time
 if(abs(fpsFrame)>0.){
  fpsFrame=1./fpsFrame;
  left=getWholeDigits(abs(fpsFrame));
  o.yzw+=floatShow(p,fpsFrame,left,2.,w).x; 
 }else {fpsFrame=0.000001;left=2.;}
  //above error case is for when a frame claim that 
  //iTimeDelta is not set, as if a frame was calculated instantly or tripple buffered or queued?
 p.x -=w;   
 o.yzw+=ch(102)o.yzw+=ch(112)o.yzw+=ch(115)//fps

 p+=vec2(10.4,-6.);
 p.y+=1.;left=getWholeDigits(abs(demo));o.yw+=floatShow(p,demo,left,5.,w).x; 

 #ifdef div0
 //testing bounds of floats for divisions by 0, will appear dark blue
 //usurprisingly divisions by 0 result in some shit.
 //especially with my cheap hanling for these cases.
 //"NaN","infinity" and "negInfinity" are not caught b my ==0. filter before applying log()
 //and log() does strange shit with these values:
  #define divn(a) p.y+=1.;left=getWholeDigits(abs(a));o.zw+=floatShow(p,a,left,2.,w).x; 
  divn(NaN) divn(Inf) divn(NIn) divn(Po0)
  p.x-=w+.2;
  o.zw+=ch(112)o.zw+=ch(111)o.zw+=ch(119)//pow
  o.zw+=ch(40)//(
  o.zw+=ch(48)o.zw+=ch(44)o.zw+=ch(48)//0,0
  o.zw+=ch(41)//)
 #endif  

  p.y*=2.5;p.x=length(p);if(p.x<.3)o+=.5;//add circle that shoes "cursor" position.

  //post processing 
  //to test gradients and borders. not to look pretty
  o*=.5*(smoothstep(0.,1.,o.w));
  vec4 v=vec4(sin(iTime*acos(-1.)*2.-asin(1.))*.5+.6);
  v=max(v,vec4(0));
  o=pow(o,v);
  o=max(o,vec4(0));
  //above sin(t) makes bloom most intense while remainer of iTime is close to 0.
  //... ==1 hz sinusoid, wavelength = 1 second.
  o=sqrt(o);//cheapest cubic bloom    
}


///--end of mainimage2 content, start of its mainimage() container:
void mainImage(out vec4 c,vec2 i){
 mainImage2(c,i);//number overlay
 c+=vec4(sin(199.*length(i/iResolution.xy)))*.2;   //ring waves.
 c+=.3*texture(iChannel0,i/iResolution.xy);//display glyph distance field
}
