#define camLens 6.
#define frame(u) camLens*(u-.5*iResolution.xy)/iResolution.y 

//case a; only inside  of unit circle gets inverted on unit circle.
//case a; only outside of unit circle gets inverted on unit circle.
//2 cases get mixed by mix(a,b,mixRatio);
#define mixRatio sin(iTime)*.5+.5
//#define mixRatio 0.
//#define mixRatio 1.
//#define mixRatio -1.
//#define mixRatio 2.

//return polar of carthesian [.x]distance [.y]angle
vec2 c2p(vec2 u){return vec2(length(u),atan(u.y,u.x));}
//return carthesian of polar [.x]distance [.y]angle
vec2 p2c(vec2 u){return vec2(u.x*cos(u.y),u.x*sin(u.y));}

void mainImage( out vec4 O, in vec2 U ){
	vec2 u = frame(U);
    u.x+=sin(iTime);
    u.y+=cos(iTime);
    u=c2p(u);
    float f,g;
    vec2 a=u;if(a.x<1.)a.x=1./a.x;//if(inside unit circle)  do circle inversion
    vec2 b=u;if(b.x>1.)b.x=1./b.x;//if(outside unit circle) do circle inversion
    u=mix(a,b,mixRatio);
    u=p2c(u);

    u.x-=sin(iTime);
    u.y-=cos(iTime);
    u=fract(u*camLens);
    u.x*=.5;
    vec3 c=u.x*vec3(2,2,0)+u.y*vec3(1,0,1);
    //BlackredBluePurple->BlackYellowCyanWhite == more contrast on lcd or oled.
    O = vec4(c,1);
}
