//Display
vec3 getNormal(in vec2 fragCoord) {
    vec3 val = vec3(0.0);
    for (int x = -3; x<=3; x++)
        for (int y = -3; y<=3; y++)
            val += texelFetch(iChannel1, ivec2(fragCoord)+ivec2(x, y), 0).xyz;
    return normalize(val);
}

//From http://lolengine.net/blog/2013/07/27/rgb-to-hsv-in-glsl
vec3 hsv2rgb(in vec3 c) {
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

//From https://www.shadertoy.com/view/4djSRW
vec4 hash42(vec2 p) {
    vec4 p4 = fract(vec4(p.xyxy) * vec4(.1031, .1030, .0973, .1099));
    p4 += dot(p4, p4.wzxy+19.19);
    return fract((p4.xxyz+p4.yzzw)*p4.zywx);
}

bool box(in vec2 p, in vec2 center, in vec2 halfSize, in vec2 skew) {
    p -= center;
    p.x += tan(skew.x)*p.y;
    p.y += tan(skew.y)*p.x;
    return clamp(p, -halfSize, halfSize) == p;
}

vec4 speck(in vec2 fragCoord, in vec3 baseColor) {
    fragCoord *= 2.5;
    vec2 bucket = floor(fragCoord/20.0) * 20.0;
    vec4 bh = hash42(bucket);
    vec4 bh2 = hash42(bucket*bucket);
    fragCoord -= bh.yz*14.0 - 7.0;
    float skew = bh.x*1.2 - 0.6;
    float size = pow(bh.w, 2.0) * 5.0;
    bool res = box(fragCoord, bucket+vec2(10), vec2(size), vec2(-skew, skew));
    if (res)
        return vec4(baseColor*1.2*(bh2.x*0.8+0.6), 1.0);
    else
        return vec4(0.0);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec3 normal = getNormal(fragCoord);
    vec3 color = vec3(0.4, 1.0, 0.1); //green
    //color = vec3(1.0, 0.1, 1.0); //purple or something
    //color = vec3(0.1, 0.4, 1.0); //blue
    //color = vec3(1.0, 0.0, 0.0); //kinda-orangey red
    //color = vec3(1.0, 0.5, 0.0); //yellowy orange
    //color = hsv2rgb(vec3(0.93, 1.0, 1.0)); //pink or something
    color = hsv2rgb(vec3(fragCoord.x/iResolution.x, 1.0, 1.0)); //all the colors
    float depth = texture(iChannel0, fragCoord/iResolution.xy).y;
    vec3 background = texture(iChannel2, fragCoord/iResolution.xy).rgb;
    vec3 lightDir = normalize(vec3(1.0, 0.0, -1.0));
    float light = dot(normal, -lightDir) * 0.25 + 0.75;

    vec3 halfwayDir = normalize(-lightDir + vec3(0.0, 1.0, 0.0));
    float spec = pow(max(dot(normal, halfwayDir), 0.0), 16.0) * 0.5;

    vec4 speckRes = speck(fragCoord, color);

    fragColor.rgb = background;
    fragColor.rgb = mix(fragColor.rgb, color*light+spec, depth);
    fragColor.rgb = mix(fragColor.rgb, speckRes.rgb, speckRes.a*pow(depth, 16.0));
    fragColor.a = 1.0;
}

