// http://creativecommons.org/publicdomain/zero/1.0/

//Mouse information
void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec4 prev = texelFetch(iChannel0, ivec2(0, 0), 0);

    if (iFrame == 0)
        prev = iMouse;

    if (ivec2(fragCoord) == ivec2(0, 0))
        fragColor = iMouse;
    else if (ivec2(fragCoord) == ivec2(1, 0))
        fragColor = prev;
}

// Copyright (C) 2018 pendingchaos
// this code is licensed under the CC0 license 
// http://creativecommons.org/publicdomain/zero/1.0/

//Drawing and "Simulation"
#define RADIUS 1.0
#define DOWNSCALE 8.0

#define GAIN 0.025

//From https://iquilezles.org/articles/distfunctions
float sdCapsule(in vec3 p, in vec3 a, in vec3 b, in float r) {
    vec3 pa = p - a, ba = b - a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h ) - r;
}

//From https://www.shadertoy.com/view/4djSRW
float hash12(vec2 p) {
    vec3 p3  = fract(vec3(p.xyx) * .1031);
    p3 += dot(p3, p3.yzx + 19.19);
    return fract((p3.x + p3.y) * p3.z);
}

float getMax(in vec2 fragCoord) {
    float v = 1.0;
    float left = hash12(vec2(floor(fragCoord.x/v), fragCoord.y));
    float right = hash12(vec2(ceil(fragCoord.x/v), fragCoord.y));
    float f = fract(fragCoord.x/v);
    return max(0.05 + mix(left, right, f)*0.6, 0.1);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    fragColor = texelFetch(iChannel0, ivec2(fragCoord), 0);

    //Drawing
    vec4 prevMouse = texelFetch(iChannel1, ivec2(1, 0), 0) / vec4(DOWNSCALE, DOWNSCALE, 1.0, 1.0);
    vec4 curMouse = iMouse / vec4(DOWNSCALE, DOWNSCALE, 1.0, 1.0);

    bool wavyLine = texelFetch(iChannel2, ivec2(83, 1), 0).x>0.5;
    if (wavyLine) {
        vec2 res = iResolution.xy / DOWNSCALE;
        vec3 p = vec3(fragCoord, 0.0);
        p.y += sin(fragCoord.x/res.x*8.0) * res.y * 0.1;
        vec3 start = vec3(res*vec2(0.1, 0.5), 0.0);
        vec3 end = vec3(res*vec2(0.9, 0.5), 0.0);
        if (sdCapsule(p, start, end, min(res.x, res.y)*0.015) < 0.0)
            fragColor.xy = vec2(1.0);
    }

    bool fill = texelFetch(iChannel2, ivec2(70, 1), 0).x>0.5;
    if (fill)
        fragColor.xy = vec2(1.0);

    bool empty = texelFetch(iChannel2, ivec2(69, 1), 0).x>0.5;
    if (empty || iFrame==0)
        fragColor.xy = vec2(0.0);

    if (prevMouse.z>0.5 && curMouse.z>0.5 && prevMouse.xy!=curMouse.xy) {
        vec2 start = prevMouse.xy;
        vec2 end = curMouse.xy;
        if (sdCapsule(vec3(fragCoord, 0.0), vec3(start, 0.0), vec3(end, 0.0), RADIUS) <= 0.0) {
            bool clear = texelFetch(iChannel2, ivec2(67, 0), 0).x>0.5;
            fragColor.x = clear ? 0.0 : 1.0;
            if (!clear) fragColor.y = 1.0;
        }
    }

    //Simulation
    if (fragColor.x > getMax(fragCoord) && fragCoord.y>0.0)
       fragColor.x = clamp(fragColor.x*(1.0-GAIN), 0.1, 1.0);

    float up = texelFetch(iChannel0, ivec2(fragCoord)+ivec2(0,1), 0).x;
    if (up > getMax(fragCoord+vec2(0,1)))
       fragColor.x = clamp(fragColor.x+up*GAIN, 0.1, 1.0);

    if (fragColor.x >= 0.1)
        fragColor.y = min(fragColor.y+iTimeDelta*0.25, 1.0);
}

// Copyright (C) 2018 pendingchaos
// this code is licensed under the CC0 license 
// http://creativecommons.org/publicdomain/zero/1.0/

//Upscale and Noise
#define DOWNSCALE 8.0

vec2 doSubSample(in vec2 coord) {
    return texture(iChannel0, coord / iResolution.xy / DOWNSCALE).xy;
}

float doSample(in vec2 coord) {
    vec2 val = vec2(0.0);
    int radius = 4;
    for (int x = -radius; x<=radius; x++) {
        for (int y = -radius; y<=radius; y++) {
            vec2 s = doSubSample(coord+vec2(x, y));
            s.x = step(s.x, 0.1);
            val += s;
        }
    }
    val *= 1.0 / float(radius*radius*4 + radius*4 + 1);
    vec2 detailCoord = coord / iResolution.xy / vec2(textureSize(iChannel2, 0)) * 100.0;
    float s = texture(iChannel2, detailCoord).x;
    float res = smoothstep(max(s-0.15, 0.0), min(s+0.15, 1.0), val.x);
    val.x = smoothstep(0.35, 0.65, val.x);
    //detail is faded in to make movement look better
    res = mix(val.x, res, step(0.001, val.y));
    return res;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    float depth = 1.0 - doSample(fragCoord);
    float noise = texture(iChannel1, fragCoord/iResolution.xy*0.5).x;
    fragColor.y = depth;
    depth = depth*0.75 + noise*depth;
    depth = depth / (depth+0.2);
    fragColor.x = depth;
}

// Copyright (C) 2018 pendingchaos
// this code is licensed under the CC0 license 
// http://creativecommons.org/publicdomain/zero/1.0/

//Normal calculation
void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    float h = 1.0 - texelFetch(iChannel0, ivec2(fragCoord), 0).x;
    vec3 pos = vec3(fragCoord.x, h*50.0, fragCoord.y);
    vec3 normal = normalize(cross(dFdx(pos), dFdy(pos)));
    normal.y *= -1.0;
    fragColor = vec4(normal, 0.0);
}
