// variant of https://shadertoy.com/view/MsGBRV

#define S(v) smoothstep(0., 3./PIX, v) 

void mainImage( out vec4 O, vec2 u ) {
    u /= PIX;
    vec2 R = iResolution.xy/PIX,
         M = T(vec2(0)).zw;                            // draw cursor
#if FOLLOW
    u += M - R/2.;                                     // camera follow cursor
#endif

    float d; vec2 U = Hcoords(u,d);                    // hexa cell center and distance
    O = T( U );                                        // cell content
    O =   O.y > 0. ? vec4(O.y/2.,0,0,0) 
        : O.y < 0. ? vec4(0,0,1,0)
        :            vec4(1); 
    O = mix( vec4(.5), O, S(d) );                      // hexa cells border

    O = mix( vec4(1, 0, 0,0), O, S(length(u-M)-.8));
    O = mix( vec4(1,.5,.5,0), O, S(length(u-M)-.6));
}

// --- paint and propagate timer, same in medium

void mainImage( out vec4 O, vec2 U )
{
    U -= .5;
    float d; 
    bool s = int(U.y) % 2 > 0;
    vec2 R0 = iResolution.xy,  
         R = R0/PIX,        
         M = iMouse.xy/PIX,
         P = U -.5*vec2(s,0); 
   // M = Hcoords(M,d);                         // hexa cell under Mouse

    if (iFrame==0) {                          // init 
        O -= O;
        vec2 A = R0/2.-abs(U-R0/2.); O.x = -min(A.x,A.y/SQR3S2); // init bg pace.
        if (  length( P - R/2.+.5) < 1.5 ) O.y = 1.;      // init drawing
        if (U==vec2(0)) O.zw = Scoords(R/2.);             // cursor
        if (U==vec2(1,0)) O.zw = vec2(1,0);               // direction
        return;
    }

    O = T(U);


    if (U.x==0. || U.y==0. || U.x==R0.x-1. || U.y==R0.y-1. ) { // borders pace
        O.x = float(iFrame); O.y = -1.;
        if ( U==vec2(1,0) && iMouse.z>0.) 
#if FOLLOW
            O.zw = normalize(M-R/2.);          // cursor direction
#else
            O.zw = normalize(M-T(vec2(0)).zw); // cursor direction
#endif        
        if (U==vec2(0)) O.zw += V * T(vec2(1,0)).zw ;     // moves cursor
    }
    else {
        if ( U == round(Hcoords(T(vec2(0)).zw,d)) )      // draw
            O.y = 2.;

    // --- detection of enclosed regions ( not painted + not pacing )
   // else if ( O.y == 0. ){                    // propagate pacing in free areas
        O.z = O.x;
        for (int k =0; k<9; k++) 
          if ( s && k!=2 && k != 8 || !s && k != 0 && k != 6 ) {
       // ( maybe compiles better if separating 2 loops s, !s ? )
            vec4 T = T(U+vec2(k%3-1,k/3-1));  //  propagate max neighbor time
              if ( T.y <= 0. ) O.x =  max(O.x,T.x);
          }
        O.w = O.x==O.z ? O.w+1. : 0.;         // count duration of unchanged
        if ( O.w > STILL ) O.x = O.y = 1.;    // longly unchanged: closed -> eaten
    }
}

#define PIX 16.   // pixel size (i.e. zoom )

#define STILL 40. // number of frames to consider area is still (thus closed)
#define V .1
#define FOLLOW 1

#define SQR3S2 0.866

#define T(U) texelFetch( iChannel0, ivec2(U), 0 )

#define keyToggle(c) (texture(iChannel3,vec2((.5+float(c))/256.,0.75)).x > 0.)
#define keyClick(c)   texture(iChannel3,vec2((.5+float(c))/256.,0.25)).x

// --- return hexa cell center + dist to center

vec2 Hcoords( vec2 U, out float d ) {
    U.y /= SQR3S2;
    float s = sign(mod(U.y,2.)-1.); 
    U.x -= .5*fract(U.y*s); 

    vec2 F = fract(U);                       // triangular barycentric coordinates
    if (s<0.) F.x = 1.-F.x;
    bool u = F.x+F.y > 1.;       
    vec3 q = vec3(F, fract(1.-F.x-F.y));
    if (u) q = 1.-q;
    vec3   r = q - q.zxy;

	vec2 S;                                  // hexagonal center, d = dist to border
    if      ( r.x>0. && r.y<0. ) S = vec2(!u,u), d = min(r.x,-r.y);
    else if ( r.y>0. && r.z<0. ) S = vec2(u,!u), d = min(r.y,-r.z);
    else                         S = vec2(u)   , d = min(r.z,-r.x);
    if (s<0.) S.x = 1.-S.x;    

    return floor(U)+S;
}

// return screen coord from hexa coord

vec2 Scoords( vec2 U ) {
    float s = sign(mod(U.y,2.)-1.); 
    U.x += .5*fract(U.y*s); 
    U.y *= SQR3S2;
    return U;
}
