vec2 uvToTex(vec2 uv){
	uv.y-=0.05;
	uv *=iResolution.yy;
    uv +=iResolution.xy*.5;
    uv /=iResolution.xy;
    return uv;
}


vec3 sampleColor(vec2 uv){
    return texture(iChannel2, uvToTex(uv)).xyz;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uvo = fragCoord.xy / iResolution.xy;
	vec2 uv = (fragCoord.xy - iResolution.xy*.5) / iResolution.yy;
    if (abs(uv.y)>.27) {
        fragColor = vec4(0,0,0,1);
        return;
    }
    uv.y+=0.05;

    vec4 fb = texture(iChannel2, uvToTex(uv));
    float td = fb.w;


    vec3 acc = vec3(0);
    float motionBlurAmp =0.04;
    vec4 noise = texture(iChannel1, (fragCoord+floor(iTime*vec2(12.0,56.0)))/64.0);

    acc += sampleColor(uv*((noise.x+2.0)/td*motionBlurAmp+1.0))*vec3(.4,.1,.1);
    acc += sampleColor(uv*((noise.y+1.0)/td*motionBlurAmp+1.0))*vec3(.3,.3,.1);
    acc += sampleColor(uv*((noise.z+0.0)/td*motionBlurAmp+1.0))*vec3(.2,.4,.2);
    acc += sampleColor(uv*((noise.w-1.0)/td*motionBlurAmp+1.0))*vec3(.1,.3,.3);
    acc += sampleColor(uv*((noise.x-2.0)/td*motionBlurAmp+1.0))*vec3(.1,.1,.4);


    vec3 c = acc.xyz;
    c*=3.0;
    c-=0.04;
    c = max(c,.0);
    c=(vec3(1.3,1.3,1.3)*c)/(vec3(1)+c);
    c=sqrt(max(c,.0));
    c+=noise.xyz*0.01;

    fragColor = vec4(c, 1.0);

}

float df ( in vec3 p){
    float terra = p.y+1.0;
    if (terra<2.0){
        terra+=-texture(iChannel0, p.xz*0.04).r;
        terra+=texture(iChannel0, p.xz*0.03+vec2(.4,.3)).r;
        if (terra<0.1){
        	terra+=-pow(texture(iChannel0, p.xz*0.05+vec2(.4,.3)).r,4.0)*texture(iChannel0, p.xz*2.0).r*0.1;
        }
    }

    return terra;
}

vec3 nf(in vec3 p, in float theta){
    vec2 e = vec2(theta, 0.0);
    float c = df(p);
    return normalize(vec3(c-df(p+e.xyy),c-df(p+e.yxy),c-df(p+e.yyx)));
}
#define LDIR normalize(vec3(9,-2,-6))
#define ATMOS_COLOR vec3(.4,.5,.6)
#define SUN_COLOR vec3(.9,.7,.4)

vec3 skyColor(in vec3 d){
    vec3 ldir = LDIR;
    vec3 atmosColor = ATMOS_COLOR;
    atmosColor = mix(atmosColor, vec3(.9,.5,.2), pow(dot(d,-ldir)*.5+.5,4.0));
    atmosColor = mix(atmosColor, vec3(.9,.1,.1), pow(dot(d,-ldir)*.5+.5,16.0));
    //vec3 sun = SUN_COLOR*pow(dot(d,-ldir)*.5+.501,6960.0)*.03;
	return (pow((1.0-abs(d.y)),2.0)*atmosColor);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 noise = texture(iChannel1, (fragCoord+floor(iTime*vec2(12.0,56.0)))/64.0);
	vec2 uv = ((fragCoord.xy+noise.xy*0.5) - iResolution.xy*.5) / iResolution.yy;
    if (abs(uv.y)>.27){
        fragColor = vec4(0,0,0,1);
        return;
    }
    uv.y+=0.05;

    vec3 pos = vec3(0,0,-4.0+iTime);
    vec3 dir = normalize(vec3(uv, 1));

    float d,td=.0;
	int it;
    for(int i=0; i<150; i++){
    	d = df(pos);
        pos += d*dir;
        td += d;
        it = i;
        if (d<0.001||d>100.0) break;
    }
    float sky = min(max(d*.5,.0),1.0);
    vec3 ldir = LDIR;
    vec3 atmosColor = ATMOS_COLOR;
    atmosColor = mix(atmosColor, vec3(.9,.5,.2), pow(dot(dir,-ldir)*.5+.5,4.0));
    atmosColor = mix(atmosColor, vec3(.9,.1,.1), pow(dot(dir,-ldir)*.5+.5,16.0));
    vec3 sunColor = SUN_COLOR;

    float wf = (-df(pos-vec3(-1,0,0))+df(pos))*0.5;
    wf=min(max(wf,0.0),1.0);
    float w = (-df(pos-vec3(0,0,-1))+df(pos))*0.5;
    w=min(max(w,0.0),1.0);
    vec3 normal = normalize(nf(pos, 0.1)+nf(pos, 0.025));
    float wfa = (df(pos)-df(pos+normal))*.5;
    wfa=min(max(wfa,0.0),1.0);
    float wfa2 = pow(wfa*1.5,8.0);
    wfa2=min(max(wfa2,0.0),1.0);

    vec3 c;
    float fog = min(max((pow(-1.0+pos.y,2.0)),.0)*0.004*td+td*0.005+0.001,1.0);
    c=vec3(fog);
    vec3 albedo = mix(mix(vec3(.6,.5,.3), vec3(.4,.6,.2), wf), vec3(.3,.3,.3),w);
    albedo = mix(albedo, vec3(.9,.9,.4), wfa2);




    float sunDiffuse = (max(dot(ldir,normal),.0));
    sunDiffuse = min(sunDiffuse, df(pos-ldir*.5)*2.0);
    sunDiffuse = min(sunDiffuse, df(pos-ldir));
    sunDiffuse = max(.0,sunDiffuse);
    float atmosDiffuse = (dot(vec3(0,-1,0),normal)*.5+.5);
    atmosDiffuse *= wfa*0.125;

    c = sunDiffuse*albedo*sunColor + atmosDiffuse*albedo*atmosColor*wfa;
    c = mix(c,atmosColor,fog);
    c = mix(c, skyColor(dir),sky);

	fragColor = vec4(c,td);
}
