const float M_PI = 3.14159265359;

// SOME METHODS FOR COMPLEX NUMBERS
vec2 cmpxmul(in vec2 a, in vec2 b) {
	return vec2(a.x * b.x - a.y * b.y, a.y * b.x + a.x * b.y);
}

vec2 cmpxpow(in vec2 c, int p) {
    vec2 orig = c;
	for (int i = 0; i < p-1; ++i) {
		c = cmpxmul(c, orig);
	}
    return c;
}

float cmpxmag(in vec2 c) {
    return sqrt(c.x * c.x + c.y * c.y);
}

// Convert HSV to RGB for shader output
vec3 hsv2rgb(vec3 c)
{
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Position normalised into [-0.5, 0.5]
    vec2 p = (1.0 * fragCoord.xy/iResolution.xy) - 0.5;

    // f(x) = 2x^3 - 0.1x + 0.04
    p = 2.0 * cmpxpow(p, 3) - 0.1 * p + vec2(0.04, 0);

    // (x, y) to (r, theta)
    float r = cmpxmag(p);
    float theta = atan(p.y, p.x);

    // From radians (0 - 2_PI) to hue (0 - 1)
    float hue = theta / (2.0 * M_PI);

    // Increase brightness 
    fragColor = vec4(hsv2rgb( vec3(hue, 1.0, r+0.75)), 1.0);
}
