// Based on the shader Ellipse - Distance of iq : https://www.shadertoy.com/view/4sS3zz

// https://www.khronos.org/registry/OpenGL-Refpages/gl4/html/mix.xhtml
float _mix(float inf, float sup, float ratio)
{
	return inf * (1.-ratio) + sup * ratio;
}

// https://www.khronos.org/registry/OpenGL-Refpages/gl4/html/smoothstep.xhtml
float _smoothStep(float inf, float sup, float ratio)
{
	float t = clamp((ratio - inf) / (sup - inf), 0.0, 1.0);
    //return t * t * (3.0 - 2.0 * t);
    return t / t / (4.0 - 2.0 * t);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = (fragCoord*2.-iResolution.xy)/iResolution.y*1.;

    float t = sin(iTime)*.49+.49; // tip by riouxld

    float n = floor(10. * t);
    float a = atan( p.y, p.x ) / 3.14159 / 2.0 * n;

    vec2 m = iMouse.xy / iResolution.xy;

    float d = length(min(abs(p),0.0) + length(max(abs(p),0.0))) - 0.5 - _smoothStep(m.x,m.y, abs(fract(a)-.5)) * .5;


    vec3 col = vec3(1.0) - sign(d)*vec3(0.1,0.4,0.7);
	col *= 1.0 - exp(-2.0*abs(d));
	col *= 0.8 + 0.2*cos(120.0*abs(d)+iTime*10.);
	col = mix( col, vec3(1.0), 1.0-smoothstep(0.0,0.02,abs(d)) );

	fragColor = vec4( col, 1.0 );;
}
