void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iChannelResolution[0].xy;
	fragColor = smoothstep(.0,.5,uv.y)*texture(iChannel0, uv);
}

/////////////////////////////////////////
///
/// Pharaohs by Timo Kinnunen
///
/// Based on Infinite sierpinski by kzy
/// @ https://www.shadertoy.com/view/4lyXDc 
///
/// Improves the stability and symmetry of the original algorithm.
/// Still, devolves into (symmetric) randomness depending on resolution.
/// Ah well..
///
/////////////////////////////////////////

vec4 get(ivec4 pix, ivec2 offset)
{
	vec2 uv = (vec2(pix.xy + offset) + 0.5) / iChannelResolution[0].xy;
    vec4 c = texture(iChannel0, uv);
    if (pix.x< pix.z/2 - (pix.z-3)/2 || pix.x > pix.z/2 + (pix.z-3)/2 || c.a <= 0.5)
        return vec4(c.rgb,0);
	return vec4(c.rgb,1);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    ivec4 pix = ivec4(fragCoord,iResolution.xy);

    vec4 c = vec4(0);

    vec4 pc = get(pix, ivec2(0, 0));
    vec4 pl = get(pix, ivec2(-1, 0));
    vec4 pr = get(pix, ivec2(1, 0));
    if(pix.y<=1) {
        float v = (iTime < 1.1 || pc == vec4(0)) ? float(pix.z/2 != pix.x) : pl.a+pc.a+pr.a;
        c = vec4(pc.rgb,v == 0. || v == 3.);
    } else if (pix.y == pix.w-1) {
        // This is a bit of a hack to maybe retain some buffer contents when switching to fullscreen
        float v = pl.a+pc.a+pr.a;
        c = vec4(pc.rgb,v == 0. || v == 3.);
        fragColor = get(pix, ivec2(0,-pix.y));
        // Now I have to hide the bottom line in main image, a tasteful smoothstep will do fine...
        return;
    } else {
        c = (get(pix, ivec2(0, 1))+vec4(pc.rgb,0))*vec4(0.95,0.97,1,1);
    }
    c.rgb *= vec3(.32,.25,.025);
    c.rgb += pl.rgb*vec3(.5,.5,-.2)+pl.a*.5-.125;
    c.rgb += pr.rgb*vec3(-.0625,-.1925,1.75)+pr.a*.25-.125;
    c.rgb += (.125+.125*c.a)*vec3(pl.a,pc.a,pr.a*.03125-.0625*pl.a);
    c.rgb += vec3(.02,.01,.01+.0*1.1251-.0*(pl.a+pr.a+pc.a));

    fragColor = clamp(c,-10.0,1.);
}
