const int iterations = 11;
const float view = 50.;
#define CIRCLE

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy - .5;
    uv *= view;

    for (int i = 0; i < iterations; i++) {
        float ang = atan(uv.y+cos(uv.x+iTime),
                         uv.x+sin(uv.y+iTime));
        mat2 m = mat2(sin(ang),cos(ang),-cos(ang),sin(ang));
        uv = uv*.25+uv*.5*m;
    }

    #ifdef CIRCLE
            float d = length(mod(uv,1.)-.5)-.4;

    #else
        float d = length(max(abs(mod(uv,1.)-.5)-vec2(.4,.4), 0.));

    #endif

    d *= 20.;
    fragColor = vec4( sin(d+.723), cos(d+.2346), -cos(d+1.63), 1. )*.5+.5;
}
