#define SPEED 1.2
#define INTENSITY 1.0

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    //uv.x *= iResolution.x/iResolution.y;
    uv = (uv - 0.5) * 2.0;

   	float t = iTime * SPEED;

    vec2 fishuv;
    float fishyness = (0.1 + 0.1 * cos(t)) * INTENSITY;
    fishuv.x = (1.0 - uv.y*uv.y) * fishyness * uv.x;
    fishuv.y = (1.0 - uv.x*uv.x) * fishyness * uv.y;

    // Fisheye Chromatic Aberration !
    float cr = texture(iChannel0, uv - fishuv*0.95).x;
    vec2 cgb = texture(iChannel0, uv - fishuv).yz;
    vec3 c = vec3(cr, cgb);

    // Vignetting
    float uvMagSqrd = dot(uv,uv);
    float vignette = 1.0 - uvMagSqrd * fishyness;
    c *= vignette;

	fragColor = vec4(c, 1.0);
}
