#define rnd(v) (2.*fract(4521.7*sin(dot(v,vec2(131,1743.1))))-1.)

void mainImage( out vec4 O,  vec2 U )
{
	U = U/iResolution.y - vec2(0,.3*iTime);

    vec2 V = (2.*fract(U*2.)-1.)*1.2, // pos in block
         T=floor(U*2.),               // block index
         D = abs(V),N;
    float a = max(D.x,D.y), l,
          b = fract(100.*length(3e2+T)), m = b<.2 ? 0. : 1.; // type of block

    if (a > 1.) {                     // --- street
        O = vec4(0,0,.6,1); 
        if (a>1.19 && min(D.x,D.y)<.96 ) O += step(cos(63.*min(D.x,D.y)),0.);
        return; 
    }

    O =  a+1.-m<.86 ? vec4(0,.7,0,1) : a<.98 ? vec4(.5) : O-O; // block color / border

    V *= 3.5;                         // --- houses
    N = floor(V+.5);                  // house index 
    D = abs(N); a = max(D.x,D.y); 
    if (a == 2.) {                    // crown in block
        D = abs(2.*fract(3.5+V+m*.2*rnd(N+T))-1.)*(1.1+.6*m); // house pos
        a = max(D.x,D.y);
        O =  a<.95 ? m<1.?vec4(.8):vec4(1,0,0,1) : a<1. ? O-O : O;    
    }
    if (b+1.-m>.7 && dot(V,V)<2.) {   // --- block pond or trees
        a=0.; for(int i=0; i<4; i++) 
            l = length(V-.2*vec2(rnd(N+T),rnd(N+T+.3))), 
            a += b<.9 ? l : 1./l;
        if (a<4.) O = m<1.||b<.8 ?vec4(0,.4,0,1):vec4(0,0,1,1);
    }
}
