#define pi 3.141592654
#define tao 2.0*pi

#define bgColor vec4(0,0.2,0.4,1.0)
#define waterColor vec4(1.0,1.0,1.0,1.0)

#define lineNumber 5.0

float lerp(float a,float b,float v)
{
	//clamp to 0 and 1
	if(v>1.0) v=1.0;
	else if(v<0.0) v=0.0;
	return a*(1.0-v)+b*v;
}

vec4 lerp(vec4 a,vec4 b,float v)
{
	float red = lerp(a.r,b.r,v);
	float green = lerp(a.g,b.g,v);
	float blue = lerp(a.b,b.b,v);
	float alpha = lerp(a.a,b.a,v);
	return vec4(red,green,blue,alpha);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv.x*=iResolution.x/iResolution.y; 
    uv.x -= 0.35;

    float dx = sin(uv.y*tao*lineNumber+iTime*10.0)*0.6*pow(uv.y,.1);//+iTime*10.0)

    float value = sin(uv.x*tao*lineNumber*5.0+dx);

    fragColor = lerp(bgColor,waterColor,value);
}

