//sily generalization og https://www.shadertoy.com/view/ltfcWn
// -  variant from https://www.shadertoy.com/view/MlXcWn


void mainImage( out vec4 O, vec2 U )
{
    float N = 1.+20.*iMouse.x/iResolution.x,              // number of axis
          S = 10.,              // scale
          a = 3.14/N, 
          h = iResolution.y;
    U /= h; U-=.5;U *= S;
    O -= O;

    for (float i=0.; i<N; i++)
        O = max( O,    smoothstep(S/h, 0., abs(fract(U.x)-iMouse.y/iResolution.y-.1)-.1)              // strip
                   * ( .7 + .3* sin(6.28* ( U.y*1.73/2. + .5*floor(U.x) ))) ), // waves
        U *= mat2(cos(a),-sin(a),sin(a),cos(a));
   O.z=0.;//make yellow, personal touch to easily differentiate a preview.
}
