#define PI 3.141592653

vec2 rotate2D(vec2 _uv, float _angle) 
{
	mat2 rotate = mat2(cos(_angle), -sin(_angle),
        		   sin(_angle), cos(_angle));

    _uv -= .5;
    _uv = rotate * _uv;
    _uv += .5;

    return _uv;
}

float box(vec2 _uv, vec2 _size, float _smoothEdge)
{	
	_size = vec2(.5) - _size * .5;
    float aa = _smoothEdge * .5;
    vec2 ret = smoothstep(_size, _size + vec2(aa), _uv);
    ret *= smoothstep(_size, _size + vec2(aa), 1. - _uv);
    return ret.x*ret.y;
}

float patterns1(vec2 _uv, vec2 _size, float _smoothEdge, float cnt, float t)
{
	_uv *= cnt;
    _uv = fract(_uv);
    _uv = rotate2D(_uv, PI * t);

    return box(_uv, _size, _smoothEdge);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv.x *= iResolution.x/iResolution.y;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    float t = iTime; 
    float sz = iTime*.1;
  	col += vec3(patterns1(uv, vec2(.7), .01, 5., cos(t) ) );
  	col -= vec3(patterns1(uv, vec2(.7), .01, 10., 1. - floor(t)*.1 ));

    // Output to screen
    fragColor = vec4(col,1.0);
}
