void mainImage(out vec4 O, vec2 U) {

    float t = iTime, l, r=.5,s=1.73*r, h=2.;      // r: radius s: offset h: height/2.
    mat2  R = mat2( cos(.1*sin(t)+vec4(0,33,11,0)) );
    vec3  q = iResolution,
          D = normalize(vec3((U+U-q.xy)/q.y, -8)),// ray direction
          p = 30./q;                             // marching point along ray 
    O-=O; t++;
    for ( O++; O.x > 0. && t > .01 ; O-=.015 )
  //for ( O++; O.x > .001 && t > .01 ; O*=.95 )
        q = p,
        q.xz *= R, q.yz *= R,                    // rotation
        t = 1e5,
#define C q.x = mod( q.x+r, r+r) -r,    \
          q.y = mod( q.y+s, s+s) -s,    \
          l   = length(q.xy),           \
        t = min(t, max(l -r/1.05, -(l-.8*r/1.05))) // hollow cylinder 

        C,                                       // even row
        q.xy -= r*vec2(1,1.73), C,               // odd row

        t = max(t,abs(q.z) -h),                       // column length in Z

        p += t*D;                                // step forward = dist to obj
  //O = vec4(.8,.9,1,1) - O;
    O *= .4; if (O.x<0.) O = vec4(.8,.9,1,1);
  //O *= .5; if (t>.01) O = vec4(.8,.9,1,1);

}

