vec2 random2( vec2 p ) {
    return fract(sin(vec2(dot(p,vec2(127.1,311.7)),dot(p,vec2(269.5,183.3))))*43758.5453);
}

// Commutative smooth minimum function. Provided by Tomkh, and taken 
// from Alex Evans's (aka Statix) talk:
float smin(float a, float b, float k){

   float f = max(0., 1. - abs(b - a)/k);
   return min(a, b) - k*.25*f*f;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
//	vec2 fragCoord = gl_FragCoord.xy;
//    vec2 iResolution = u_resolution;
   // float iTime = u_time;
  //  vec4 fragColor;

    vec3 color;

    vec2 st = (fragCoord - .5 * iResolution.xy) / iResolution.y;

	st *=10.;

    // Tile
    vec2 i_st = floor(st);
    vec2 f_st = fract(st);

    float m_dist = 1.; // min distance

    for(int j=-5; j<=5; j++) {
        for(int i=-5; i<=5; i++) {

            // Neighbor place in the grid
            vec2 neighbor = vec2(float(i), float(j));

            // Random position from current + neighbor place in the grid
            vec2 offset = random2(i_st + neighbor);

            // Animate the offset
            offset = 0.5 + 0.5 * sin(iTime + 6.2831 * offset );

            // Position of the cell
            vec2 pos = neighbor + offset - f_st;

            // Cell distance
            float dist = length(pos);

            // Metaball
            m_dist = smin(m_dist, dist, 1.688);            
        }
    }

	float f = m_dist *8.296;
    //f = ceil(f *3.) / 3.;
    f = sin(m_dist * 40.)/2. + .5;
   // f = smoothstep(0., fwidth(f) * 200.708, f - 0.108);
   // f = smoothstep(0., fwidth(f + 10000.196) * 100.5, f + -01.4036);
    color = 1.0 - vec3(f);
    color *= 	fwidth(f) * 3.640;
    color *= 1.5;

	//color = sqrt(max(color, 0.00));



    fragColor = vec4(color, 1.0);
   // gl_FragColor = fragColor;

}
