float arrow(vec2 pos) {

    float boxlen = length(max(abs(pos)-0.25,0.0));
    float trianglelen = max(pos.x+0.25,(-pos.x+abs(pos.y)*1.5-1.0)/sqrt(2.0));
    return min(boxlen,trianglelen);
}

int getcell(vec2 pos) {
    return int(floor(texelFetch(iChannel0, ivec2(mod(pos,vec2(64.0))),0).r*2.99999));
}


#define STARTPOSITION vec2(-0.0,0.0)
#define pi acos(-1.0)
#define rot(spin) mat2(cos(spin),sin(spin),-sin(spin),cos(spin))
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 floorpos = STARTPOSITION;
    vec2 pos = vec2(0.0,0.5)+floorpos;
    vec2 dir = vec2(1.0,0.0);

    //follows the path
    for (float i = 0.0; i < floor(iTime); i++) {
        pos += dir*0.5;
        int celltype = getcell(floorpos);

        if (celltype!=2) {
        	dir = (celltype == 0 ? 1.0 : -1.0) * dir.yx;
        }

        floorpos += dir;
        pos += dir*0.5;
    }

    vec2 dir2=dir;
    pos += dir*sin(fract(iTime)*pi*0.5)*0.5;
    int celltype = getcell(floorpos);
    if (celltype==0) {
        dir2 *= rot(dot(abs(dir),vec2(-1.0,1.0))*fract(iTime)*pi*0.5);
        dir =  dir.yx;
    } else if (celltype == 1) {
        dir2 *= rot(dot(abs(dir),vec2(1.0,-1.0))*fract(iTime)*pi*0.5);
        dir = -dir.yx;
    }

    pos += dir*(0.5-cos(fract(iTime)*pi*0.5)*0.5);



	vec2 uv = (fragCoord.xy * 2.0 - iResolution.xy) / iResolution.y;

    vec3 ro = vec3(pos.x-dir2.x*0.3,1.2,pos.y-dir2.y*0.3);
    vec3 rd = normalize(vec3(uv-vec2(0.0,0.5),1.0));
    rd.xz *= mat2(vec2(-dir2.y, dir2.x),dir2);

    if (rd.y > 0.0) {
        fragColor = vec4(0.0,0.0,1.0,1.0);
        return;
    }
    float dist = -(ro.y-1.0)/rd.y;
    vec2 hit1 = ro.xz+rd.xz*dist;
    vec2 hit2 = ro.xz+rd.xz*-((ro.y-0.8)/rd.y);

    vec2 flooruv = floor(hit1);
    vec2 uv2 = hit1-flooruv-0.5;

    celltype = getcell(flooruv.xy);
    if (celltype==0) uv2 *= vec2(1.0,-1.0);



    //if (uv2.x<-uv2.y) uv2 = -uv2.yx;
    uv2 *= step(0.0,uv2.x+uv2.y)*2.0-1.0;


    float checkerboard = step(0.5,mod(flooruv.x+flooruv.y,2.0));
    float len;
    if (celltype == 2) {
        len = min(abs(uv2.x),abs(uv2.y));
    } else {
    	len = length(uv2-0.5)-0.5;
    }

    //draws the truchet and the background with two different textures

    vec2 arrowspace = (pos-hit1)*4.0*mat2(dir2, vec2(dir2.y,-dir2.x));
    if (arrow(arrowspace) <= 0.0) {
        fragColor = textureLod(iChannel3,arrowspace*0.5,0.0);
    } else if (abs(len) < 0.125) {
		fragColor = textureLod(iChannel1,hit1*0.2,sqrt(-dist/rd.y));
    } else {
        fragColor = textureLod(iChannel2,hit2*0.2,sqrt(-dist/rd.y));
    }


    //invert the colord if the dot and the pixel is in the same cell
    //fragColor = abs(float(all(equal(flooruv,floorpos)))-fragColor);
}
