void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    fragColor = 1.-(texture(iChannel0, uv))*0.8;

    // df test with mouse by Abje
    vec2 muv = iMouse.xy / iResolution.xy;
    fragColor += max(1.0-abs(length((uv-muv)*iResolution.xy/iResolution.y)-(1.0-texture(iChannel0, muv))/6.0)*500.0,0.0);
}

