vec4 cell(vec2 fragCoord, vec2 pixel)
{
	vec2 uv = (fragCoord-pixel) / iResolution.xy;
    return texture(iChannel0, uv);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;

    if (iFrame < 150)
    {
        fragColor = clamp(fwidth(texture(iChannel2, uv)).rrrr*50., 0.,1.);
        fragColor = vec4(step(0.9,fragColor.r));
    }
    else
    {       
        vec2 n = vec2(1,1);
        //n = normalize(sobel(uv));

        // get current cell from backbuffer
        vec4 c = cell(fragCoord, vec2(0,0)); // current cell
        if (length(c) < 0.5)
        {
#if 0
            // A B
            // C D
        	// get adjacents cells from backbuffer 
        	vec4 l = cell(fragCoord, n*vec2(-1,0)); // left cell
        	vec4 r = cell(fragCoord, n*vec2(1,0)); // rigt cell
        	vec4 t = cell(fragCoord, n*vec2(0,1)); // top cell
        	vec4 b = cell(fragCoord, n*vec2(0,-1)); // bottom cell
        	c = max(c, max(l,max(r,max(t,b))));
#else
            // A B C
            // D E F
            // G H I
            vec4 d = vec4(0);
            for (float i = -1.; i <= 1.; i++)
            for (float j = -1.; j <= 1.; j++)
            {
               c = max(c, cell(fragCoord, vec2(i,j)));
            }
#endif
            c *= .975;         
        }

        fragColor = c;
	}
}
