// 2d sdf + lighting code taken from https://www.shadertoy.com/view/MsXBWH by wutzhuhn
// Melt Text added by Del 11/12/2017
// added multi-colour support, improved blend etc.
#define roundnes 0.055

#define PI 3.14159265
#define DEG2RAD ((PI * 2.0) / 360.0)

float subtract(float a, float b)
{
	return min(max(a, -b), -roundnes) + length(max(vec2(roundnes) + vec2(a, -b), vec2(0.0)));
}

float merge(float a, float b)
{
	return max(min(a, b), roundnes) - length(max(vec2(roundnes) - vec2(a, b), vec2(0.0)));
}

mat2 rotate(float a)
{
    float sa = sin(a), ca = cos(a);
	return mat2(ca, -sa, sa, ca);
}

float circle(vec2 p, float radius)
{
	return length(p) - radius;
}

 // --- access to the image of ascii code c
float char(vec2 p, int C)
{
    p*=4.2;
    if (p.x<0.|| p.x>1. || p.y<0.|| p.y>1.) return 1e5;
    vec4 cc = textureGrad( iChannel0, p/16. + fract( vec2(C, 15-C/16) / 16. ) , dFdx(p/16.),dFdy(p/16.) );

    if (iMouse.z>0.5)
 	   return (cc.w-0.525)*0.3;    		// 0.3 = blur

    return (cc.w-0.51)*0.3;    		// 0.3 = blur
}


// http://mercury.sexy/hg_sdf/
// Repeat space along one axis. Use like this to repeat along the x axis:
// <float cell = pMod1(p.x,5);> - using the return value is optional.
float pMod1(inout float p, float size)
{
	float halfsize = size*0.5;
	float c = floor((p + halfsize)/size);
	p = mod(p + halfsize, size) - halfsize;
	return c;
}


// Central 'DEL' text...
float fontDist(vec2 p)
{
    p = p*rotate(DEG2RAD*-30.0);
    p.x += iTime*0.08;
    p.y += iTime*0.04;
    p.x += pMod1(p.y,0.25)*0.15;		// offset X based on yMod
    pMod1(p.x, 0.45);					// xMod
    p += vec2(0.24,0.12);
    float d1 =  char(p,68);	//68
    p.x -= 0.12;
    float d2 =  char(p,69);	//69
    p.x -= 0.12;
    float d3 =  char(p,76);	//76
    return (min(min(d1,d2),d3));
}


// render Mask + Repeating 'DEL'
vec4 shapeDist(vec2 p)
{
    vec2 p2 = p*0.85;
    pMod1(p2.x, 0.9);					// xMod
	float mask = circle(p2,0.40);

    vec4 res = vec4(0.0);
    res.w = fontDist(p);

    res.w = subtract(mask,res.w);

    const vec3 maskcol = vec3(0.43,0.35,0.5);
    const vec3 fontcol = vec3(0.8,0.7,0.4);

    // blend hack
    float blenddist = 0.05;
    float blend = mask<-blenddist ? 1.0 : clamp((-mask)*1.0/blenddist,0.0,1.0);
    res.xyz = mix(maskcol,fontcol,blend);
    return res;
}

vec4 maskSharp(vec2 p)
{
    vec4 res = shapeDist(p);
    res.w = clamp(res.w * 250.0, 0.0, 1.0);
    return res;
}

float maskBlurry(vec2 p)
{
    return clamp((shapeDist(p).w + 0.003) * 75.0, 0.0, 1.0);
}

vec3 normalAt(vec2 p)
{
    float ps = 1.0 / iResolution.x;
    float x = maskBlurry(p - vec2( ps, 0.0)) - maskBlurry(p + vec2( ps, 0.0));
    float y = maskBlurry(p - vec2(0.0,  ps)) - maskBlurry(p + vec2(0.0,  ps));
    return normalize(vec3(x, y, 1.0));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
	vec2 p = ( fragCoord -.5*iResolution.xy ) / iResolution.y;
    vec3 color = vec3(0.3,0.32,0.3);

    vec4 res = maskSharp(p);
    float shade = 0.0;
    if (res.w>0.0)
    {
        float z = (0.5+sin(iTime*3.5))*0.1;
        vec3 lightPos = vec3(-0.5, -0.3, 0.25+z);
        vec3 toLight = lightPos - vec3(p, 0.0);
        vec3 normal = normalAt(p);
        color = res.xyz;
        color *= 0.25+clamp(dot(normalize(toLight), normal), 0.0, 1.0) / 0.95 ;;

    }
	fragColor = vec4(color, 1.0);
}
