vec2 random2(vec2 uv){
    uv = vec2( dot(uv,vec2(127.1,311.7)),
              dot(uv,vec2(269.5,183.3)) );
    return -1.0 + 2.0*fract(sin(uv)*43758.5453123);
}

// Value Noise by Inigo Quilez - iq/2013
// https://www.shadertoy.com/view/lsf3WH
float noise(vec2 uv) {
    vec2 i = floor(uv);
    vec2 f = fract(uv);

    vec2 u = f*f*(3.0-2.0*f);

    return mix( mix( dot( random2(i + vec2(0.0,0.0) ), f - vec2(0.0,0.0) ), 
                     dot( random2(i + vec2(1.0,0.0) ), f - vec2(1.0,0.0) ), u.x),
                mix( dot( random2(i + vec2(0.0,1.0) ), f - vec2(0.0,1.0) ), 
                     dot( random2(i + vec2(1.0,1.0) ), f - vec2(1.0,1.0) ), u.x), u.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = fragCoord.xy / iResolution.xy;
    uv.y *=.5;
    uv.x += iTime*0.002;
    vec3 color = vec3(0.0);

    float t = 1.0;
    t = abs(1.0-(0.5*sin((iTime/100.))))*1.;
    uv += noise(uv*2.)*t;

    color += smoothstep(.15,.26,noise(uv*8.))+vec3(0.,.6,0.4); 

    color += vec3(1.3) * smoothstep(.01,1.,noise(uv));


    color += smoothstep(.1,.11,noise(uv*20.))+vec3(0.9,0.2,0.0);
    color -= smoothstep(.15,.4,noise(uv*10.));

    color /= vec3(noise(uv*100.), noise(uv*150.), noise(uv*200.));

    fragColor = vec4(color-vec3(0.,1.,1.),1.);
}
