// z * z
vec2 zmul(vec2 a, vec2 b)
{
	return vec2(a.x*b.x-a.y*b.y, a.x*b.y+b.x*a.y);
}

// 1 / z
vec2 zinv(vec2 a)
{
	return vec2(a.x, -a.y) / dot(a,a);
}

// z^n ( n : 1 to 100 )
vec2 zpow(vec2 a, int n)
{
	vec2 res = a;
	for (int k=1;k<100;k++)
	{	if (k>n-1) break;
		res = zmul(res, a);	}
	return res;
}

