#define TEXT 1
#define NO_STROBE 1

#if TEXT
vec4 text(vec2 uv, int value)
{    
    uint[16] font = uint[16](
        0xEAAAEu, // 0
        0x4644Eu, // 1
        0xE8E2Eu, // 2
        0xE8E8Eu, // 3
        0xAAE88u, // 4
        0xE2E8Eu, // 5
        0xE2EAEu, // 6
        0xE8888u, // 7
        0xEAEAEu, // 8
        0xEAE8Eu, // 9
        0xEAEAAu, // A
        0x6A6A6u, // B
        0xE222Eu, // C
        0x6AAA6u, // D
        0xE2E2Eu, // E
        0xE2E22u  // F
    );
    if (uv.x < 0. || uv.y < 0. || uv.x > 3. || uv.y > 5.)
        return vec4(0);
    value = int(mod(float(value), 16.));
    return vec4((font[value]>>int(uv.y*4.+uv.x-1.))&1u);
}
#endif

void mainImage(out vec4 fragColor, vec2 fragCoord)
{
    fragColor = texture(iChannel0,fragCoord/iResolution.xy).rrrr;

#if NO_STROBE
    if (mod(float(iFrame),iResolution.y) < fragCoord.y)
    {
        fragColor = vec4(.5);
    }
#endif

#if TEXT
    int rule = int(floor(float(iFrame)/iResolution.y+1.)*997.)&0xFFFF;
    vec2 textbox = fragCoord - vec2(0, iResolution.y - 7.);

    if(textbox.y>=0. && textbox.x<17.)
    {
        fragColor = vec4(0,0,0,0);
        fragColor += text(textbox-vec2(13,1), (rule>>0)&15);
        fragColor += text(textbox-vec2(9,1), (rule>>4)&15);
        fragColor += text(textbox-vec2(5,1), (rule>>8)&15);
        fragColor += text(textbox-vec2(1,1), (rule>>12)&15);
    }
#endif
}

float rand(float n){return fract(sin(n) * 43758.5453123);}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    int rule = int(floor(float(iFrame)/iResolution.y+1.)*997.)&0xFFFF;

    vec2 uv = fragCoord/iResolution.xy;
    vec2 o = 1./iResolution.xy;

	if (fragCoord.y < 1.)
    {
        fragColor.r = step(.5,rand(fragCoord.x));
    }
    else
    {
        float a = texture(iChannel0,uv-vec2( o.x,o.y)).r;
        float b = texture(iChannel0,uv-vec2(   0,o.y)).r;
        float c = texture(iChannel0,uv-vec2(-o.x,o.y)).r;
        float d = texture(iChannel0,uv-vec2(0,2.*o.y)).r;
        int r = int(a*8.+b*4.+c*2.+d);
        fragColor.r = float((rule>>r)&1);
    }
}
