vec4 h2rgb(float c) {  // classical hsv2rgb simplified
    vec4 p = abs(fract(c + vec4(3,2,1,0)/3.) * 6. - 3.);
    return clamp(p - 1., 0., 1.);
}

vec4 j2hue(float c) {
    return .5+.5*cos(6.28*c+vec4(0,-2.1,2.1,0));
}

vec4 sin2hue(float c) {
    vec4 C = .5+.5*cos(6.28*clamp(1.5*fract(c)-vec4(0,.5,1,1.5),-.5,.5));
    return C+vec4(C.w,0,0,0);
}

void mainImage( out vec4 O, vec2 U )
{
	U /= iResolution.xy; U.y*=3.; U.x*=6.;
	O =   U.y>2. ? sin2hue(U.x) : U.y>1.99 ? O-O
        : U.y>1. ? j2hue(U.x)   : U.y>0.99 ? O-O
        :          h2rgb(U.x);
    float v = O.r+O.g+O.b, 
          m = min(O.r,min(O.g,O.b)),
        M = max(O.r,max(O.g,O.b));

    O =   U.x>5. ? sqrt((O-m)/(M-m)) : U.x>4.99 ? O-O
        : U.x>4. ? sqrt(O/v)         : U.x>3.99 ? O-O
        : U.x>3. ? sqrt(O)           : U.x>2.99 ? O-O+1.
        : U.x>2. ? (O-m)/(M-m)       : U.x>1.99 ? O-O
        : U.x>1. ? O/v               : U.x>0.99 ? O-O
                 : O;

}
