void mainImage( out vec4 fragColor, in vec2 px )
{
    vec2 res = iResolution.xy;
    vec2 uv = (px - res) / res.y;
    vec2 ms = (iMouse.xy - res) / res.y;
    vec4 col = cell(ch0, ivec2(px));
    vec4 av = vec4(0);

    // messes up on screen boundaries
    float n = 5.;// 6. * (1.-1./cosh(-0.2*col.a));
    //n = round(n);
    for (float i = -n; i <= n; i++)  
    for (float j = -n; j <= n; j++) {
        float sc = 0.;
        sc = 6. * (1.-1./cosh(2.*abs(uv.y-ms.y)));

        // maybe do abs(col.a - targetDistance)
        //sc = 3. * (1.-1./cosh(-0.15*col.a));
        av += cell(ch0, ivec2(px + sc * vec2(i, j)));
    }
    av /= pow(2.*n+1.,2.);

    fragColor = av;//col * exp(-0.*col.a);

}

// Thanks to (everyone I've copied code + ideas from):
