#define tau 6.2831853071
#define pi 3.1415926535
#define thc(a,b) tanh(a*cos(b))/tanh(a)
#define ths(a,b) tanh(a*sin(b))/tanh(a)
#define pal(a,b) .5+.5*cos(2.*pi*(a+b))
#define sabs(x) sqrt(x*x+1e-2)
//#define sabs(x, k) sqrt(x*x+k)
#define rot(a) mat2(cos(a), -sin(a), sin(a), cos(a))

#define ch0 iChannel0
#define ch1 iChannel1
#define ch2 iChannel2

vec4 cell(in sampler2D ch, in ivec2 p) {
    ivec2 r = ivec2(textureSize(ch, 0));
    p = (p+r) % r;
    return texelFetch(ch, p, 0);
}

#define FK(k) floatBitsToInt(k*k/7.)^floatBitsToInt(k)
float hash(float a, float b) {
    int x = FK(a), y = FK(b);
    return float((x*x+y)*(y*y-x)-x)/2.14e9;
}

vec3 erot(vec3 p, vec3 ax, float ro) {
  return mix(dot(ax, p)*ax, p, cos(ro)) + cross(ax,p)*sin(ro);
}

float cc(float a, float b) {
    float f = thc(a, b);
    return sign(f) * pow(abs(f), 0.25);
}

float cs(float a, float b) {
    float f = ths(a, b);
    return sign(f) * pow(abs(f), 0.25);
}

float h21(vec2 a) { return fract(sin(dot(a.xy, vec2(12.9898, 78.233))) * 43758.5453123); }
float mlength(vec2 uv) { return max(abs(uv.x), abs(uv.y)); }
float mlength(vec3 uv) { return max(max(abs(uv.x), abs(uv.y)), abs(uv.z)); }

// Maybe remove this
float sfloor(float a, float b) { return floor(b-.5)+.5+.5*tanh(a*(fract(b-.5)-.5))/tanh(.5*a); }

float smin(float a, float b, float k) {
    float h = clamp(0.5 + 0.5 * (b - a) / k, 0., 1.);
    return mix(b, a, h) - k * h * (1. - h);
}

float smax(float a, float b, float k) {
    float h = clamp(0.5 - 0.5 * (b - a) / k, 0., 1.);
    return mix(b, a, h) + k * h * (1. - h); 
}

#define MAX_STEPS 400
#define MAX_DIST 100.
#define SURF_DIST .001

vec3 ori() {
    vec2 m = iMouse.xy/iResolution.xy;
    vec3 ro = vec3(0, 0.6, -3);
    ro.xz *= rot(-0.2 * iTime);
    // ro.yz *= rot(-m.y*3.14+1.);
    // ro.xz *= rot(-m.x*6.2831);
    return ro;
}

vec2 map(vec3 p) {
    float mat = 0.;   
    float sd = p.y;

    float tx = 0.;
    float t = mod(0.25 * iTime, 3.);
    float f = fract(0.25 * iTime);
    f = smoothstep(0., 1., f);

    float t2 = 0.2 * iTime;
    vec2 uv = p.xz + 5. * (t2 + thc(1.5, t2));
    float tx1 = texture(ch0, 0.015 * uv).r;
    float tx2 = texture(ch1, 0.035 * uv).r;
    float tx3 = tx2 + 0.2*texture(ch2, 0.12 * uv).r;

    if (t < 1.)      tx = mix(tx1, tx2, f);
    else if (t < 2.) tx = mix(tx2, tx3, f);
    else             tx = mix(tx3, tx1, f);

    sd -= -1.+1./cosh(0.2*length(p.xz))*tx;
    sd -= 0.02 * dot(p.xz, p.xz);
    return vec2(sd, mat);
}

vec3 march(vec3 ro, vec3 rd, float z) {	
    float d = 0.;
    float s = sign(z);
    int steps = 0;
    float mat = 0.;
    for(int i = 0; i < MAX_STEPS; i++) {
    	vec3 p = ro + rd * d;
        vec2 m = map(p);
        m.x *= 0.8 + 0.2 * hash(hash(p.x,p.z), p.y); // for glow
        if (s != sign(m.x)) { z *= 0.5; s = sign(m.x); }
        if (abs(m.x) < SURF_DIST || d > MAX_DIST) {
            steps = i + 1;
            mat = m.y;
            break;
        }
        d += m.x * z; 
    }   
    return vec3(min(d, MAX_DIST), steps, mat);
}

vec3 norm(vec3 p) {
	float d = map(p).x;
    vec2 e = vec2(.001, 0);

    vec3 n = d - vec3(
        map(p-e.xyy).x,
        map(p-e.yxy).x,
        map(p-e.yyx).x);

    return normalize(n);
}

vec3 dir(vec2 uv, vec3 p, vec3 l, float z) {
    vec3 f = normalize(l-p),
        r = normalize(cross(vec3(0,1,0), f)),
        u = cross(f,r),
        c = f*z,
        i = c + uv.x*r + uv.y*u,
        d = normalize(i);
    return d;
}

float AO(in vec3 p, in vec3 n) {
	float occ = 0.;
    float sc = 1.;
    for (float i = 0.; i < 5.; i++) {
        float h = 0.015 + 0.015 * i;
        float d = map(p+h*n).x;
        occ += (h-d)*sc;
        sc *= 0.95;
    }
    return clamp(1. - 3.*occ, 0., 1.);
}

float shadow(in vec3 ro, in vec3 rd) {
    float res = 1.;
    float t = SURF_DIST;
    for (int i=0; i<24; i++)
    {
		float h = map(ro + rd * t).x;
        float s = clamp(32. * h / t, 0., 1.);
        res = min(res, s);
        t += clamp(h, 0.01, 0.2);
        if(res<SURF_DIST || t>MAX_DIST ) break;
    }
    res = clamp(res, 0.0, 1.0);
    return smoothstep(0., 1., res);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    vec3 ro = ori();

    vec3 rd = dir(uv, ro, vec3(0), 1.);
    vec3 col = vec3(0);

    vec3 m = march(ro, rd, 1.);  
    float d = m.x;    
    vec3 p = ro + rd * d;
     vec3 n = norm(p);
    if (d<MAX_DIST) {        

        vec3 r = reflect(rd, n);        

        vec3 ld = normalize(vec3(1,2,3));
        float dif  = dot(abs(n),  ld)*.5+.5;
        float spec = pow(dif, 6.);
        float fres = pow(1. + dot(rd, n),  5.);

        col = vec3(1);  
        col = .5+.5*n;
        //col = mix(col, vec3(1, 0, 0), fres);
    }

    col *= vec3(1. - 1./cosh(-0.025 * m.y));
    //col *= 1./cosh(0.04*length(p.xz));

    col = pow(col, vec3(1./2.2));	// gamma correction

    col = clamp(col, 0., 1.);
    fragColor = vec4(col, d);
}
