/// by Dom Mandy in 2023


/*
domrally's flatstep function.
Kinda like a smoothest-step or ∞ degree hermite interpolation.
*/
float flatstep(float a, float b, float x) {
    float t = clamp((x - a) / (b - a), 9e-8, 1. - 9e-8),
          h = atanh(cos(3.14 * t)),
          H = h * h;

    // error function method by Sergei Winitzki using Padé approximations
    return .5 - .5 * sign(h) * sqrt(1. - exp(-H * (.1 * H + 1.27) / (.1 * H + 1.)));
}


void mainImage(out vec4 o, vec2 i) {
    vec2 R = iResolution.xy;
    float f = flatstep(.5, R.x - .5, i.x + .5);

    o = vec4(flatstep(7. / R.y, 0., abs(.2 + .6 * f - i.y / R.y)));
}


