//--- quaternionRotation math (updated 2021-Jan-7) //https://www.shadertoy.com/view/4tSyRz
//
//essential for the quaternion-camera.
//
//ANY rotations in 3d are non-commutative!
//
//matrix rotations are just bulky, memory wasting
//EulerRotations almost certainly fail to rotate over the SHORTEST path.
//EulerRotations almost certainly will gimbalLock and get stuck along one axis
//QuaternionRotations are superior here.
//-> we only use EulerRorations for simple input devices (keyboard input)
//-> we convert to quaternions, buffered as vec4.
vec4 qid(){return vec4(0,0,0,1);}//quaternion Identity
//
//---quaternion & AxisAngle (==quaternion multiplication)
//
//return quaternion from Axis[a] and Angle[r]
vec4 aa2q(vec3 a,float r){vec2 g=vec2(sin(r),cos(r))*.5;return normalize(vec4(a*g.x,g.y));}
//return AxisAngle of NORMALIZED quaternion input
vec4 q2aa(vec4 q){return vec4(q.xyz/sqrt(1.0-q.w*q.w),acos(q.w)*2.);}//assumed q is normalized coverts to axis&angle
//return q2, rotated by q1, order matters (is non commutative) : (aka quaternion multiplication == AxisAngleRotation)
vec4 qq2q(vec4 q1, vec4 q2){return vec4(q1.xyz*q2.w+q2.xyz*q1.w+cross(q1.xyz,q2.xyz),(q1.w*q2.w)-dot(q1.xyz,q2.xyz));}
//extension to qq2q(), scaled by sensitivity [f] (==quaternion ANGULAR equivalent to slerp() )
vec4 qq2qLerp(vec4 a, vec4 b, float f//jonathanBlow wrote many documents about better alternatives.
){float d=dot(a,b),t=acos(abs(d)),o=(1./sin(t));return normalize(a*sin(t*(1.0-f))*o*sign(d)+b*sin(t*f)*o);}
//doing qq2q() multiple times, you need to normalize() the quaternion, to fix rounding errors.
//how often you do this is up to you.
//normalize q (assuming length(q) is already close to 1, we can skip whe sqrt()
vec4 qn(vec4 q//){return q/dd(q) //not as wortksafe, division is slow.
){float w=dot(q,q);return w==1.?q:.5*(3.-w)*q;}//worksafe and faster: https://www.shadertoy.com/view/llySRh
//return normalize(q) //unnecessary, as long as lenght(q) is close to 1.

//qexp() qlog() qpow() seem useful for fractals.
vec4 qexp(vec4 v
){float a=dot(v.xyz,v.xyz)
 ;if (a<1e-7) return vec4(0,0,0,1)
 ;a=.5*sqrt(a)
 ;return vec4(cos(a),v.xyz*(sin(a)*.5/a));}
vec4 qlog(vec4 q
){float l=length(q.xyz),s=2.*atan(l/q.w)/l
 ;return vec4(q.xyz*2.*atan(l/q.w)/l,l);}//bug in qexp, final quat constructor args reversed.
vec4 wpow(vec4 a,vec4 b){return qexp(qlog(a)*b);}//or something like that
vec4 qrev(vec4 q){q.w=-q.w;return q;}// conjugate quaternion
// untested, just tossed together to demo qlog,qexp instead of porting original lol - probably backward
/*vec4 qq2qLerpBAD(vec4 a, vec4 b, float i){
    //ERROR; will go around the wrong long-angle hyper-geodesic half-the-time, needs bit of work
   ;if (dot(a, b) < 0.) b.w = -b.w; // should help
    return qq2q(a,qexp(qlog(qq2q(qrev(a),b))*i));}//from https://www.shadertoy.com/view/ssGSDm
*/
//
//not sure about more use-cases pf qexp() qlog() qpow()
//
//return quaternion, that is the shortest rotation, between looking to [a before], and looking to [b after] the rotation.
vec4 qBetween(vec3 a,vec3 b//http://wiki.secondlife.com/wiki/LlRotBetween
){float v=sqrt(dd(a)*dd(b));if(v==0.)return qid();v=dot(a,b)/v
 ;vec3 c=a.yzx*b.zxy-a.zxy*b.yzx/v;float d=dd(c)
 ;if(d!=0.){float s=(v>-0.707107) ? 1.+v : d/(1.+sqrt(1.-d));return vec4(c,s)/sqrt(d+s*s);}
 ;if(v>0.)return qid()
 ;float m=length(a.xy);return (m!=0.) ? vec4(a.y,-a.x,0,0)/m : vec4(1,0,0,0);
 ;}//Written by Moon Metty, optimized by Strife Onizuka (faster version, without trigonometry)
vec4 qBetween2(vec3 a,vec3 b){if(a==b)return qid();return aa2q(cross(a,b),AngleBetween(a,b));}//qBetween() with less code, but has some slower or less precise cases.
//return inverse of quaternion
vec4 qinv(vec4 q){return vec4(-q.xyz,q.w)/dd(q);}
//return VECTOR p, rotated by quaterion q;
vec3 qv2v(vec4 q,vec3 p){return qq2q(q,qq2q(vec4(p,.0),qinv(q))).xyz;}
//return [v]vector, rotated by [b] around v2(0)
v2 r(v3 b,v2 v){
//return qv2v(b*vec4(1,1,1,-1),v);//somehow this is not identical, fuck it all!
v2 t=2.*cross(b.xyz, v);return v+b.w*t+cross(b.xyz,t);}//return r(q,r(v3(v,.0),qinv(q))).xyz
//
//qv2v()  with swapped inputs
//return quaterion P (as vector), as if it i rotated by VECTOR p (asif it is a quaternion)
vec3 vq2v(vec3 p,vec4 q){return qq2q(qinv(q),qq2q(vec4(p,0.0),q)).xyz;}
vec3 vq2v(vec4 a,vec3 b){return qv2v(a,b);}//in case of namespace confuction
vec3 qv2v(vec3 a,vec4 b){return vq2v(a,b);}//in case of namespace confuction
//
