// https://beautifier.io/
#define M(a) mat2(cos(a), -sin(a), sin(a), cos(a))
void mainImage(out vec4 O, vec2 v) {
        vec2 e0, R = iResolution.xy,
                u = 9. * (v + v - R) / R.y + 120.;
        //w = u + .5;
        //u = vec2(atan(u.y,u.x)/3.14159*40.,length(u)* 1.5)+199.;
        vec2 f = fract(u + .5) - .5, i = floor(u + .5), g = abs(f) - .5;

        O *= 0.;
        if (mod(i.x, 2.) < 1.) O = vec4(.9, .9, 1, 1);
        if (mod(i.y, 2.) < 1.) O = vec4(.4, .4, 1, 1);
        g = g + g.yx * vec2(1, -1);
        if ((int(u.x) + int(u.y * .5) + 1000) % 2 == 0) g = g.yx * vec2(-1, 1);
        float m;
        if(iMouse.x<.5*R.x) m = (g.x * g.y < 0. ? 1. : -1.);
        else                m=(g.x*g.y*sign(mod(iTime,8.)-4.)>0.?-1.:1.); // dray
        if (length(max(e0, abs(g) - .25)) == .0) O = vec4(m);
        if ( //(int(u.y*.5)+1000)%2==0  && 
                mod(i.y, 2.) < 1.) {
                f *= M(.05 * (mod(i.y, 4.) < 1. ? 1. : -1.));
                if (length(max(e0, abs(f) - vec2(.2, .05))) == .0) O = vec4(.25, .15, 1, 0);
        }
}

