// cubic bump that goes through (0,0)->(1,0)
// slope at x=0 is df0
// slope at x=1 is df1
float cubicbump(v0 x,v0 c,v0 a){a+=c;return x*(x*(x*a-a-c)+c); }
float smoothbump(v0 a,v0 r,v0 x){return 1.-smoothstep_unchecked(min(abs(x-a),r)/r);}
//smoothstep(x,center,radius)
float smoothstep_c(v0 x,v0 c,v0 r){return smoothstep(c-r,c+r,x);}
//smoothstep centered at 0
float smoothband(v0 x,v0 r,v0 raa){return 1.-smoothstep_c(abs(x),r,raa );}
// range s,e
float smoothband(v0 x,v0 s,v0 e,v0 raa){return smoothband(x-(e+s)*.5,(e-s)*.5,raa);}
