vec2 PathNoise11sD( float x ){
	x -= 0.5;
	float fr = fract( x );
	int x0 = int( x - fr );
	uint i = uint( x0 );
	float y0 = hash11u_lowbias32( i );
	float y1 = hash11u_lowbias32( i + 1u );
	float f = smoothstep_unchecked( fr );
	float fd = smoothstep_unchecked_d( fr );
	return vec2( mix( y0, y1, f ), ( y1 - y0 ) * fd );}

