//The shader code that embeds a 938x84 frame buffer at its largest size without stretching the input

const vec2 target_resolution = vec2(940, 86);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Same texCoord as in Minecraft
    vec2 texCoord = fragCoord/iResolution.xy;
    // Own uv variable
    vec2 uv = texCoord;

    // Center origin around screen center (range -0.5, 0.5)
    uv -= .5;

    // Squish image in excessive dimension
    float r = (iResolution.x/iResolution.y) / (target_resolution.x/target_resolution.y);
    if (r >= 1.) uv.x *= r;
    else uv.y /= r;

    // Recenter origin to start at 0 (range 0.0, 1.0)
    uv += .5;
    uv.y -= 0.5/r - 0.5;    

    float PI = 3.1415926;
    float time = iTime * 0.02; 
    float scale = 1.2;
    vec3 morningScatter = vec3(1.000,0.647,0.322);;

    /*
    //已经调好的不出错色板
    vec4 color0 = vec4(0.122,0.353,1.000,1.0);
    vec4 color1 = vec4(0.400,0.561,1.000,1.0);
    vec4 color2 = vec4(0.561,0.690,1.000,1.0);
    vec4 color3 = vec4(1.000,0.541,0.459,1.0);
    vec4 color4 = vec4(1.000,0.616,0.361,1.0);*/

    //watching Color（已经调好的）
    vec4 color0 = vec4(0.000,0.267,1.000,1.0);
    vec4 color1 = vec4(0.220,0.427,1.000,1.0);
    vec4 color2 = vec4(0.659,0.761,1.000,1.0);
    vec4 color3 = vec4(1.000,0.506,0.420,1.0);
    vec4 color4 = vec4(1.000,0.675,0.459,1.0);

    //moving Color //深色
    vec4 vcolor0 = vec4(0.031,0.243,0.831,1.0);
    vec4 vcolor1 = vec4(0.180,0.400,1.000,1.0);
    vec4 vcolor2 = vec4(0.522,0.647,1.000,1.0);
    vec4 vcolor3 = vec4(1.000,0.506,0.420,1.0);
    vec4 vcolor4 = vec4(1.000,0.639,0.400,1.0);

    float step0 = 0.4 * scale;
    float step1 = 0.59 * scale;
    float step2 = 0.80 * scale;
    float step3 = 0.86 * scale;

    float offset = uv.x-0.45-time;
    float y = mod(offset,scale);

    //visiting Color
    vec4 visitingColor; 
    if (y < step0) {
    float dist0 = smoothstep(0.0, step0, y);
    visitingColor = mix(vcolor0, vcolor1, dist0);
    }

    if (y > step0 && y < step1) {
    float dist1 = smoothstep(step0, step1, y);
    visitingColor = mix(vcolor1, vcolor2, dist1);
    }

    //红到蓝 step1 - step2
    else if (y > step1 && y < step2) {
    float dist2 = smoothstep(step1, step2, y);
    visitingColor = mix(vcolor2, vcolor3, dist2);
    }

    else if (y > step2 && y < step3) {
    float dist02 = smoothstep(step2, step3, y);
    visitingColor = mix(vcolor3, vcolor4, dist02);
    }

    else if (y > step3){
    float dist3 = smoothstep(step3, 1.0 * scale, y);
    visitingColor = mix(vcolor4, vcolor0, dist3);
    }

    visitingColor = sqrt(visitingColor);
    fragColor = visitingColor;

    // Black bars at the sides
    if (uv.x < 0. || uv.x > 1. || uv.y < 0. || uv.y > 1.) fragColor = vec4(0);    
}
