vec4 solidblack = vec4(0,0.0,0.0,1.0); //This is actually black right now
vec4 solidBlue = vec4(0.2,0.2,1.0,1.0); //This is actually black right now
vec4 solidorang = vec4(1.0,0.65,0.0,1.0); //This is actually black right now
vec4 solidgelb = vec4(1.0,1.,0.0,1.0); //This is actually black right now

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 xy = fragCoord.xy; //We obtain our coordinates for the current pixel
    xy.x = xy.x / iResolution.x; //We divide the coordinates by the screen size
    xy.y = xy.y / iResolution.y;
    vec2 uv = (fragCoord.xy - 0.5 * iResolution.xy)/iResolution.y;
    vec2 st = vec2(atan(uv.x, uv.y)/(2.*3.1415)+0.5,length(uv));
    uv = st;

    // Now x is 0 for the leftmost pixel, and 1 for the rightmost pixel
    vec4 solidRed = vec4(0,0.0,0.0,1.0); //This is actually black right now

    if(xy.x > 0.75) {
        fragColor = solidBlue;
    }
    else if(xy.x > 0.25) {
        fragColor = solidblack;
    }
    else{
        fragColor = solidgelb;
    }


    float x = uv.x * 5.; 
    float m = min(fract(x), fract(1.-x));

    float c = smoothstep(.09, .1, m*.3+.2-uv.y);
    float d = smoothstep(.09, .1, m*.4+.2-uv.y);

    if (d > 0.5){
        fragColor = vec4(1);
    }

    if (c > 0.5){
        fragColor = vec4(0);
    }







}
