vec3 view_transform(vec3 col)
{
	// OETF
    return pow(col, vec3(1. / 2.2));
}

void get_vertices(out vec2 v0, out vec2 v1, out vec2 v2)
{
    v0 = vec2(random() - .5, random() - .5);
    v1 = vec2(random() - .5, random() - .5);
    v2 = vec2(random() - .5, random() - .5);

    v0 = mix(v0, 1.3 * vec2(0., .4), .6);
    v1 = mix(v1, 1.3 * vec2(-.5, -.3), .6);
    v2 = mix(v2, 1.3 * vec2(.5, -.3), .6);
}

void mainImage(out vec4 frag_col, in vec2 frag_coord)
{
    // time
    const float speed = 1.7;
    float ti = floor(iTime * speed);
    float tfrac = fract(iTime * speed);

    // UV
    vec2 uv = screen_to_uv_fit(frag_coord, iResolution.xy);

    // triangle vertices
    vec2 v0, v1, v2,
        v0a, v0b,
        v1a, v1b,
        v2a, v2b;
    {
        prng_init(vec3(float(int(ti)), 1, 2));
        get_vertices(v0a, v1a, v2a);

        prng_init(vec3(float(int(ti) + 1), 1, 2));
        get_vertices(v0b, v1b, v2b);

        float t = smoothstep(.1, .9, tfrac);
        v0 = mix(v0a, v0b, t);
        v1 = mix(v1a, v1b, t);
        v2 = mix(v2a, v2b, t);
    }

    // get barycentric coords
    bool outside;
    vec3 col = cartesian_to_barycentric(uv, v0, v1, v2, true, outside);

    // darken the outer areas
    if (outside)
    {
        col *= cos(TAU * speed * .1 * iTime) * .45 + .55;
    }

    // output
    col = view_transform(col);
    frag_col = vec4(col, 1);
}

