#define PI 3.1415926535897932384626433832
#define TAU 6.283185307179586476925286766
#define PI_OVER_2 1.57079632679489661923132169163

#define FUNC_LERP(T) \
T lerp(T a, T b, float t) \
{ \
    return a + t * (b - a); \
}

#define FUNC_WRAP(T) \
T wrap(T inp, float start, float end) \
{ \
    return start + mod(inp - start, end - start); \
}

#define FUNC_REMAP(T) \
T remap(T inp, float inp_start, float inp_end, float out_start, float out_end) \
{ \
    return out_start \
        + ((out_end - out_start) / (inp_end - inp_start)) * (inp - inp_start); \
}

#define FUNC_REMAP_CLAMP(T) \
T remap_clamp( \
    T inp, \
    float inp_start, \
    float inp_end, \
    float out_start, \
    float out_end \
) \
{ \
    T t = clamp((inp - inp_start) / (inp_end - inp_start), 0.0, 1.0); \
    return out_start + t * (out_end - out_start); \
}

#define FUNC_REMAP01(T) \
T remap01(T inp, float inp_start, float inp_end) \
{ \
    return clamp((inp - inp_start) / (inp_end - inp_start), 0.0, 1.0); \
}

