void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec3 v3 = texelFetch( iChannel0, ivec2(int(fragCoord.x),int(fragCoord.y)), 0 ).xyz;

    vec2 uv = (fragCoord - (0.5 * iResolution.xy)) / iResolution.y;
    float blue = abs(uv.y) < 0.43 ? 0.12 : 0.;
    float white = blue>0. && abs(uv.y) > 0.427 && abs(uv.x)<0.85 ? 1. : 0.;

    fragColor = vec4( white+v3.x*0.9, white+v3.y*0.95, white+blue+v3.z, 1.0 );
}

// send away dots

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    if (readKey(KEY_ENTER)) { fragColor=vec4(0); return; }
    if (int(texelFetch(iChannel1, ivec2(0,0), 0).w) != int(iResolution.x)) { fragColor=vec4(0); return; }

    int pxi = int(fragCoord.x) + int(fragCoord.y) * int(iResolution.x) - 3;
    if (pxi > NOF_DOTS) discard;

    ivec2 r = ivec2(iResolution.xy);

    if (ivec2(0,0) == ivec2(fragCoord)) {
        ivec4 val = ivec4(texelFetch(iChannel0, ivec2(0,0), 0 ));
        int tv = (1-int(texelFetch(iChannel0, ivec2(1,0), 0 ).x))*2-1;
        if (iFrame < 2) {
            val = ivec4(0);
        } else {
            int v=val[DOTS_PER_FRAME-1];
            for (int j = 0; j<DOTS_PER_FRAME; j++) {
                for (int i = 0; i < 5000; i++) { // search for lit pixel
                    v+=1*tv;
                    vec2 p=vec2(v%r.x,r.y-v/r.x);

                    if (texelFetch(iChannel1,ivec2(p),0).x > 0.1) {
                        break;
                    }
                }
                val[j] = v;
            }
        }
        fragColor = vec4(val);
        return;
    }

    if (ivec2(1,0) == ivec2(fragCoord)) {
        ivec4 val = ivec4(texelFetch(iChannel0, ivec2(0,0), 0 ));
        int tv =  int(texelFetch(iChannel0, ivec2(1,0), 0 ).x);
        if (iFrame < 2) {
            tv = 0;
        } else {
            if (val.x > int(iResolution.x*iResolution.y)+5000*NOF_STEPS*2) tv=1;
            if (val.x < 0) tv=0;
        }
        fragColor = vec4(tv);
        return;
    }
    if (ivec2(2,0) == ivec2(fragCoord)) {
        fragColor = texelFetch(iChannel0, ivec2(0,0), 0 );
        return;
    }

    vec2 sp=vec2(0), ep=vec2(0);
    int dotFound = -1;
    for (int i=0; i<DOTS_PER_FRAME; i++) {
        if ((pxi+i*(NOF_STEPS+1)) % NOF_DOTS == iFrame % NOF_DOTS)
            dotFound = i;
    }

    if (dotFound >= 0) { // initialize new dot
        int i = int(texelFetch(iChannel0, ivec2(0,0), 0 )[dotFound]);
        vec2 p=vec2(i%r.x,r.y-i/r.x);

        if (texelFetch(iChannel1,ivec2(p),0).x > 0.1) {
            float progr = float(i / r.x) / float(r.y);
            if (progr > 0.7)
                sp = vec2(iResolution.x*0.5+sin(iTime*8.)*(iResolution.x*0.25), iResolution.y*0.92);
            else if (progr > 0.4)
                sp = vec2(0.55*iResolution.x+sin(iTime*5.)*(iResolution.x*0.1), 0.44*iResolution.y+cos(iTime*5.)*(iResolution.y*0.16));
            else
                sp = vec2(iResolution.x*0.5+sin(iTime*15.)*(iResolution.x*0.15), iResolution.y*0.08);
            ep = p;
            if (int(texelFetch(iChannel0, ivec2(1,0), 0 ).x) == 1) { ep=sp; sp=p; }
        }
        fragColor = vec4(sp, ep);
    }
    else { // update active dot
        vec4 cs = texelFetch(iChannel0, ivec2(fragCoord), 0 );
        int count = (int(cs.w) >> 12) + 1;
        int ai = int(cs.w) & 4095;
        cs.w = float((count<<12) | ai);
        vec2 ep = vec2(cs.z, ai);

        if (count >= NOF_STEPS) {
            cs.x=ep.x; cs.y=ep.y; cs.w=float((NOF_STEPS<<12) | ai);
        } else {
            vec2 cp = cs.xy;
            vec2 ap = (ep-cp)/(float(NOF_STEPS)-float(count));
            cs.x += ap.x;
            cs.y += ap.y;
        }
        fragColor = cs;
    }
}

const int NOF_STEPS = 66;

const int DOTS_PER_FRAME = 2; // 1-4, higher value writes faster

const int NOF_DOTS = (NOF_STEPS + 1) * DOTS_PER_FRAME;

const int NOF_REGIONS = 9; // 2-9
const int BPC = NOF_REGIONS * NOF_REGIONS /4 + 1;

#define LARGE_DOTS

const int KEY_SPACE = 32;
const int KEY_ENTER = 13;
const int KEY_D     = 68;

#define readKey(key) (texelFetch( iChannel3, ivec2(key, 0), 0).x > .5)
#define readKeyToggle(key) (texelFetch( iChannel3, ivec2(key, 2), 0).x > .5)

// check which regions have dots

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    int pxi = int(fragCoord.x) + int(fragCoord.y) * int(iResolution.x) - 3;
    if (pxi > NOF_DOTS) discard;

    if (ivec2(0,0)==ivec2(fragCoord)) {

        int xd=int(iResolution.x) / NOF_REGIONS + 1;
        int yd=int(iResolution.y) / NOF_REGIONS + 1;
        ivec2 r = ivec2(iResolution.xy);

        ivec4 regions = ivec4(0);
        for (int i=3; i<NOF_DOTS+3; i++) {
            ivec2 p=ivec2(i%r.x,i/r.x); 
            vec2 px = texelFetch(iChannel0, p, 0 ).xy;

            int xr=int(px.x) / xd;
            int yr=int(px.y) / yd;
            int reg = xr + yr * NOF_REGIONS;
            int cp = reg / BPC;
            int bp = reg % BPC;
            regions[cp] |= (1<<bp);
        }
        fragColor = vec4(regions);
        return;
    }

    fragColor = texelFetch(iChannel0, ivec2(fragCoord), 0 );
}

// draw dots in place and fade old ones

const float END_VAL = 10.;

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    if (readKey(KEY_ENTER)) { fragColor=vec4(0); return; }
    if (int(texelFetch(iChannel2, ivec2(0,0), 0).w) != int(iResolution.x)) { fragColor=vec4(0); return; }

    ivec2 r = ivec2(iResolution.xy);

    bool alt = readKeyToggle(KEY_SPACE);
    vec4 org = max(vec4(0.),texelFetch(iChannel1, ivec2(fragCoord), 0 ) - (alt? vec4(0.15,0.15,0.15,0.) : vec4(0.3,0.3,0.25,0.)));
    float val = org.x;

    if (org.w >= END_VAL) { // keep this finished dot, unless it is one of our current start dots when reversing
        bool isInit = false;
        ivec4 inits = ivec4(texelFetch(iChannel0, ivec2(2,0), 0 ));
        for (int i=0; i<DOTS_PER_FRAME; i++) {
            if(ivec2(fragCoord)==ivec2(inits[i]%r.x,r.y-inits[i]/r.x))
                isInit = true;
        }
        fragColor = vec4(texelFetch(iChannel2, ivec2(fragCoord), 0 ).xyz, isInit ? 0. : END_VAL);
        return;
    }

    vec4 regions = texelFetch(iChannel0, ivec2(0,0), 0 ); //regions = vec4(2097151.0);
    int xd=int(iResolution.x) / NOF_REGIONS + 1;
    int xr=int(fragCoord.x) / xd;
    int yd=int(iResolution.y) / NOF_REGIONS + 1;
    int yr=int(fragCoord.y) / yd;
    int reg = xr + yr * NOF_REGIONS;
    int cp = reg / BPC;
    int bp = reg % BPC;
    if ((int(regions[cp]) & (1<<bp)) == 0) {
        fragColor = vec4(min(1.,val));
        if (readKeyToggle(KEY_D)) fragColor = vec4(0.5); // see skipped regions
        return;
    }

    float hitadd = alt? 0.6 : 1.0;
    int ended = 0;

    for (int i=3; i<NOF_DOTS+3; i++) {
        ivec2 p=ivec2(i%r.x,i/r.x); 
        vec3 pxw = texelFetch(iChannel0, p, 0 ).xyw;
        vec2 px = pxw.xy;
#ifdef LARGE_DOTS
        if (distance(px,fragCoord) < 1.) {
            if ((int(pxw.z) >> 12) >= NOF_STEPS) { if (ended==0) ended=1; if (ivec2(px) == ivec2(fragCoord)) ended = 2; }
#else
        if (ivec2(px) == ivec2(fragCoord)) {
            if ((int(pxw.z) >> 12) >= NOF_STEPS) ended = 2;
#endif
            org += hitadd; hitadd=0.;
            // break; // slows things down, a lot...
        }
    }
    if (ended > 0) { fragColor = vec4(texelFetch(iChannel2, ivec2(fragCoord), 0 ).xyz, ended == 1 ? org.w : END_VAL); return; }
	fragColor = min(vec4(1.),org);
}

// text to write

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    if (ivec2(0,0)==ivec2(fragCoord)) {
        fragColor=vec4(0,0,0,iResolution.x); return;
    }

    fragColor = vec4(0.);
    vec2 UO = 18.*(fragCoord)/iResolution.y;
    vec2 U;

    U = UO;
    U.x -= 3.;
    if (U.x >= 0. && U.x <= 6. && U.y >= 14. && U.y <= 15.) {
        int i = int[](28,85,110,100,101,114)[int(U)];
        fragColor = vec4(texture( iChannel0, fract(U)*.055 + vec2(i,15-i/16)/16. ).x);
        if (fract(U.x) < 0.1 || fract(U.x) > 0.97|| fract(U.y) < 0.1 || fract(U.y) > 0.97) fragColor=vec4(0.);
    }
    U = UO;
    U.x -= 3.;
    if (U.x >= 0. && U.x <= 13. && U.y >= 13. && U.y <= 14.) {
        int i = int[](99,111,110,115,116,114,117,99,116,105,111,110,33)[int(U)];
        fragColor = vec4(texture( iChannel0, fract(U)*.055 + vec2(i,15-i/16)/16. ).x);
        if (fract(U.x) < 0.1 || fract(U.x) > 0.97|| fract(U.y) < 0.1 || fract(U.y) > 0.97) fragColor=vec4(0.);
    }

    U = UO;
    U.x -= 22.;
    if (U.x >= 0. && U.x <= 6. && U.y >= 11. && U.y <= 14.) {
        int i;
        switch(int(U.y-11.)) {
            case 2: i = int[](73,32,87,65,83,32,32)[int(U)]; break;
            case 1: i = int[](73,78,32,84,72,69,32)[int(U)]; break;
            case 0: i = int[](80,79,79,76,33,32,32)[int(U)]; break;
        }
        fragColor = vec4(texture( iChannel0, fract(U)*.055 + vec2(i,15-i/16)/16. ).x);
        if (fract(U.x) < 0.1 || fract(U.x) > 0.97|| fract(U.y) < 0.1 || fract(U.y) > 0.97) fragColor=vec4(0.);
    }

    U = UO;
    U.x -= 7.;
    if (U.x >= 0. && U.x <= 4. && U.y >= 6. && U.y <= 10.) {
        int i;
        switch(int(U.y-6.)) {
            case 3: i = int[](32,68,65,32)[int(U)]; break;
            case 2: i = int[](33,32,32,77)[int(U)]; break;
            case 1: i = int[](63,32,32,78)[int(U)]; break;
            case 0: i = int[](32,84,73,32)[int(U)]; break;
        }
        fragColor = vec4(texture( iChannel0, fract(U)*.055 + vec2(i,15-i/16)/16. ).x);
        if (fract(U.x) < 0.1 || fract(U.x) > 0.97|| fract(U.y) < 0.1 || fract(U.y) > 0.97) fragColor=vec4(0.);
    }

    U = UO;
    U.x -= 14.;
    if (U.x >= 0. && U.x <= 14. && U.y >= 3. && U.y <= 4.) {
        int i = int[](29,87,114,111,110,103,32,115,104,97,100,101,114,29)[int(U)];
        fragColor = vec4(texture( iChannel0, fract(U)*.055 + vec2(i,15-i/16)/16. ).x);
        if (fract(U.x) < 0.1 || fract(U.x) > 0.97|| fract(U.y) < 0.05 || fract(U.y) > 0.97) fragColor=vec4(0.);
    }

}
