const float SPEED = 0.001;




vec3 startColor = vec3(1.0, 0.0, 0.0);
vec3 midColor1 = vec3(1.0, 1.0, 1.0);
vec3 midColor2 = vec3(0.0, 1.0, 0.0);
vec3 endColor = vec3(1.0, 1.0, 0.0);

vec3 gradient(float t, float time) {
t = t + time * SPEED; // Bewegung des Farbverlaufs Ã¼ber die Zeit
t = fract(t); // Normalisierung des Parameters auf den Bereich [0,1]

if (t < 0.25)
return mix(startColor, midColor1, t * 4.0);
else if (t < 0.5)
return mix(midColor1, midColor2, (t - 0.25) * 4.0);
else if (t < 0.75)
return mix(midColor2, endColor, (t - 0.5) * 4.0);
else
return mix(endColor, startColor, (t - 0.75) * 4.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
vec2 uv = fragCoord/iResolution.xy;

vec3 color = gradient(uv.x, iTime);

fragColor = vec4(color, 1.0);

}
