void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
vec2 uv = -1.0 + 2.0 * fragCoord.xy/iResolution.xy;
uv.x *= iResolution.x/iResolution.y;
uv-=vec2(0.0,-0.5); uv*=3.5;
vec3 col=vec3(0.66,0.83,0.92);
float t=iTime;
uv.x=abs(uv.x)+(0.5+0.5*sin(t*2.0))-(5.5+6.7*sin(t))+1.0;

col=chica((uv-vec2(-0.5,-3.0))*0.5,col) ;
col=chica((uv-vec2(-3.5,-1.5)),col) ;
col=chica((uv-vec2(-5.5,-1.5))*2.0,col );

fragColor = vec4(col,1.0);
}

float sdEllipse( in vec2 p, in vec2 ab )
