vec2 screen2uv(vec2 glCoord, vec2 glRes)
{
    return (glCoord.xy * 2.0 - glRes.xy) / glRes.y; 
}

vec2 angle2vec(float angle, float cx, float cy)
{
    return vec2(cos(cx * angle), sin(cy * angle));
}

vec2 angle2vec(float angle)
{
    return angle2vec(angle, 1.0, 1.0);
}


vec3 rgb(int r, int g, int b)
{
    return vec3(r, g, b) / 255.0;
}



vec3 palette(float t)
{
    vec3 a = vec3(0.5, 0.5, 0.5);
    vec3 b = vec3(0.6, 0.5, 0.5);
    vec3 c = vec3(1.0, 1.0, 1.0);
    vec3 d = vec3(0.263, 0.416, 0.557);

    return a + b * cos(6.28318 * (c * t + d));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv0 = screen2uv(fragCoord.xy, iResolution.xy); 
    vec2 uvM = screen2uv(iMouse.xy, iResolution.xy);
    float t = iTime;

    vec3 finalColor = vec3(0.0);
    vec2 uv = uv0 - uvM;    

    //star data
    float starCount = 20.0;
    float starRadius = 0.6;
    float starOrbitRadius = 3.0;
    float starSpeedScale = 0.5;
    vec3 starCol = rgb(15, 190, 190);

    //planet data
    float planetCount = 32.0;
    float planetRadius = 0.5;
    float planetOrbitRadius = 0.2;
    float planetSpeedScale = 0.6;
    vec3 planetCol = rgb(200, 200, 15);


    //orbit pos - uv (0,0 center); 
    vec2 starOrbitPos = uv;

    for(float i = 1.0; i <= starCount; ++i)
    {
        float starShift = radians(360.0 * i / starCount);
        vec2 starOrbitVector = angle2vec(t * starSpeedScale + starShift) * starOrbitRadius;
        vec2 starPos = starOrbitPos + starOrbitVector;

        float dStar = length(starPos);

        dStar = step(dStar, starRadius);
        dStar = sin(dStar * 32.0 + t) / 32.0;
        dStar = abs(dStar);

        finalColor += dStar * starCol;


        //orbit pos - starPos (star center); 
        vec2 planetOrbitPos = starPos;

        for(float j = 1.0; j <= planetCount; ++j)
        {
            float planetShift = radians(360.0 * j / planetCount);
            vec2 planetOrbitVector = angle2vec(-t * planetSpeedScale + planetShift) * planetOrbitRadius;
            vec2 planetPos = planetOrbitPos + planetOrbitVector;

            float dPlanet = length(planetPos);

            //dPlanet = step(dPlanet, planetRadius);

            dPlanet = sin(dPlanet * 32.0 + t) / 32.0;


            finalColor += dPlanet * planetCol;
        } 
    }

    fragColor = vec4(finalColor, 1.0);
}
