float fmod(float a, float b){
    float
    t1 = mod1(floor(b/2.),2.),
    a1 =
        floor(a+t1),
    a0 =
        a1
        //a1+floor(a1/2.)*4.
        //a1+floor(a1/4.)*3.+floor(a1/3.)*4.
