vec2 synth_(vec2 a1,vec2 a,vec2 a2,vec2 a3,vec2 nb,vec2 a4){
    return log(a1+a*abs(a2-abs(.5-a3*vec2(fract1(nb.x*.998),fract1(nb.y)))*a4));
}
#define synth(a1,a,a2,a3,nb,a4) synth_(vec2(a1),vec2(a),vec2(a2),vec2(a3),vec2(nb),vec2(a4))

vec2 banjo(vec2 a, vec2 nb){
return a/16./4.*abs(
  synth(1./4.,a,1./8.,1.,nb*4.,4./6.)
  + synth(1./8.,a,1./4.,1.,nb*2.,6./4.));
}

